#define MAX_MARCH 100

vec3 materialize(vec3 p, vec3 ray, float depth, vec2 mat)
{
    vec3 col = vec3(0.0);
    vec3 nor = norm(p);
    float t = iTime;
    vec3 sky = min(sinebow(fract(t * 2.5 + 0.5)) * vec3(0.35) + vec3(0.3) + pow(max(1.29 - p.y, 0.), 5.), 1.);
    if(p.y >= 0.29) {
    	col = sky;
    } else {
        if(mat.y > 0.0){
            vec2 index = floor(p.xz + 0.5);

            float roughness, metalness;
            if(mat.y == MAT_BLACK) {
                col = vec3(0.0, 0.0, 0.0);
                roughness = 0.8;
                metalness = 0.0;
            } else if(mat.y == MAT_FACE) {
                //col = vec3(1.0, 0.8, 0.6);
                col = min(sinebow(fract(rand(vec2(index.x, index.y) * 32.) + t * 0.5)) + vec3(0.2), 1.);
                roughness = 0.8;
                metalness = 0.0;
            } else if(mat.y == MAT_BROW) {
                col = vec3(1.0, 0, 0);
                roughness = 0.1;
                metalness = 0.0;
            } else if(mat.y == MAT_CHEEP) {
                col = vec3(1.0, 0.3, 0.5);
                roughness = 0.1;
                metalness = 0.0;
            } else if(mat.y == MAT_BG) {
                col = min(sinebow(fract((index.x + index.y) * 0.175 + t * 0.5 + 0.5)) * vec3(0.3), 1.);
                float e = smoothstep(abs(mod(p.y + 0.25 + t * 0.5, 0.5) - 0.25), 0.0, 0.0125); 
                roughness = 0.0;
                metalness = 0.8;
            }

            vec3 result = vec3(0.);
            result += calcAmbient(p, col, metalness, roughness, nor, -ray, depth);
            col = result;
        }
        float fog = min(1.0, (1.0 / float(MAX_MARCH)) * float(mat.x))*1.0;
    	vec3  fog2 = 0.0025 * vec3(1) * depth;
    	col += vec3(0.1)* fog;
    	col += fog2 + sky * 0.1;
    }

    return col;
}

vec3 trace(vec3 p, vec3 ray)
{
    float t = 0.0;
    vec3 pos;
    vec2 mat;

    float planeDistance = GetIntersectLength(p, ray, vec3(0.,0.3,0.), vec3(0.,1.,0.));
    t = planeDistance;

    int count = 0;
    for (int i = 0; i < MAX_MARCH; i++) {
        pos = p + ray * t;
        mat = map(pos);
        if (mat.x <= 0.000001) {
        	break;
        }
        t += mat.x * 0.5;
        count++;
    }
    p = p + t * ray;
    mat.x = float(count);
    return materialize(p, ray, t, mat);
}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

        float rotSpeed = iTime * M_PI2 * 0.1;
        vec3 ro = vec3(cos(rotSpeed)*5.0, sin(iTime * M_PI2 * 0.4) * 0.3 + 1.0, -5.0);
        vec3 ta = vec3(0., 0.0, 0.);

        mat3 c = camera(ro, ta, 0.);
        vec3 ray = c * normalize(vec3(p, 3.5));
        vec3 col = trace(ro, ray);
        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif
    fragColor = vec4(tot,1.0);
}

