#define TAU 6.2831853071
#define dt mod(iTime-TAU/8.,TAU)
#define scale 0.08

#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(.001,fwidth(val-thre)))
#define square(u,s) AAstep(s, max(abs(u.x),abs(u.y)))
#define circle(u,s) AAstep(s, length(u))


float piece (vec2 uv)
{
   	vec2 offsetX = vec2 (scale,0.);
    vec2 offsetXY = vec2 (scale*0.2, scale);
    return square(uv, scale)
        * circle(uv-offsetX,scale*0.4)
        * circle(uv-offsetXY, scale*0.4)
        +(1.-circle(uv+offsetX,scale*0.4))
        +(1.-circle(uv+offsetXY,scale*0.4));
}

float pattern (vec2 uv)
{return piece(uv)*piece(vec2(-uv.x,-uv.y)+vec2(-scale*2.,scale*2.));}

float grid (vec2 uv)
{
    uv = mod(uv,scale*4.)-scale*2.;
    float d = 1.;
    for (int i=-1; i<=1; i++)
    {
        for(int j=-1; j<=1;j++)
        {
            vec2 offset = vec2(float(i),float(j));
            d *= pattern(uv+offset*(scale*4.));
        }
    }  
    return clamp(d,0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy);
    uv.x *= iResolution.x/iResolution.y;
    vec2 centered_uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

 	vec3 col = mix(vec3(1.,0.,.5),vec3(.95),grid(uv));

    float mask = piece(centered_uv*sin(length(centered_uv*0.7)-dt)*0.2);
    col = mix(col, 1.-col, clamp(mask,0.,1.));
    fragColor = vec4(sqrt(col),1.0);
}

const float FOVY = 0.25 * 3.141569;
const int RAY_STEPS = 256;
#define BACK_WALL_SDF sdfPlane(pos, BACK_WALL_INVERSE_T)
#define BACK_WALL_ID 1
#define BACK_WALL_INVERSE_T inverseTransform(vec3(0., 5., 15.), vec3(0.), vec3(40., 40., 1.))
#define FLOOR_SDF sdfPlane(pos, FLOOR_INVERSE_T)
#define FLOOR_ID 2
#define FLOOR_INVERSE_T inverseTransform(vec3(0., -4.95, 0.), vec3(90., 0., 0.), vec3(40., 40., 1.))

#define TALL_CUBE_SDF sdfBox(pos, vec3(5., 7, 3), TALL_CUBE_INVERSE_T)
#define TALL_CUBE_ID 3
#define TALL_CUBE_INVERSE_T inverseTransform(vec3(4, -1.5, 8), vec3(0, 35, 0), vec3(5., 7, 3))

#define SPHERE_SDF sdfSphere(pos, vec3(-5, -1, 5), 3.)
#define SPHERE_ID 5
#define SPHERE_INVERSE_T inverseTransform(vec3(-5, -1, 5), vec3(0, 90., 0), vec3(3.))

struct Intersection {
    float t;
    vec3 color;
    vec3 p;
    int object;
};

mat4 inverseTransform(vec3 translate, vec3 rotate, vec3 scale) {
    mat4 s;
    // matrices indexed to columns!
    s[0] = vec4(scale.x, 0., 0., 0.);
    s[1] = vec4(0., scale.y, 0., 0.);
    s[2] = vec4(0., 0., scale.z, 0.);
	s[3] = vec4(0., 0., 0., 1.);                                     

    rotate.x = radians(rotate.x);
    rotate.y = radians(rotate.y);
    rotate.z = radians(rotate.z);

    mat4 r_x;
    r_x[0] = vec4(1., 0., 0., 0.);
    r_x[1] = vec4(0., cos(rotate.x), sin(rotate.x), 0.);
    r_x[2] = vec4(0., -sin(rotate.x), cos(rotate.x), 0.);
    r_x[3] = vec4(0., 0., 0., 1.);


    mat4 r_y;
    r_y[0] = vec4(cos(rotate.y), 0., -sin(rotate.y), 0.);
    r_y[1] = vec4(0., 1, 0., 0.);
    r_y[2] = vec4(sin(rotate.y), 0., cos(rotate.y), 0.);
    r_y[3] = vec4(0., 0., 0., 1.);

    mat4 r_z;
    r_z[0] = vec4(cos(rotate.z), sin(rotate.z), 0., 0.);
    r_z[1] = vec4(-sin(rotate.z), cos(rotate.z), 0., 0.);
    r_z[2] = vec4(0., 0., 1., 0.);
    r_z[3] = vec4(0., 0., 0., 1.);

    mat4 r = r_x * r_y * r_z;

    mat4 t;
    t[0] = vec4(1., 0., 0., 0.);
    t[1] = vec4(0., 1., 0., 0.);
    t[2] = vec4(0., 0., 1., 0.);
    t[3] = vec4(translate.x, translate.y, translate.z, 1.); 

    return inverse(t * r * s);      
}

float sdfPlane(vec3 p, mat4 inverseT) {
    p = vec3(inverseT * vec4(p, 1.));
    float dot = dot(p, vec3(0., 0., 1.));
    if (abs(dot) < 0.0001) {
        // n must be normalized
        if (p.x >= -.5 && p.x <= 0.5 && p.y >= -0.5 && p.y <= 0.5) {
            return 0.;  
        }
        return 324234.;
    }
    float dist = 0.f;
    if (abs(p.x) > 0.5) {
        dist += ((p.x - 0.5) * (p.x - 0.5));
    }
    if (abs(p.y) > 0.5) {
        dist += ((p.y - 0.5) * (p.y - 0.5));
    }
    dist += abs(p.z) * abs(p.z);
    return sqrt(dist);
} 

// Box with side lengths b
float sdfBox(vec3 p, vec3 scale, mat4 inverseT)
{
    p = vec3(inverseT * vec4(p, 1.));
    vec3 q = vec3(abs(p.x), abs(p.y), abs(p.z)) - vec3(.5);
    float d = q.x;
    d = max(d,q.y);
    d = max(d,q.z);
    return d * min(min(scale.x, scale.y), scale.z);
}


float sdfSphere(vec3 p, vec3 c, float r) {
    return distance(p, c) - r;
}

void sceneMap3D(vec3 pos, out float t, out int objectHit) {
	t = BACK_WALL_SDF;
    objectHit = BACK_WALL_ID;
    float t2;

	if ((t2 = FLOOR_SDF) < t) {
        t = t2;
        objectHit = FLOOR_ID;
    }
    if ((t2 = TALL_CUBE_SDF) < t) {
        t = t2;
        objectHit = TALL_CUBE_ID;
    }
     if ((t2 = SPHERE_SDF) < t) {
        t = t2;
        objectHit = SPHERE_ID;
    }
}

// called by computeNormal
float sceneMap3D(vec3 pos) {
    float t;
    int obj;
    sceneMap3D(pos, t, obj);
    return t;
}

void march(vec3 origin, vec3 dir, out float t, out int objectHit) {
    t = 0.001;
    for (int i = 0; i < RAY_STEPS; i++) {
        vec3 pos = origin + t * dir;
        float min;
        sceneMap3D(pos, min, objectHit);
        if (min < 0.01) {
            return;
        }
        t += min;
    }
    t = -1.;
    objectHit = -1;
}

vec3 computeNormal(vec3 p) {
    vec3 epsilon = vec3(0., .001, 0.);
    return normalize(vec3(sceneMap3D(p + epsilon.yxx) - sceneMap3D(p - epsilon.yxx),
                          sceneMap3D(p + epsilon.xyx) - sceneMap3D(p - epsilon.xyx),
                     	  sceneMap3D(p + epsilon.xxy) - sceneMap3D(p - epsilon.xxy)));
}

float noise2D( vec2 p ) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) *
                  45964.03 / (4372. * (cos(.5 * iTime) + 3.) / 2.));
}

float interpNoise2D(float x, float y) {
    int intX = int(floor(x));
    float fractX = fract(x);
    int intY = int(floor(y));
    float fractY = fract(y);

    float v1 = noise2D(vec2(intX, intY));
    float v2 = noise2D(vec2(intX + 1, intY));
    float v3 = noise2D(vec2(intX, intY + 1));
    float v4 = noise2D(vec2(intX + 1, intY + 1));

    float i1 = mix(v1, v2, fractX);
    float i2 = mix(v3, v4, fractX);
    return mix(i1, i2, fractY);
}


float fbm(float x, float y) {
    float total = 0.;
    float persistence = 0.5f;
    float octaves = 4.;

    for(float i = 1.; i <= octaves; i++) {
        float freq = pow(2.f, i);
        float amp = pow(persistence, i);

        total += interpNoise2D(x * freq,
                               y * freq) * amp;
    }
    return total;
}


vec2 random2 (vec2 p) {
    return fract(sin(vec2(dot(p, vec2(127.1, 311.7)),
                          dot(p, vec2(269.5, 183.3))))
                 * 43758.5433);
}

float WorleyNoise(vec2 uv) {
    uv /= 5.;
    vec2 uvInt = floor(uv);
    vec2 uvFract = fract(uv);
    float minDist = 1.0; // Minimum distance initialized to max.
    for(int y = -1; y <= 1; ++y) {
        for(int x = -1; x <= 1; ++x) {
            vec2 neighbor = vec2(float(x), float(y)); // Direction in which neighbor cell lies
            vec2 point = random2(uvInt + neighbor); // Get the Voronoi centerpoint for the neighboring cell
            point = 0.5 + 0.5 * sin(iTime + 13.2831 * point);
            vec2 diff = neighbor + point - uvFract; // Distance between fragment coord and neighborâs Voronoi point
            float dist = length(diff);
            minDist = min(minDist, dist);
        }
    }
    return minDist;
}

vec3 random3( vec3 p ) {
    return fract(sin(vec3(dot(p,vec3(127.1, 311.7, 191.999)),
                          dot(p,vec3(269.5, 183.3, 765.54)),
                          dot(p, vec3(420.69, 631.2,109.21))))
                 *43758.5453);
}

float surflet(vec3 p, vec3 gridPoint) {
    vec3 t2 = abs(p - gridPoint);
    vec3 t = vec3(1.f) - 6.f * vec3(pow(t2.x, 5.),pow(t2.y, 5.), pow(t2.z, 5.)) + 15.f * vec3(pow(t2.x, 4.), pow(t2.y, 4.),pow(t2.z, 4.)) - 10.f * vec3(pow(t2.x, 3.),pow(t2.y, 3.),pow(t2.z, 3.));
    vec3 gradient = random3(gridPoint) * 2. - vec3(1) * (sin(2. * iTime) + 1.);
    vec3 diff = p - gridPoint;
    float height = dot(diff, gradient);
    return height * t.x * t.y * t.z;
}

float perlinNoise(vec3 p) {
	float surfletSum = 0.f;
	// Iterate over the four integer corners surrounding uv
	for(int dx = 0; dx <= 1; ++dx) {
		for(int dy = 0; dy <= 1; ++dy) {
            for(int dz = 0; dz <= 1; ++dz) {
				surfletSum += surflet(p, floor(p) + vec3(dx, dy, dz));
            }
		}
	}
	return surfletSum + 0.2;
}

vec3 computeMaterial(int objectHit, vec3 p, vec3 n, vec3 lightDir, vec3 view) {
    lightDir = normalize(lightDir - p);
    float lambert = dot(n, lightDir);
    vec3 color = vec3(1);

    switch (objectHit) {
        case SPHERE_ID:
        p = vec3(SPHERE_INVERSE_T * vec4(p, 1.));        

        vec3 n = p;
		float u = atan(n.x, n.z) / (2.*3.1415926535897932384626433832795) + 0.5;
		float v = 0.5 - asin(n.y)/3.1415926535897932384626433832795;

        float f = fbm(u * 20., v * 20.);

        color = vec3(1) * f;
        vec3 a = vec3(-0.642, 0.608, 0.608);
   		vec3 b = vec3(0.588, -0.342, 0.048);
        vec3 c = vec3(f, f, f);
    	vec3 d = vec3(f*f, f*f, f*f);
        vec3 color1 = a + b * cos(2. * 3.1415926535897932384626433832795 *
                                 (c  + d));
        vec3 color2 = c + d * cos(2. * 3.1415926535897932384626433832795 *
                                 (a  + b));
        color = mix(color2, color1, (cos(.5 * iTime) + 1.) / 2.) * 1.5;                
        break;

        case FLOOR_ID:

        color = vec3(.8, 1, .7);

        break;

        case TALL_CUBE_ID:
        float perlin = perlinNoise(p);

        a = vec3(1.000, 0.500, 0.500);
		b = vec3(0.5);
		d = vec3(0.750, 1.000, 0.667);
		c = vec3(0.800, 1.000, 0.333);

        vec3 perlinColor = a + b * cos(6.28 * (perlin * 2. * c + d));
        color = vec3(1) * perlinColor;

        break;

        case BACK_WALL_ID:
        vec3 uvec = vec3(1, 0, 0);
        vec3 vvec = vec3(0, 1, 0);
        u = dot(uvec, p);
        v = dot(vvec, p);

        float t = WorleyNoise(vec2(u, v));
        a = vec3(0.5, 0.5, 0.5);
		b = vec3(0.5, 0.5, 0.5);
		d = vec3(1.);
		c = vec3(0.3, 0.2, 0.2);
        vec3 worleyColor = a + b * cos(6.28 * (t * c + d));

        color = vec3(1) * worleyColor;
        break;
    }
    return color * lambert;
}

Intersection sdf3D(vec3 eye, vec3 dir) {
    float t;
    int objectHit;
    march(eye, dir, t, objectHit);
    vec3 isect = eye + t * dir;

    vec3 nor = computeNormal(isect);

    vec3 lightDir = vec3(3, 7, -4);

    vec3 color = computeMaterial(objectHit, isect, nor, lightDir, normalize(eye - isect));

    if (objectHit == -1) {
        color = vec3(0, 0, 0);
    }

    return Intersection(t, color, isect, objectHit);
}

vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0., 1., 0.)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / float(iResolution.y);

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - vec2(1, 1);

    vec3 eye = vec3(0, 3, -11);
    vec3 ref = vec3(0, 0, 2);
    vec3 rayDir = rayCast(eye, ref, uv); 

    Intersection isect = sdf3D(eye, rayDir);

    fragColor = vec4(isect.color, 1.0);
}

