// udTriangle - https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float dot2( vec3 v ) { return dot(v,v); }
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
  vec3 ba = b - a; vec3 pa = p - a;
  vec3 cb = c - b; vec3 pb = p - b;
  vec3 ac = a - c; vec3 pc = p - c;
  vec3 nor = cross( ba, ac );

  return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

vec2 SampleBezier(vec2 a, vec2 b, vec2 c, vec2 d, float t)
{
    // First degree
    vec2 p11 = mix(a, b, t);
    vec2 p12 = mix(b, c, t);
    vec2 p13 = mix(c, d, t);

    // Second degree
    vec2 p21 = mix(p11, p12, t);
    vec2 p22 = mix(p12, p13, t);

    // Third degree
    return mix(p21, p22, t);
}


// SCENE ///////////////////////////////////////////////////////////////////////////////////////////////////
Hit GetDist(vec3 p)
{
    Hit closest; // TODO try combining vec4(color, dist) and see if mix speeds up intersections
    closest.Dist = 999999.;
    closest.Color = vec3(1);

    float dist = 999999.;

    // Tri surface
    {
        const vec3 col = vec3(1);
        for (int i = 0; i < NUM_TRIS; i++)
        {
            dist = udTriangle(p, _tris[i].a, _tris[i].b, _tris[i].c) - 0.01;
            if (dist < closest.Dist)
            {
                closest.Dist = dist;
                closest.Color = col;
            }
        }
    }


    // Vertices
    {
        const vec3 col = vec3(1);
        for (int i = 0; i < NUM_VERTS; i++)
        {
            dist = length(p-_vertices[i]) - 0.03;
            if (dist < closest.Dist)
            {
                closest.Dist = dist;
                closest.Color = col;
            }
        }
    }


    // Plane
    {
        vec3 col = vec3(.5,.5,.5);
        dist = p.y+0.5; // ground at y=0
		if (dist < closest.Dist)
        {
            closest.Dist = dist;
            closest.Color = col;
        }
    }

    return closest;
}

vec3 GetNormal(vec3 p)
{
    float d = GetDist(p).Dist;
    float offset = 0.01;
    vec3 n = d - vec3(
        GetDist(p - vec3(offset,0,0)).Dist,
        GetDist(p - vec3(0,offset,0)).Dist,
        GetDist(p - vec3(0,0,offset)).Dist
        );

    return normalize(n);
}

Hit RayMarch(Ray r)
{
    Hit lastHit;
    float totalDist = 0.0;

    for (int i=0; i<MAX_STEPS; i++)
    {
        vec3 p = r.Origin + totalDist*r.Direction;
        lastHit = GetDist(p);
        totalDist += lastHit.Dist;

        if (totalDist < SURFACE_DIST || totalDist > MAX_DIST) 
            break;
    }

    lastHit.Dist = totalDist;

    return lastHit;
}

vec3 GetLight(vec3 p, Light light)
{
    vec3 PL = light.Pos-p;
    float PLLen = length(PL);

    vec3 lightNormal = PL/PLLen;
    vec3 surfNormal = GetNormal(p);

    // Light
    float strength = clamp(dot(surfNormal, lightNormal), 0., 1.);
    float attenuation = 1. / max(PLLen*PLLen, 0.001);

    // Shadow
    Ray shadow;
    shadow.Origin = p + surfNormal*SURFACE_DIST*2.;
    shadow.Direction = lightNormal;
    if(RayMarch(shadow).Dist < PLLen) strength = 0.0;

    return light.Intensity * light.Color * strength * attenuation;
}

mat3 RotateXZ(float rad)
{
    float cosr = cos(rad);
    float sinr = sin(rad);
    return mat3(
        cosr,  0,  sinr,
        0,     1,  0,
        -sinr, 0,  cosr);
}


