void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.yy; // map to center:(0, 0), topright:(aspect/2, 0.5)

    // Camera
    Ray r;
    r.Origin = vec3(0, 1.3, -3.1);
    r.Direction = normalize(vec3(uv.x, uv.y-0.7, 1.5));


    // Build scene
    {
        mat3 R = RotateXZ(iTime*0.2);

		BezierControl seg1a = BezierControl(vec2(-1, 0), vec2(-.3,-0.7) + vec2(0, 0.2*sin(iTime+1.))); 
        BezierControl seg1b = BezierControl(vec2(1,  0), vec2(.3, -.3) + vec2(0, 0.2*cos(iTime+1.)));

        BezierControl seg2a = BezierControl(vec2(-1, 0), vec2(-.5,-.2) + vec2(0, 0.2*sin(iTime))); 
        BezierControl seg2b = BezierControl(vec2(1,  0), vec2(.5, -.2) + vec2(0, 0.2*cos(iTime)));

        // Verts
        int index = 0;
        for (int z = 0; z < DIM_VERTS; z++)
        {
            float v = float(z)/float(DIM_VERTS-1);
			v = v*2. - 1.; // transform to [-1,1]

            for (int x = 0; x < DIM_VERTS; x++)
            {
                float u = float(x)/float(DIM_VERTS-1);
                vec2 u0 = SampleBezier(seg1a.Vertex, seg1a.Handle, seg1b.Handle, seg1b.Vertex, u);
                vec2 u1 = SampleBezier(seg2a.Vertex, seg2a.Handle, seg2b.Handle, seg2b.Vertex, u);

                vec2 xy = mix(u0, u1, v) + vec2(0,.35);
 				_vertices[index++] = R*vec3(xy, v);
            }
        }

        // Tris
        {
			for (int i = 0; i < NUM_TRIS; i+=2)
            {
                //int i = 1;
                int baseIndex = i/2;
                int base = (baseIndex/DIM_SEGS)*DIM_VERTS + baseIndex%DIM_SEGS;
                vec3 a = _vertices[base];
                vec3 b = _vertices[base + 1];
                vec3 c = _vertices[base + DIM_VERTS + 1];
                vec3 d = _vertices[base + DIM_VERTS];

                _tris[i] = Tri(a,b,c);
                _tris[i+1] = Tri(c,d,a);
            }
        }
    }


    // Hit scene
    Hit hit = RayMarch(r);
    vec3 p = r.Origin + r.Direction*hit.Dist; // intersection point


    // Direct Light
    vec3 directLight = vec3(0);
    {
        Light l1;
        l1.Pos = vec3(0, 2, 0) + 5.0;// * vec3(0.5*sin(iTime), 1.0, 0.5*cos(iTime));
        l1.Color = vec3(1.0,0.5,0.4);
        l1.Intensity = 50.0;

        /*Light l2;
        l2.Pos = vec3(5, 5, -5);
        l2.Color = vec3(1.0,0.7,0.5);
        l2.Intensity = 0.5;*/

        directLight += hit.Color * GetLight(p, l1);
    	//directLight += GetLight(p, l2);
    }


    // Ambient Light
    vec3 ambient = hit.Color * 0.8*vec3(0.02,0.02,0.08);


    // Compose colour
    vec3 col = ambient + vec3(directLight);


    // Post
    col *= 1.0; // exposure bias
  	col = ACESFitted(col); // tonemap 
   	col = pow(col, vec3(1.0/2.2)); // gamma


	fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord)/iResolution.xy;
    // Time varying pixel color
    vec2 col = (uv.yx*10.0);
    for(float i = 1.0; i <5.0; i++){
        uv += col.yx;
        col.xy = cos(uv.yx*i+iTime);
    }
    // Output to screen
    fragColor = vec4(col,1.0,1.0);
}

void remap(inout vec2 st) {
  st = st/.5 - 1.;
}

void proportion(inout vec2 st) {
  st.x *= iResolution.x/iResolution.y;
}


vec2 polar(in vec2 pos, in vec2 uv) {
    vec2 dist = pos - uv;
    return vec2(atan(dist.y,dist.x)+iTime/4., dot(dist,dist)*4.);
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy/iResolution.xy;
    // uv = fract((uv)*3.);
    remap(uv);
    proportion(uv);

    uv *= rotate2d(sin(iTime*2.));
    uv *= scale(vec2((sin(iTime)+3.5)/3.));

    vec2 pos = vec2(0.0);

    vec2 uv_r = uv * scale(vec2(.5,(sin(iTime)+2.)*3.));
    vec2 uv_b = uv + vec2(cos(iTime*4.)*2.,.0) /4.;

    vec2 p_ar = polar(pos, uv);

    vec2 p_ar_r = polar(pos, uv_r);
    vec2 p_ar_b = polar(pos, uv_b);
    float f = cos(p_ar[1]*8.0)/6. +.5;
    float f_r = cos(p_ar_r[0]*8.0)/6. +.5;
    float f_b = cos(p_ar_b[0]*8.0)/6. +.5;
    vec3 color = vec3( 
        smoothstep(f,f+0.2,p_ar_r[1]),
        smoothstep(f,f+0.024,p_ar[1]*(sin(iTime*5.)+2.)/4.+(sin(iTime*5.)+2.)/4.),
        smoothstep(f,f+0.1,p_ar_b[1]) 
        );

    fragColor = vec4(color,1.);
}

