const float Epsilon = 1e-10;

// noise function from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec2 fade(vec2 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}

float cnoise(vec2 P){
  vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
  vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
  Pi = mod(Pi, 289.0); // To avoid truncation effects in permutation
  vec4 ix = Pi.xzxz;
  vec4 iy = Pi.yyww;
  vec4 fx = Pf.xzxz;
  vec4 fy = Pf.yyww;
  vec4 i = permute(permute(ix) + iy);
  vec4 gx = 2.0 * fract(i * 0.0243902439) - 1.0; // 1/41 = 0.024...
  vec4 gy = abs(gx) - 0.5;
  vec4 tx = floor(gx + 0.5);
  gx = gx - tx;
  vec2 g00 = vec2(gx.x,gy.x);
  vec2 g10 = vec2(gx.y,gy.y);
  vec2 g01 = vec2(gx.z,gy.z);
  vec2 g11 = vec2(gx.w,gy.w);
  vec4 norm = 1.79284291400159 - 0.85373472095314 * 
    vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11));
  g00 *= norm.x;
  g01 *= norm.y;
  g10 *= norm.z;
  g11 *= norm.w;
  float n00 = dot(g00, vec2(fx.x, fy.x));
  float n10 = dot(g10, vec2(fx.y, fy.y));
  float n01 = dot(g01, vec2(fx.z, fy.z));
  float n11 = dot(g11, vec2(fx.w, fy.w));
  vec2 fade_xy = fade(Pf.xy);
  vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
  float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
  return 2.3 * n_xy;
}

vec3 RGBtoHCV(vec3 RGB)
{
    vec4 P = (RGB.g < RGB.b) ? vec4(RGB.bg, -1.0, 2.0/3.0) : vec4(RGB.gb, 0.0, -1.0/3.0);
    vec4 Q = (RGB.r < P.x) ? vec4(P.xyw, RGB.r) : vec4(RGB.r, P.yzx);
    float C = Q.x - min(Q.w, Q.y);
    float H = abs((Q.w - Q.y) / (6. * C + Epsilon) + Q.z);
    return vec3(H, C, Q.x);
}

vec3 RGBtoHSL(vec3 RGB)
{
    vec3 HCV = RGBtoHCV(RGB);
    float L = HCV.z - HCV.y * 0.5;
    float S = HCV.y / (1. - abs(L * 2. - 1.) + Epsilon);
    return vec3(HCV.x, S, L);
}

vec3 HUEtoRGB(float H)
{
    float R = abs(H * 6. - 3.) - 1.;
    float G = 2. - abs(H * 6. - 2.);
    float B = 2. - abs(H * 6. - 4.);
    return clamp(vec3(R,G,B),0.,1.);
}

vec3 HSLtoRGB(vec3 HSL)
{
    vec3 RGB = HUEtoRGB(HSL.x);
    float C = (1. - abs(2. * HSL.z - 1.)) * HSL.y;
    return (RGB - 0.5) * C + HSL.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x, iResolution.y);
	p*=1.6;
    vec2 prevP = p;

    float t = iTime*0.1;
    float dist = 1.0;
    p.x+=cos(t)*dist;
    p.y+=sin(t)*dist;
    p*= cnoise(p*0.5);
    float d = length(p)-0.6;

    dist = 1.3;
    p.x-=cos(-t*1.2)*dist;
    p.y-=sin(-t*1.2)*dist;
    p*= cnoise(p*0.9);
    float d2 = length(p)-0.7;

    p = prevP;
    p.y+=iTime*0.1;
    float n = cnoise(p*2.5)+min(d,d2);
	//n = min(d,d2);

    // tone curve
    float toneval = 0.5;
    vec3 originalCol = vec3(pow(n,toneval));

    // gradient map
    vec3 gradientCol1 = vec3(0.7,0.3,0.6);
    vec3 gradientCol2 = vec3(0.7,0.7, .3);

    vec3 hsl = RGBtoHSL(originalCol);

    float r = gradientCol1.r+(gradientCol2.r-gradientCol1.r)*hsl.z;
    float g = gradientCol1.g+(gradientCol2.g-gradientCol1.g)*hsl.z;
    float b = gradientCol1.b+(gradientCol2.b-gradientCol1.b)*hsl.z;

    vec3 gradientMapResult = RGBtoHSL(vec3(r,g,b));

    // hue
    float hueVal = 0.02;
    gradientMapResult.x+=hueVal;

    // brightness
    float brightness = 0.5;

    vec3 resRGB = HSLtoRGB(gradientMapResult)*brightness;

    float threthold =  0.21;
    fragColor = vec4(vec3(step(resRGB.r,threthold),step(resRGB.g,threthold),step(resRGB.b,threthold)),1.0);
}

