float exponentialOut(float t) {
  return t == 1.0 ? t : 1.0 - pow(2.0, -10.0 * t);
}

float easeInOutCubic(float t){
    return t<0.5 ? 4.*t*t*t : (t - 1.)*(2.*t - 2.)*(2.*t - 2.) + 1.;
}

float appearDelayInOutCubic(float delay, float speed){
	return iTime < delay ? 0.0 : easeInOutCubic(speed * (iTime - delay));
}

float appearDelayExpo(float delay, float speed) {
	return iTime < delay ? 0.0 : exponentialOut(speed * (iTime - delay));
}

float appearDelay(float delay, float speed, float duration) {
	return iTime < delay ? 0.0 : mod(speed * (iTime - delay), speed * duration);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xx;


    p -= vec2(0.5,0.5*iResolution.y/iResolution.x);

    float t = iTime;

    float angle = appearDelayExpo(4., 0.5) * PI;

    mat2 rotationMatrix = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
	p *= rotationMatrix;

    p *= - 40.0 + 80.*appearDelayExpo(0., 0.25) + -80.*appearDelayExpo(2.5, 0.25);// * p.y;

	//p *= sin(7.0*t)*0.001+40.0;
//	p.x -= 10.5;
	vec2 n = floor(p);
	vec2 f = fract(p);

	float dist = 0.0;


	dist = sqrt(1.0/dot(p-(n+0.5), p-(n+0.5)));

	n.x += 8.00;
	n.y += 2.00;

	ivec2 ni = ivec2(n.x, n.y-6.0);

	float scale = 0.35;

	if(sin(t*4.0)>0.0)
	{
		dist *= scale;
		scale = 1.0/scale;
	}
	float delay = 0.75;
	float speed = 1.0;
	float offset = 52.0;
	float dist2 = 50.0;
	ivec2 ni1 = ni + ivec2(offset - dist2 * appearDelayExpo(delay + 0.5, speed), 0);
	// if( G(ni1, 0, 0) || O(ni1, -4, 0) || O(ni1, -8, 0) || D(ni1, -12, 0) )
	if( M(ni1, 0, 0) || E(ni1, -4, 0) || R(ni1, -8, 0) || C(ni1, -12, 0) || I(ni1, -16, 0) )
		dist *= scale;
	ni += ivec2(2, 6);
	ivec2 ni2 = ni + ivec2(-offset + dist2 * appearDelayExpo(delay + 0.75, speed), 0);
	if( J(ni2, 0, 0) || E(ni2, -4, 0) || A(ni2, -8, 0) || N(ni2, -12, 0) )
		dist *= scale;
	ni += ivec2(4, 6);
	ivec2 ni3 = ni + ivec2(offset - dist2 * appearDelayExpo(delay + 1.0, speed), 0);
	if( C(ni3, 0, 0) || O(ni3, -4, 0) || M(ni3, -8, 0) || E(ni3, -12, 0) || ii(ni3, -16, 0) || ii(ni3, -20, 0) || ii(ni3, -24, 0) || ii(ni3, -28, 0) )
		dist *= scale;

	vec3 c1 = vec3(0.25,0.33,0.4);
