float distance_from_sphere(in vec3 curPos, in vec3 sphereOri, in float r){
	return length(curPos-sphereOri)-r;
} 

float map_the_world(in vec3 p){
    float displacement = sin(sin(iTime)*5.0 * p.x) * sin(sin(iTime)*5.0 * p.y) * sin(sin(iTime)*5.0 * p.z) * 0.25;
	float sphere_0 = distance_from_sphere(p,vec3(0.0), 1.0);
    return sphere_0+displacement;
}

vec3 calc_normal(vec3 p){

	const vec3 SMALL_STEP = vec3(0.001, 0.0, 0.0);

    float gradient_x = map_the_world(p+SMALL_STEP.xyy)-map_the_world(p-SMALL_STEP.xyy);
    float gradient_y = map_the_world(p+SMALL_STEP.yxy)-map_the_world(p-SMALL_STEP.yxy);
    float gradient_z = map_the_world(p+SMALL_STEP.yyx)-map_the_world(p-SMALL_STEP.yyx);

    vec3 normal = vec3(gradient_x,gradient_y,gradient_z);

    return normalize(normal);


}

//ray march
vec3 ray_march(in vec3 ro, in vec3 rd ){

    float total_distance_marched = .0;
    const int MAX_STEP = 32;
    const float MIN_HIT_DISTANCE = 0.001;
    const float MAX_MARCH_Distance = 1000.0;


    for(int i=0;i<MAX_STEP;i++){

        vec3 currentPos = ro+rd*total_distance_marched;
        float dist = map_the_world(currentPos);
        //hit
        if(dist < MIN_HIT_DISTANCE){
            //shader
        	vec3 normal = calc_normal(currentPos);
            vec3 lightPos = vec3(3.0,-5.0,3.0);
            vec3 dir_to_light = normalize(-lightPos +currentPos);

            float diffuse = max(0.0, dot(normal, dir_to_light));

            return vec3(1.0,0.0,0.0)*diffuse;
        }
        total_distance_marched += dist;
        //not hit
        if(total_distance_marched > MAX_MARCH_Distance){
        	break;

        }


    }
    //return black
    return vec3(.0);


}



