const int maxIters = 25;
float epsilon = 0.005;
const int subdivisions = 2;

struct ray {
    vec3 o;
    vec3 d;
};

float square(float x) { return x*x; }

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec2 p, vec2 r) {
    return length(max(abs(p) - r, 0.0));
}

float sdCross(vec3 p) {
    p = abs(p) - vec3(0.0, 0.48, 0.0);
    p.y = abs(p.y);
    p.xz = p.x>p.z ? p.xz : p.zx;
	return length(max(p - vec3(0.17, 0.015, 0.015), 0.0));
}


float delta(float k, float diff) {
    if (diff > k) {
        return 0.0;
    } else {
        return (square(diff-k))*1.0/(4.0*k);
    }
}

float smax(float a, float b, float k) {
        return max(a, b) + delta(k, abs(a-b));
}

float sdStick(vec3 p) {
    float r = 0.5;
    float line = length(vec3(p.x, max(abs(p.y)-r, 0.0), p.z));
    return line - 0.01;
}

float gear(vec3 p, float angle) {
    if (p.y > 0.0) {
        angle += 0.5*6.28/12.0;
    }
    p.xz *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 q = p.xz;


    float segAngle = 6.28/12.0;
    float segment = round(atan(p.z, p.x) / segAngle);
    float theta = segment*segAngle;
    mat2 rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
    q = rot* q;
    float teeth = sdBox(q - vec2(0.212, 0.0), vec2(0.04, 0.02));

    float ring = abs(length(p.xz) - 0.18) - 0.03;
    float gear2d = min(teeth, ring);
    float boundingSphere = abs(length(p) - 0.5) - 0.02;
    float gear = smax(gear2d, boundingSphere, 0.005) - 0.005;

    return min(sdCross(p), min(sdStick(p), gear));
}

float gears(vec3 p) {

    float segAngle = 6.28/6.0;
    float d = 1000.0;
    for (int j = 0; j < 2; j++) {
        vec3 p1 = p;
        float theta = float(j)*6.28/4.0;
        mat2 rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
		p1.xz *= rot;

    for (int i = -1; i < 2; i++) {
		vec3 p2 = p1;
        float theta = float(i)*segAngle;

        mat2 rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));

        p2.xy *= rot;
        float offset;
        float segment = 6.28/12.0;
        switch(i) {
            case -1: offset = 0.0; break;
            case 0: offset = segment/2.0; break;
            case 1: offset = 0.0; break;
            default: offset = 0.0;
        }
        d = min(d, gear(p2, offset + 2.0*iTime*(i % 2 == 0 ? 1.0 : -1.0)));
    }
    }
    return d;
}

float scene(vec3 p) {
    float sphere = length(p) - 0.1;
    return min(sphere, gears(p));
}

vec3 getNorm(vec3 p) {
    return normalize(vec3(
        scene(vec3(p + vec3(epsilon, 0, 0))) - scene(vec3(p - vec3(epsilon, 0, 0))),
        scene(vec3(p + vec3(0, epsilon, 0))) - scene(vec3(p - vec3(0, epsilon, 0))),
        scene(vec3(p + vec3(0, 0, epsilon))) - scene(vec3(p - vec3(0, 0, epsilon)))
        ));
}

vec3 march(ray r) { 
    for (int i = 0; i < maxIters; i++) {
    	float d = scene(r.o);
        if (d < epsilon) {
            return (getNorm(r.o) + 1.0)/2.0;
        } else {
            r.o += r.d*d;
        }
    }
    return vec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 camPos = vec3((iMouse.x/iResolution.x-1.0)*sin(iTime), 2.0*(-iMouse.y/iResolution.y + 0.5), (iMouse.x/iResolution.x-1.0)*cos(iTime));
    vec3 lookAt = vec3(0);
    vec3 worldUp = vec3(0.0, 1.0, 0.0);
    vec3 camForward = normalize(lookAt - camPos);
    vec3 camRight = cross(camForward, worldUp);
    vec3 camUp = cross(camRight, camForward);

    vec2 filmSize = vec2(1.0, iResolution.y/iResolution.x);
    vec3 filmCentre = camPos + camForward*0.3;

    vec3 col = vec3(0);
    for (int i = 0; i < subdivisions; i++) {
        for (int j = 0; j < subdivisions; j++) {
        	vec2 uv;
            uv.x = (fragCoord.x + float(i)/float(subdivisions) - 0.5)/iResolution.x - 0.5;
            uv.y = (fragCoord.y + float(j)/float(subdivisions) - 0.5)/iResolution.y - 0.5;

            vec3 filmPos = filmCentre + filmSize.x*uv.x*camRight + filmSize.y*uv.y*camUp;
    		ray r = ray(camPos, normalize(filmPos - camPos));    
    		col += march(r);
    	}
    }
    fragColor = vec4(col/float(subdivisions*subdivisions), 1.0);
}

