float stair(vec3 p, float size, float w, float h) 
{
  vec3 bp=p;
  p.xy *= rotate(PI*0.25);
  p.x=rep(p.x,size*2.0);
  p.xy *= rotate(PI*0.25);
  float d=sdBox(p, vec3(size,size,w));
  d=max(d, abs(bp.y)-h);
  return d;
}





