float GetDist(vec3 p  ) 
{	

      float d;
    float dif1;
    float dif2;

    d=999.9;


   vec3 pp, p2;
    pp=p;
    p2=p;


    float planeDist1 = p.y+1.0;  //piso inf
    float planeDist2 = 30.0-p.y;  //piso sup
    float planeDist3 = p.x+30.0; //pared izq
    float planeDist4 = 30.0-p.x;  //pared der
    float planeDist5 = -p.z+40.0;  //pared atras

    vec2 res;
    res = vec2(9999, 0);


    bool  resp= espar(iTime);
    if (resp)
    { 
        float sdc1 =sdCylinder( p- vec3(-30.0,10.0,40.0), vec2(5.0,20.0) );	
        float sdc2 =sdCylinder( p- vec3(30.0,10.0,40.0), vec2(5.0,20.0) );	
        res =opU(res, vec2(sdc1,23));
        res =opU(res, vec2(sdc2,23));

    }
    else
    {
        float sdc1 =sdCylinder( p- vec3(-30.0,10.0,40.0), vec2(5.0,20.0) );	
        float sdc2 =sdCylinder( p- vec3(30.0,10.0,40.0), vec2(5.0,20.0) );	
        res =opU(res, vec2(sdc1,22));
        res =opU(res, vec2(sdc2,22));

    }


    float ssol;

    resp=espar(iTime*1.5);
    if (resp)
     { ssol= sol(p , vec3(10.0,35.,20.0+2.0*sin(2.0*iTime)) );	}
    else
    { ssol= sol(p , vec3(10.0,35.,20.0+5.0*cos(10.0*iTime)) );	}



    res =opU(res, vec2(ssol,28));

    float sdbInf =sdBox( p-vec3(0.0,-10.0,0.0), vec3(8.0,15.0,19.0) );
    float  sdif3= differenceSDF( planeDist1,sdbInf);


    //res =opU(res, vec2(planeDist1,2)); //inf
    res =opU(res, vec2(sdif3,30)); //inf

    res =opU(res, vec2(planeDist2,21)); //sup
    res =opU(res, vec2(planeDist3,21));
    res =opU(res, vec2(planeDist4,21));
    res =opU(res, vec2(planeDist5,21));


    float st1 =stair(p.zyx-vec3(0.0,0.0,-20.0), 1.5, 5.0, 30.0) ;


    float st2 =stair(p.zyx-vec3(0.0,-15.0,0.0), 1.5, 6.0, 12.0);
    res =opU(res, vec2(st1,3));
    res =opU(res, vec2(st2,5));


    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}



