void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 uv = (fragCoord.xy -0.5* iResolution.xy)/iResolution.x;
    vec2 p = uv;
    //rotate ring
    p*=rotate(iTime/1.);

    //create ring
    float ang = 6.283185/90.0;
    float sector = round(atan(p.x,p.y)/ang);
    float an = sector*ang;
    vec2 q = p;
    q = rotate(an)*q;

    //duplicate ring radialy 
    float c = 0.006;
    q.y-=iTime/10.;
    q.y = mod(q.y+0.5*c,c)-0.5*c;

    //make circles
    vec3 r = vec3(length(q));
    vec3 color = smoothstep(0.0019,0.002,r);
    fragColor = vec4(color,1.0);
}

#define PI 3.141592

float random(float p) { return fract(sin(p * 452.21) * 223.32); }

vec2 random2(vec2 p) {
  return vec2(fract(sin(dot(p, vec2(12.43, 24.12) * 452.21)) * 223.32),
              fract(sin(dot(p, vec2(46.26, 57.84) * 681.37)) * 412.49));
}

float perlinNoise1d(float p) {
  float i1 = floor(p), i2 = i1 + 1.;
  float p1 = p - i1, p2 = p - i2;
  float r1 = random(i1), r2 = random(i2);
  float d1 = dot(r1, p1), d2 = dot(r2, p2);
  float j = fract(p);
  j = j * j * (3. - 2. * j);
  return mix(d1, d2, j) * .5 + .5;
}

float perlinNoise2d(vec2 p) {
  vec2 i1 = floor(p), i2 = i1 + vec2(1., 0.), i3 = i1 + vec2(1., 1.),
       i4 = i1 + vec2(0., 1.);
  vec2 p1 = p - i1, p2 = p - i2, p3 = p - i3, p4 = p - i4;
  vec2 r1 = random2(i1), r2 = random2(i2), r3 = random2(i3), r4 = random2(i4);
  float d1 = dot(r1, p1), d2 = dot(r2, p2), d3 = dot(r3, p3), d4 = dot(r4, p4);
  vec2 j = fract(p);
  j = j * j * (3. - 2. * j);
  return mix(mix(d1, d2, j.x), mix(d4, d3, j.x), j.y);
}

float cellularNoise(vec2 p) {
  vec2 ci = floor(p);
  vec2 cf = fract(p);
  float minDist = 1000.0;

  for (int y = -1; y <= 1; y++) {
    for (int x = -1; x <= 1; x++) {
      vec2 neighbor = vec2(float(x), float(y));
      vec2 neighborPoint = random2(ci + neighbor);
      neighborPoint = 0.5 + 0.5 * sin(iTime * 3.5 + PI * 2.0 * neighborPoint);
      vec2 diff = neighborPoint + neighbor - cf;
      float dist = length(diff);
      minDist = min(minDist, dist);
    }
  }
  return minDist;
}

float moreNoise(vec2 p) { return 1.0 - 1.0 * cellularNoise(p); }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 p = fragCoord.xy / iResolution.xy;

  p.x +=iTime/90.;

  p.x += perlinNoise2d(p * vec2(12.2, 8.9) + vec2(iTime*0.8, 0.)) / 9.;
  p.y += perlinNoise2d(p * vec2(9.2, 6.9) + vec2(iTime*1.1, 0.)) / 9.;

  float color = smoothstep(1., 0., p.y) * 2. - 1.;
  color += cellularNoise(p * vec2(22.,7.) - vec2(0., iTime * 2.)); 

  float hei = perlinNoise1d(p.x * 12.) * 4. - 1.9;
  float color2 = smoothstep(hei, hei + 0.8, p.y) * 2. - 1.;
  color2 += moreNoise(p * vec2(25.,16.) - vec2(0., iTime * 2.));

  color = color - color2;
  color = step(0.5, color);
  color = 1. - color;

  fragColor = vec4(vec3(color), 1.0);
}

#define DELTA_DISTANCE  1e-10

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float random (in float _st) {
    return fract(sin(dot(vec2(_st, 0.0),
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float timescale = 0.5;

vec3 ProjectPointOnLine(vec3 a, vec3 v, vec3 pos)
{
 	vec3 p = pos-a;

	float l_l = length(v);
	float l_p = length(p);

	if (l_l<DELTA_DISTANCE)
		return a;
	if (l_p<DELTA_DISTANCE)
		return a;

	vec3 l_u = normalize(v);

	return a + l_u*dot(v,p)/l_l;
}

bool intersectPlaneAndLine(vec3 planePoint, vec3 planeVector, vec3 linePoint, vec3 lineVector, out vec3 p)
{
    float planeD = -(planeVector.x*planePoint.x + planeVector.y*planePoint.y + planeVector.z*planePoint.z);
    p = vec3(0);
    float temp = planeVector.x * lineVector.x + planeVector.y * lineVector.y + planeVector.z * lineVector.z;
    if (abs(temp) < 1E-40)
        return false;
    float t = (-planeD - planeVector.x * linePoint.x - planeVector.y * linePoint.y - planeVector.z * linePoint.z) / temp;
    p.x = lineVector.x * t + linePoint.x;
    p.y = lineVector.y * t + linePoint.y;
    p.z = lineVector.z * t + linePoint.z;
    return true;
}

