    //float pd = 1.0 - abs(prg - 0.5);
    //float pdprev = max(0.0, 0.5 - prg);
    //float pdnext = max(0.0, prg - 0.5);

    float ddd = pow(2.0*(prg - 0.5), 2.0) * 0.5;
    float pd = 1.0 - ddd;
    float pdprev = ddd;
    float pdnext = ddd;
    if (prg < 0.5)
        pdnext = 0.0;
    else
        pdprev = 0.0;

    vec2 dtotal = dprev * pdprev + d * pd + dnext * pdnext;
    return dtotal * 1.5;
}

