float GetAngle(vec3 v1, vec3 v2)
{
    float cosval = dot(v1, v2) / (length(v1) * length(v2));
    if (cosval < -1.0)
        cosval = -1.0;
    if (cosval > 1.0)
        cosval = 1.0;
    return acos(cosval);
}

vec3 RotateVectorAroundVector(vec3 axe, vec3 v, float angle)
{
    vec3 res;
    float a [3 * 3];

    float c = cos(angle);
    float s = sin(angle);
    axe = normalize(axe);
    a[0*3+0] = c + (1.0 - c) * axe.x * axe.x;
    a[0*3+1] = (1.0 - c) * axe.y * axe.x - s * axe.z;
    a[0*3+2] = (1.0 - c) * axe.z * axe.x + s * axe.y;
    a[1*3+0] = (1.0 - c) * axe.x * axe.y + s * axe.z;
    a[1*3+1] = c + (1.0 - c) * axe.y * axe.y;
    a[1*3+2] = (1.0 - c) * axe.z * axe.y - s * axe.x;
    a[2*3+0] = (1.0 - c) * axe.x * axe.z - s * axe.y;
    a[2*3+1] = (1.0 - c) * axe.y * axe.z + s * axe.x;
    a[2*3+2] = c + (1.0 - c) * axe.z * axe.z;
    res.x = v.x * a[0*3+0] + v.y * a[0*3+1] + v.z * a[0*3+2];
    res.y = v.x * a[1*3+0] + v.y * a[1*3+1] + v.z * a[1*3+2];
    res.z = v.x * a[2*3+0] + v.y * a[2*3+1] + v.z * a[2*3+2];
    return res;
}

vec4 raymarch(vec3 ro, vec3 rd) {

    vec3 path = vec3(0, 0, 0);
    path.y += 0.0;
    float stp = 0.004;
