    vec3 forward = vec3(0, 0, 1);
    vec3 axis = cross(pathzd, forward);
    float angle = GetAngle(pathzd, forward);
    for (int i = 0; i < 1000; i++) {
        float crstp = stp * pow(2.0, (crT - iTime)*0.5);
        crT += crstp;
        vec3 pathd = vec3(GetTotal(crT), 1.0);
        pathd = RotateVectorAroundVector(axis, pathd, angle);
        //pathd.x -= pathzd.x;
        //pathd.y -= pathzd.y;
        path += pathd * crstp * timescale;

        //vec3 prj = ProjectPointOnLine(ro, rd, path);
        vec3 prj;

        if (intersectPlaneAndLine(path, pathd, ro, rd, prj) && length2(path - prj) > maxdist)
        {
            maxdist = length2(path - prj);
            maxdistT = crT;
            maxdistProj = prj;
            maxdistProjV = path - prj;
            vec3 axis_ = cross(pathd, forward);
            float angle_ = GetAngle(pathd, forward);
            maxdistProjV = RotateVectorAroundVector(axis_, maxdistProjV, angle_);
        }
        if (maxdist > radius*radius)
        {
            break;
        }
    }
    float freq = 5.0;
    vec3 c = vec3(0.0, 0.0, 0.0);
    if (maxdist > radius*radius)
    {

        float an = GetAngle(maxdistProjV, vec3(1, 0, 0));
        vec3 c3 = vec3(
        cos(an * 5.0) * sin(fract(maxdistT) * 3.14 * 3.0),
        -cos(an * 5.0) * sin(fract(maxdistT) * 3.14 * 3.0),
        abs(cos(an * 5.0) * sin(fract(maxdistT) * 3.14 * 3.0))
    	);

        c = (c3 ) / 1.0;

    }
    return vec4(c, 1.0);
}

