void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2(.5 * ratio, .7);
    vec2 barPos = vec2(.5 * ratio, .3);
    vec2 b1Pos = vec2(.5 * ratio, .15);
    vec2 b2Pos = vec2(.5 * ratio, .21);
    vec2 b3Pos = vec2(.5 * ratio, .27);

    float dist = distance(shapePos, uv) * 2.0;

    vec2 uv1 = uv;
    uv1 -= shapePos;

    float angle = atan(uv1.y, uv1.x) + iTime;

    float ring = circle(uv,.33, 0., shapePos);
    ring -= circle(uv, .35, 0., shapePos);

    float back = circle(uv, .1, 0., shapePos);
    float plug = circle(uv, .05, 0., shapePos);
    float bar = rect(uv, barPos, vec2(.13, .7));
    float b1 = rect(uv, b1Pos, vec2(.04, .04));
    float b2 = rect(uv, b2Pos, vec2(.04, .04));
    float b3 = rect(uv, b3Pos, vec2(.04, .04));

    float radius = sin((angle + iTime * 12.)*3.);
    radius *= .6;

    float value = 1.-smoothstep(radius, radius +.1, dist);

    vec3 color = mix(vec3(.1), vec3(1.), back);
    color = mix(color, vec3(.1), bar);
    color = mix(color, vec3(1.), b1);
    color = mix(color, vec3(1.), b2);
    color = mix(color, vec3(1.), b3);
    color = mix(color, vec3(.2), value);
    color = mix( vec3(.1), color, plug);
    color = mix(color, vec3(.2), ring);

    // Output to screen
    fragColor = vec4(color*2.5,1.0);
}

#define pi 3.14159265
#define R(p, a) p=cos(a)*p+sin(a)*vec2(p.y, -p.x)
#define hsv(h,s,v) mix(vec3(1.), clamp((abs(fract(h+vec3(1., 1., 3.)/3.)*6.-3.)-1.), 0., 1.), s)*v

float smallBeat, mediumBeat, bigBeat;

float pn(vec3 p) { //noise @Las^Mercury
	vec3 i = floor(p);
	vec4 a = dot(i, vec3(1., 57., 21.)) + vec4(0., 57., 21., 78.);
	vec3 f = cos((p-i)*pi)*(-.5) + .5;
	a = mix(sin(cos(a)*a), sin(cos(1.+a)*(1.+a)), f.x);
	a.xy = mix(a.xz, a.yw, f.y);
	return mix(a.x, a.y, f.z);
}

float fpn(vec3 p) {
	return pn(p*.06125)*.5 + pn(p*.125)*.25 + pn(p*.25)*.125;
}

float field(vec3 hitpoint,float r,float R) {

	if ( sin(iChannelTime[0]) > .5 ) {

		 return (cos( hitpoint.x ) + cos( hitpoint.y ) + cos( hitpoint.z )) + r*1.2;

	} else {

		 return length(vec2(length(sin(hitpoint.yz + sin(hitpoint.xy))*cos(hitpoint.yz + cos(hitpoint.yx))+sin(hitpoint.xy)) - r, sin(hitpoint.x) - r)) - R;		//} else if(mediumBeat>0.1) {

	}

}

float distfunc(vec3 pos) {
	float t = iTime * 0.01;
	vec3 rotpos=mat3(vec3(cos(t*1.63),0.0,-sin(t*1.63)),vec3(0.0,1.0,0.0),vec3(sin(t*1.63),0.0,cos(t*1.63)))*mat3(vec3(cos(t*1.2),-sin(t*1.2),0.0),vec3(sin(t*1.2),cos(t*1.2),0.0),vec3(0.0,0.0,1.0))*(pos-vec3(1.0,1.0,-4.0));
	return field(rotpos,(0.6 * smallBeat)+0.4 + (0.6 * bigBeat),(0.5 * mediumBeat)+0.45 + (0.5 * bigBeat)) + fpn(rotpos*30.+ t*25.) * 0.15;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // create pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;


	float x = uv.x;
	float y = uv.y;

	vec3 ray_dir  = normalize( vec3( x, y, -1.0 ) );
	vec3 ray_orig = vec3( 5.0, 1.0, 2.0 );

	float j, w, ld, td= 0.,offs = 0.0;
	vec3 tc = vec3(0.);


	float fft = texture( iChannel0, vec2(uv.x,1.75) ).x;
	vec3 sync = vec3( fft, 4.0*fft*(1.0-fft), 1.0-fft ) * fft;

	smallBeat  = sync.r * 0.1;
	mediumBeat = sync.g * 0.1;
	bigBeat    = sync.b * 0.1;	

	for( float i = 0.0; i < 50.0;i += 1.0 ) {

		float dist = distfunc(ray_orig+ray_dir*offs);
		offs+=dist;

		if ( abs(dist) < .05 ) {

			 ld = 0.05 - dist;
			 w = (1. - td) * ld;   

			 tc = tc + w * hsv(w*3.-0.5, 1.-w*20., 1.) * hsv(w*3.-0.5, 1.-w*20., 1.); 
			 td = td + w ;	
		}	

		td += 1./200.;			

		if(abs(dist)<0.0001) break;
		else if(offs>50.0) break;

	}

	fragColor = vec4( tc + mediumBeat * 0.001, 1.0);

}

float map(float value, float fmin, float fmax, float tmin, float tmax) {
    return tmin + (value - fmin) * (tmax - tmin) / (tmax - tmin);
}

float modAxis(float p, float size) {
	float halfsize = size * 0.5;
	float c = floor((p + halfsize) / size);
	p = mod(p + halfsize, size) - halfsize;
	p = mod(-p + halfsize, size) - halfsize;
	return p;
}

float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

float sdBox(vec2 p, vec2 s, float r) {
    vec2 d = abs(p) - s;
    float sd = length(max(d, vec2(0))) + min(max(d.x, d.y), 0.0);

    return sd - r;
}

float su(float d1, float d2, float k) {
    float h = clamp(.5 + .5 * (d2 - d1) / k, 0., 1.);

    return mix(d2, d1, h) - k * h * (1. - h);
}

float ss(float d1, float d2, float k) {
    float h = clamp(.5 -.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1. - h);
}

float si(float d1, float d2, float k) {
    float h = clamp(.5 - .5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) + k * h * (1.0 - h);
}

float un(float d1, float d2) {
    return min(d1, d2);
}

vec2 rotate(vec2 p, float angle) {
    mat2 rot_mat = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

    return rot_mat * p;
}

vec2 magic(vec2 p) {
    mat2 mat = mat2(cos(p.x), cos(p.y), cos(p.y), cos(p.x));

    return mat * p;
}

