#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    	float angle = t;
	mat2 rotationMatrix = mat2(cos(angle), -sin(angle),
                               sin(angle),  cos(angle));



    float mData = texelFetch( iChannel0, ivec2(6,0), 0 ).x; //é³é¢æ°æ®
	vec3 c;
	float l,z=t;//é³é¢æ°æ®å½±åæ¶é´
	for(int i=0;i<3;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.5;
		p.x*=r.x/r.y;
		z+=.07;
		l=length(p);
		uv+=p/l*(sin(z)+1.-mData*mData*mData)*abs(sin(l*9.-z*1.));
