// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float vcaps( vec3 p, float h, float r )
{
    p.y -= clamp( p.y, 0.0, h );
    return length( p ) - r;
}

float water (vec3 p)
{
    p.y += texNoise(p.xz*0.1).r*0.2;
    p.y += sin(length((p.xz-vec2(0.,0.5))*3.)-iTime*3.)*0.05;
    return p.y;
}

float otter_body (vec3 p)   
{
    vec3 pp = p;
    p.yz *= rot(0.2);
    float r = 0.15+p.z*0.1;
    p.y += 0.2+sin((p.z)*5.)*0.15;
    float d = max(length(p.xy)-r, abs(p.z)-2.);

    p = pp;
    p.z -= 1.5;
    d = min(d, length(p*vec3(0.7,1.2,1.))-0.3);

    p = pp;
    p.yz += vec2(0.25,-1.);
    p.x = abs(p.x)-.5;
    p.z += p.y * p.y;
    d = min(d,vcaps(p,0.5, 0.08));
    return d;
}

float otter_face (vec3 p)
{
    vec3 pp = p;
    p.yz -= vec2(0.25,1.3);
    float d = length(p*vec3(0.6,1.,1.))-0.05;

    p = pp;
    p.yz -= vec2(0.28,1.4);
    p.x = abs(p.x)-0.15;
    d = min(d,length(p)-0.05);
    return d;
}

float ob,of;
float otter_all (vec3 p)
{
    p.x += sin(p.z*1.2+iTime*2.)*0.2;
    ob = otter_body(p)-texture(iChannel0, p.xz*0.3).r*0.05;
    of = otter_face(p);
    return min(ob,of);
}

float w;
float SDF (vec3 p)
{
    w = water(p);
    return min(otter_all(p),w);
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.1,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f,l));
    return normalize(f + uv.x * l + uv.y * u);
}

float spec (vec3 rd, vec3 l, vec3 n, float spec_pow)
{
    vec3 h = normalize(l-rd);
    return pow(max(dot(n,h),0.), spec_pow);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0.,2.5,-.1),
        p = ro,
        rd = getcam(ro,vec3(0.),uv),
        l = normalize(vec3(.8, 1., -.5)),
        col = vec3(0.);


    float d = 0.;
    for (float i=0.;i<32.;i++)
    {
        d = SDF(p);
        if (d<0.001) break;
        p += d*rd*0.5;
    }

    float temp_ob = ob;
    float temp_of = of;
    float temp_w = w;
    vec3 n = getnorm(p);
    float light = max(dot(n,l),0.);

    if (d == temp_ob) 
    {
        col = mix(vec3(0.,0.1,0.2),vec3(0.8,0.8,0.6),light);
        col += spec(rd,l,n, 1.)*0.4;
    }
    if (d == temp_of)
    {
        col = vec3(0.);
        col += spec(rd,l,n,15.);
    }
    if (d == temp_w)
    {
        col = mix(vec3(0.05,0.2,0.05),vec3(0.7,0.85,.95),light);
        col += spec(rd,l,n,25.)*0.2;
    }


	// vignetting (from iq)
	vec2 q = fragCoord.xy / iResolution.xy;
	col *= .5 + 0.5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), 0.15);

    fragColor = vec4(sqrt(col),1.0);
}

// variant of https://shadertoy.com/view/tdlfWX
// variant of https://shadertoy.com/view/XsdBW8

void mainImage(out vec4 O, vec2 U) {

    float T = iTime, t=1.,a;
    mat2  R = mat2( cos(-.3*T +vec4(0,33,11,0)) );
    vec3  q = iResolution, A,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),        // ray direction
          p = 30./q;                              // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p.xzy - vec3(0,0,T),
        q.xy *= R,
        A = abs(q), t = abs(max(A.x,A.y)-6.)-2.,  // bounding hollow box
        a = atan(q.y,q.x),
        t = max(t, sin(a-floor(q.z*4.)/4.*1.047)),// helix: stairs side
        t = max(t, sin(a-q.z*1.047+2.3) ),        // helix: roof side
        p += .7*t*D;                              // step forward = dist to obj
}

#define PI		3.14159265
#define EPS	0.001
#define MAX_STEPS 200
#define MAX_DIST 100.0
#define BOARD_SQUARE 1.0
#define SPHERE1 0
#define SPHERE2 1
#define SPHERE3 2
#define HEART	3
#define PLANE	4


struct Intersect {
    int		idx;
    float	dist;};

struct Point	{
	vec3	pos;
    vec3	col;};

vec3 diffCol[] = vec3[](vec3(0.0, 0.0, 1.0),
						vec3(1.0, 0.0, 1.0),
						vec3(0.0, 1.0, 0.3),
						vec3(1.0, 0.1, 0.1));

float diff[] =  float[](0.4, 0.5,  0.4,  0.8,  0.7);
float spec[] =  float[](0.9, 1.2,  1.1,  1.9,  0.3);
float specPow[]=float[](8.0, 16.0, 16.0, 32.0, 4.0);

vec3 lightPos = vec3(-3.0, 3.0, 5.0);

float sphereDist(in vec3 pos, in float radius, in vec3 center)
{
    return (length(pos + center) - radius);
}

float planeDist(in vec3 pos, in vec4 normal)
{
    return (dot(pos, normal.xyz) + normal.w);
}

float heartDist(vec3 q, float r)
{
    float ani = pow(0.5 + 0.5 * sin(7.0 * iTime + q.y / 20.0), 4.0);
    q *= 1.0 - 0.2 * vec3(1.0,0.5,1.0)*ani;
    q.y -= 0.2 * ani;
    float x = abs(q.x);

    float y = q.y;
    float z = q.z;
    y = -0.5 + y * 1.2 - x * sqrt(max((5.0 - x) / 10.0, 0.0));
    float res = sqrt(x*x + y*y + z*z) - r;
    return res;
}


Intersect sceneDist(vec3 pos)
{
	float SINt = sin(iTime);
    float SINt03 = sin(iTime * 0.3);
    float COSt03 = cos(iTime * 0.3);
    float sphereD1_1 = sphereDist(pos, 1.8, vec3(0, 0.1, 0));
	float sphereD1_2 = sphereDist(pos, 1.8, vec3(0, 0.1 - 2.0*(0.5 + 0.5 * SINt), 0));
    float sphereD2 = sphereDist(pos, 0.7, vec3(2.8 * COSt03, SINt, 2.8 * SINt03));
    float sphereD3 = sphereDist(pos, 0.5, vec3(3.8 * SINt03, cos(iTime), 3.8 * COSt03));
    float planeD = planeDist(pos, vec4(0.0, 1.0, 0.0, 2.2));
    float heartD = heartDist(pos, 0.5);
    Intersect res;

    // hollow sphere
	sphereD1_1 = abs(sphereD1_1) - 0.03;
	sphereD1_1 = max(sphereD1_1, pos.y);
	sphereD1_2 = abs(sphereD1_2) - 0.03;
	sphereD1_2 = max(sphereD1_2, - (pos.y - 2.0 * (0.5 + 0.5 * SINt)));

    res.dist = min(min(min(min(min(sphereD1_1, sphereD1_2), sphereD2), sphereD3), planeD), heartD);
    if (res.dist == sphereD1_1 || res.dist == sphereD1_2)
        res.idx = SPHERE1;
    else if (res.dist == sphereD2)
		res.idx = SPHERE2;
    else if (res.dist == sphereD3)
		res.idx = SPHERE3;
    else if (res.dist == heartD)
        res.idx = HEART;     
    else
        res.idx = PLANE;
    return (res);
}

vec3 getNormal(vec3 p)
{
    return normalize(vec3(
    sceneDist(vec3(p.x + EPS, p.y, p.z)).dist - sceneDist(vec3(p.x - EPS, p.y, p.z)).dist,
    sceneDist(vec3(p.x, p.y + EPS, p.z)).dist - sceneDist(vec3(p.x, p.y - EPS, p.z)).dist,
    sceneDist(vec3(p.x, p.y, p.z  + EPS)).dist - sceneDist(vec3(p.x, p.y, p.z - EPS)).dist));
}

Intersect rayMarch(vec3 ro, vec3 rd)
{
    Intersect d;
    Intersect intersect;
    for (int i = 0; i < MAX_STEPS; ++i)
    {
        d = sceneDist(ro + intersect.dist * rd);
        intersect.dist += d.dist;
        if (intersect.dist < EPS || intersect.dist >= MAX_DIST)
            break;
    }
    intersect.idx = d.idx;
    return (intersect);
}

float getShadow(in vec3 ro, in vec3 rd, in float min_t, in float max_t, in float k)
{
    float res = 1.0;
    for (float t = min_t; t < max_t;)
    {
        float dist = sceneDist(ro + rd * t).dist;
        if (dist < EPS)
            return 0.0;
        res = min(res, k * dist / t);
        t += dist;
    }
    return (res);
}

vec3 boardColor(vec3 p)
{
    vec3 pos = vec3(p.x, p.y, p.z - (0.7 * iTime));

    if ((mod(pos.x,(BOARD_SQUARE * 2.0)) < BOARD_SQUARE && mod(pos.z,(BOARD_SQUARE * 2.0)) > BOARD_SQUARE) ||
        (mod(pos.x,(BOARD_SQUARE * 2.0)) > BOARD_SQUARE && mod(pos.z,(BOARD_SQUARE * 2.0)) < BOARD_SQUARE))
		return vec3(1.0);
    else
		return vec3(0.0);
}

vec3 getLight(int idx, vec3 pos, vec3 eye)
{
    vec3 diffuseCol;
    if (idx == PLANE)
        diffuseCol = boardColor(pos);
    else
        diffuseCol = diffCol[idx];
    vec3 specularCol = vec3(1.0);
    vec3 Norm = getNormal(pos);
    vec3 Ldir = normalize(lightPos - pos);
    vec3 View = normalize(eye - pos);
    vec3 Refl = normalize(reflect(-Ldir, Norm));

    float dotLN = dot(Ldir, Norm);
    float dotRV = dot(Refl, View);

    // light not visible
    if (dotLN < 0.0)
        return vec3(0.0);

    // light reflection in opposite direction, apply only diffuse lighting
    if (dotRV < 0.0)
        return 1.7 * (diffuseCol * dotLN) * diff[idx];

    vec3 half_direction = normalize(normalize(Ldir) + View);
    float specular = pow(max(dot(half_direction, Norm), 0.0), specPow[idx]);
    return 1.7 * diffuseCol * dotLN * diff[idx] + specularCol * specular * spec[idx];
}

Point getColor(vec3 ro, vec3 rd, vec3 eye)
{
    Point point;
    Intersect intersect = rayMarch(ro, rd);
    point.pos = ro + intersect.dist * rd;
    if (intersect.dist > MAX_DIST - EPS)
        point.col = vec3(0.0, 0.0, 1.0 - 2.0 * rd.y); //background
    else
    {    
		//ambient
        vec3 ambientCol = vec3(0.2, 0.4, 0.7);    
    	vec3 ambientLight = 0.25 * vec3(1.0);
    	point.col = ambientLight * ambientCol;

    	//shadow
    	vec3 shadowDir = normalize(lightPos - point.pos);
    	float shadow = getShadow(point.pos + shadowDir, shadowDir, 0.0, MAX_DIST, 8.0);

    	//diffuse + specular
    	vec3 blinn_phong = getLight(intersect.idx, point.pos, eye) * shadow;
    	point.col += blinn_phong;
    }
    return point;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);
    vec2 mouse = 4.0 * (iMouse.xy / iResolution.xy);
    vec3 rd = normalize(vec3(uv, -1.0));
    vec3 ro = vec3(mouse.x, mouse.y, 8.0);

    Point point = getColor(ro, rd, ro);
	vec3 color = point.col;   

    vec3 rd1 = rd - (2.0 * dot(rd, getNormal(point.pos)) * getNormal(point.pos));
    Point point2 = getColor(point.pos + rd1, rd1, ro);
    color = mix(color, point2.col, 0.22);

    vec3 rd2 = rd1 - (2.0 * dot(rd1, getNormal(point2.pos)) * getNormal(point2.pos));
    Point point3 = getColor(point2.pos + rd2, rd2, ro);
    color = mix(color, point3.col, 0.12);

    fragColor = vec4(color, 1.0);
}

float uvrand(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = gl_FragCoord.xy;

    const float pi = 3.1415926;
    float t = floor(iTime * 1.1) * 7.3962;

    vec2 sc = (coord.xy - iResolution.xy / 2.0) / iResolution.y;
    float phi = atan(sc.y, sc.x + 1e-6);
    vec2 pc = vec2(fract(phi / (pi * 2.f)), length(sc));  //æ¥µåº§æ¨

    // æ¾å°
    float h_divnum = 5. + 10.*abs(sin(float(int(iTime))));
    float s_divnum = 5. + 10.*abs(sin(float(int(iTime))));

    float ddth = fract(pc.x * h_divnum);

    // æ¾å°åºåããã¨ã®ã©ã³ãã å¤
    float h_id = (pc.x * h_divnum) - fract(pc.x * h_divnum); 
    float h_rand = uvrand(vec2(h_id,int(iTime)));

    // åå¿ååºåããã¨ã®ã©ã³ãã å¤
    float s_id  = (pc.y * s_divnum) - fract(pc.y * s_divnum); 
    int   is_id = int(s_id);   

    float s_rand = uvrand(vec2(float(s_id),int(iTime)));



    // æ¨ªç·ããã¾ã
    float l = pc.y+ ddth * (h_rand - 0.5) + ddth*(s_rand - 0.5)*0.2 ;

    // æ¾å°ã»åå¿å

    float thickness = 0.5- pc.y + (s_rand*0.1 + h_rand*0.1) + (0.02*sin((h_rand)*iTime*10.f)); // å¾®å¦

    float a = step(abs(sin( ( pc.x*pi*2.f  )  * s_divnum )),thickness * 0.5);
    float b = step(abs(sin( ( pc.y*pi*2.f + h_rand + l)  * h_divnum )),thickness*0.5 );

    fragColor = vec4(a+b,a+b,a+b,1.0);
}

//https://thebookofshaders.com/07/


float gear (vec2 uv, float r, float a)
{
     float rr = length(uv)*2./r;
	 a = atan(uv.y,uv.x)+a/r;
	float f = smoothstep(-.5,1., cos(a*r*10.))*0.2/r+0.5;
    return 1.-smoothstep(f,f+0.02,rr)-step(length(uv),.02);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	vec2 pos = vec2(0.5)-uv;

    pos.x *=iResolution.x/iResolution.y;
    pos.y -= .2;

    vec3 color = vec3(0.0);

    //float r = length(pos)*2.0;
    //float a = atan(pos.y,pos.x);
    //a+=iTime;

     //f = cos(a*3.);
     //f = abs(cos(a*3.));
     //f = abs(cos(a*2.5))*.5+.3;
     //f = abs(cos(a*12.)*sin(a*3.))*.8+.1; 
     //f = smoothstep(-.5,1., cos(a*10.))*0.2+0.5;

	float f = gear(pos,.8,iTime)
        	+ gear(pos+vec2(-.7,.1),1.5,-iTime-.3)
