const int iterationAmount=1024*3;


float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

vec3 rotx(in vec3 p, float a) 
{
	return vec3(p.x,
                cos(a) * p.y + sin(a) * p.z,
                cos(a) * p.z - sin(a) * p.y);
}

vec3 roty(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.z,
                p.y,
                cos(a) * p.z - sin(a) * p.x);
}

vec3 rotz(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.y,
                cos(a) * p.y - sin(a) * p.x,
                p.z);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdAmigahSphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdPlane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdInvertedBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return -length(max(q,0.0)) - min(max(q.x,max(q.y,q.z)),0.0);
}

float sdTwistingBox( vec3 p, vec3 b, float r )
{
	p=rotx(p,2.0*sin(iTime)*sin(.92*p.x+iTime/2.0));
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdPlasmaCube( vec3 p, vec3 b, vec3 shift )
{
  p=rotx(p,iTime);
  p=roty(p,iTime);
  p+=shift;
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdTorus( vec3 p, vec2 t )
{
  p=roty(p,iTime+3.141592/2.0);
  vec2 q = vec2(length(p.xy)-t.x,p.z);
  return length(q)-t.y;
}

//#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
#define FK(k) int(cos(k))*int(k)
float hash(float a, float b) {
    int x = FK(a);int y = FK(b);
    return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

float noise(vec2 p) 
{
    vec2 id = floor(p);
    vec2 crds = fract(p);
    float h1 = hash(id.x,id.y);
    float h2 = hash(id.x+1.,id.y);
    float h3 = hash(id.x,id.y+1.);
    float h4 = hash(id.x+1.,id.y+1.);
    return mix(mix(h1,h2,crds.x),mix(h3,h4,crds.x),crds.y);
}

float triplanar(vec3 p, vec3 n) 
{
    return mix(noise(p.xy), mix(noise(p.xz), noise(p.yz), n.x*n.x), 1.-n.z*n.z);
}

float dot2( vec2 v ) { return dot(v,v); }
float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

