float mapBin(vec3 r)
{
    //r*=1.55;
	float mainCone=sdCappedCone(r-vec3(0.0,-0.4,0.0),.6,  0.5,0.7);
    float innerCone=sdCappedCone(r-vec3(0.0,-0.25,0.0),.55,0.45,0.65);
    float t=opSubtraction(innerCone,mainCone);
    //float t=100.0;

    vec3 capsr=r;

    const float PI=3.141592;
    const float TWOPI=PI*2.0;
    for (float i=0.0;i<TWOPI;i+=PI/18.0)
    {
        vec3 realcapsr=roty(capsr,i);
    	realcapsr=rotz(realcapsr,-0.15);
        float caps=sdVerticalCapsule(realcapsr-vec3(0.60,-0.63,0.0),0.9,0.06);
        t=opSubtraction(caps,t);
        //t=min(t,caps);
    }

    return t;
}

float mapDemosceneLogo(vec3 r)
{
    //r*=1.55;
    float t=10000.0;

    r=rotx(r,0.25);
    r=rotz(r,-0.5);

    float spessore=0.05;

    float horz1=sdBox(r-vec3(0.0,0.85,0.0),vec3(0.3,spessore,spessore));
    t=min(t,horz1);
    float horz2=sdBox(r-vec3(0.0,0.6,0.0),vec3(0.3,spessore,spessore));
    t=min(t,horz2);
    float horz3=sdBox(r-vec3(0.0,0.35,0.0),vec3(0.3,spessore,spessore));
    t=min(t,horz3);

    float vertleft=sdBox(r-vec3(-0.25,0.80,0.0),vec3(spessore,0.25,spessore));
    t=min(t,vertleft);
    float vertright=sdBox(r-vec3(0.25,0.80,0.0),vec3(spessore,0.25,spessore));
    t=min(t,vertright);
    float vertcenter=sdBox(r-vec3(0.0,0.25,0.0),vec3(spessore,0.3,spessore));
    t=min(t,vertcenter);

    return t;
}

// returns: t, material, hit type (0.0 solid, 1.0 transparent)

vec3 SDF(vec3 r)
{
    float amt=9.0; // bump amount on glass panels
    float t=0.0;
    float mat=0.0;
    vec3 origR=r;

    vec3 boxR=r-vec3(0.0,0.0,0.0);
    t=sdInvertedBox(boxR,vec3(4.0,2.5,35.0));

    //float domainRep=3.7;
    //vec3 c=vec3(domainRep);
    //r = mod(r+0.5*c,c)-0.5*c;

    // little boxes+balls at the entrance
    float pyrh=1.1;
    float pyr1=sdBox(r-vec3(3.0,-2.5,-5.0),vec3(.55,0.25,0.55));
    t=min(t,pyr1);

    float pyr2=sdBox(r-vec3(-3.0,-2.5,-5.0),vec3(.55,0.25,0.55));
    t=min(t,pyr2);

    float container1=sdRoundBox(r-vec3(3.0,-1.7,-5.0),vec3(.5,.5,.5),0.1);
    float innerBox1=sdBox(r-vec3(3.0,-1.7,-5.0),vec3(.49));
    container1=opSubtraction(innerBox1,container1);
    t=min(t,container1);

    float container2=sdRoundBox(r-vec3(-3.0,-1.7,-5.0),vec3(.5,.5,.5),0.1);
    float innerBox2=sdBox(r-vec3(-3.0,-1.7,-5.0),vec3(.49));
    container2=opSubtraction(innerBox2,container2);
    t=min(t,container2);

    float amigaball1=sdAmigahSphere(r-vec3(3.0,-1.7+0.05*sin(iTime*2.0),-5.0),0.45);
    t=min(t,amigaball1);

    float amigaball2=sdAmigahSphere(r-vec3(-3.0,-1.7+0.05*sin(iTime*2.0),-5.0),0.45);
    t=min(t,amigaball2);

    // metaballs

    float mbz=11.0;
    float mbally=0.0;
    if (iTime>32.0) mbally=(iTime-32.0)/2.5;

    float backS=sdSphere(r-vec3(1.7*sin(iTime),-.7*sin(iTime)+mbally,mbz),.76);
    backS+=(.04)*sin(r.x*9.0)*(sin(r.y*9.0))*cos(r.z*9.0);

    float backS2=sdSphere(r-vec3(-1.0*cos(iTime),-.1+mbally,mbz),.86);
    backS2+=(.04)*sin(r.x*9.0)*(sin(r.y*9.0))*cos(r.z*9.0);

    float backS3=sdSphere(r-vec3(-1.0*sin(iTime),-.7*cos(iTime)+mbally,mbz),.76);
    backS3+=(.04)*sin(r.x*9.0)*(sin(r.y*9.0))*cos(r.z*9.0);

    float metaballs=opSmoothUnion(opSmoothUnion(backS,backS2,0.5),backS3,0.5);
    t=min(t,metaballs);

    // twisting block
    vec3 tbr=r;
    float tblockpos=.5;

    float spikeAmt=6.0;
    float tblocky=0.0;
    if (iTime>25.0) tblocky=1.5*sin((iTime-25.0)/2.3);
    float tblock=sdTwistingBox(tbr-vec3(iTime/4.0,tblocky,2.0+tblockpos),vec3(17.0,.87,.87),0.1);
    tblock+=(.12)*sin(r.x*spikeAmt)*(.52*sin(r.y*spikeAmt))*(.52*cos(r.z*spikeAmt));
    t=min(t,tblock);

    float trashbin=10000.0;
    float merlogo=10000.0;

    if (iTime>24.0)
    {        
        // biiin
        float binx=3.2;
        float binz=14.5;
        trashbin=mapBin(r-vec3(binx,-1.5,binz));
        t=min(t,trashbin);
        // random demoscene logo
        merlogo=mapDemosceneLogo(r-vec3(binx-0.21,-1.7,binz-0.1));
        t=min(t,merlogo);
    }

    // cyboman's torus (not really)
    vec3 rottor=r;
    float cybotorus=sdTorus(rottor-vec3(0.0,-.5,21.0),vec2(1.5,.5));
    cybotorus+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,cybotorus);

    // plasmacube from 2nd reality
    float cubeZ=27.0;
    float cubeBase=sdBox(r-vec3(0.0,-1.96,cubeZ),vec3(1.35,1.07,1.35));
    t=min(t,cubeBase);

    vec3 rcubed=r;
    float cubeSide=0.52;

    //float innerCube=sdBox(rcubed-vec3(0.0,0.0,cubeZ-cubeSide/2.0),vec3(cubeSide-0.1,cubeSide-0.1,cubeSide-0.1));

    float sideProf=0.32;
    float pcube0=sdPlasmaCube(rcubed-vec3(0.0,0.0,cubeZ-cubeSide/2.0),
                              vec3(cubeSide,cubeSide,sideProf),
                              vec3(0.0,0.0,-cubeSide/2.5));
    float pcube1=sdPlasmaCube(rcubed-vec3(0.0,0.0,cubeZ-cubeSide/2.0),
                              vec3(cubeSide,cubeSide,sideProf),
                              vec3(0.0,0.0,cubeSide/2.5));
    float pcube2=sdPlasmaCube(rcubed-vec3(0.0,0.0,cubeZ-cubeSide/2.0),
                              vec3(cubeSide,sideProf,cubeSide),
                              vec3(0.0,cubeSide/2.5,0.0));
    float pcube3=sdPlasmaCube(rcubed-vec3(0.0,0.0,cubeZ-cubeSide/2.0),
                              vec3(cubeSide,sideProf,cubeSide),
                              vec3(0.0,-cubeSide/2.5,0.0));
    float pcube4=sdPlasmaCube(rcubed-vec3(0.0,0.0,cubeZ-cubeSide/2.0),
                              vec3(sideProf,cubeSide,cubeSide),
                              vec3(-cubeSide/2.5,0.0,0.0));
    float pcube5=sdPlasmaCube(rcubed-vec3(0.0,0.0,cubeZ-cubeSide/2.0),
                              vec3(sideProf,cubeSide,cubeSide),
                              vec3(cubeSide/2.5,0.0,0.0));
    t=min(t,pcube0);
    t=min(t,pcube1);
    t=min(t,pcube2);
    t=min(t,pcube3);
    t=min(t,pcube4);
    t=min(t,pcube5);

    float container3=sdRoundBox(r-vec3(0.0,-0.1,cubeZ),vec3(1.25,1.25,1.25),0.1);
    float innerBox3=sdBox(r-vec3(0.0,-0.1,cubeZ),vec3(1.15,1.15,1.15));
    container3=opSubtraction(innerBox3,container3);
    t=min(t,container3);
    //float container3=1000.0;

    //
    // glass panels
    //

    float boxxpos=3.0;
    float glass0z=0.0;
    if (iTime>11.0) boxxpos+=iTime-11.0;
    float rbox=sdRoundBox(r-vec3(boxxpos,-0.1,glass0z),vec3(3.0,4.6,0.1),0.1);
    rbox+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox);

    float rbox2=sdRoundBox(r-vec3(-boxxpos,-0.1,glass0z),vec3(3.0,4.6,0.1),0.1);
    rbox2+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox2);

    float boxxpos2=3.0;
    float glass1z=8.0;
    if (iTime>26.5) boxxpos2+=iTime-26.5;
    float rbox3=sdRoundBox(r-vec3(boxxpos2,-0.1,glass1z),vec3(3.0,4.6,0.1),0.1);
    rbox3+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox3);

    float rbox4=sdRoundBox(r-vec3(-boxxpos2,-0.1,glass1z),vec3(3.0,4.6,0.1),0.1);
    rbox4+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox4);

    float boxxpos3=3.0;
    float glass2z=16.0;
    if (iTime>36.0) boxxpos3+=iTime-36.0;
    float rbox5=sdRoundBox(r-vec3(boxxpos3,-0.1,glass2z),vec3(3.0,4.6,0.1),0.1);
    rbox5+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox5);

    float rbox6=sdRoundBox(r-vec3(-boxxpos3,-0.1,glass2z),vec3(3.0,4.6,0.1),0.1);
    rbox6+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox6);

    float boxxpos4=3.0;
    float glass3z=25.0;
    if (iTime>44.0) boxxpos4+=iTime-44.0;
    float rbox7=sdRoundBox(r-vec3(boxxpos4,-0.1,glass3z),vec3(3.0,4.6,0.1),0.1);
    rbox7+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox7);

    float rbox8=sdRoundBox(r-vec3(-boxxpos4,-0.1,glass3z),vec3(3.0,4.6,0.1),0.1);
    rbox8+=(.04)*sin(r.x*amt)*(sin(r.y*amt))*cos(r.z*amt);
    t=min(t,rbox8);

    // calc materials

    if (t==metaballs) return vec3(t,2.0,0.0);
    if (t==tblock) return vec3(t,3.0,0.0);
    if ((t==container1)||(t==container2)||(t==container3)) return vec3(t,7.0,1.0);
    if ((t==rbox)||(t==rbox2)||(t==rbox3)||(t==rbox4)||(t==rbox5)||(t==rbox6)||(t==rbox7)||(t==rbox8)) return vec3(t,0.0,1.0);
    if ((t==pyr1)||(t==pyr2)||(t==cubeBase)) return vec3(t,5.0,0.0);
    if ((t==amigaball1)||(t==amigaball2)) return vec3(t,6.0,0.0);
    if (t==cybotorus) return vec3(t,4.0,0.0);
    if ((t==pcube0)||(t==pcube1)) return vec3(t,8.0,0.0);
    if ((t==pcube2)||(t==pcube3)) return vec3(t,8.1,0.0);
    if ((t==pcube4)||(t==pcube5)) return vec3(t,8.2,0.0);
    if (t==trashbin) return vec3(t,9.0,0.0);
    if (t==merlogo) return vec3(t,10.0,0.0);

    return vec3(t,1.0,0.0);
}

float calcAO( in vec3 pos, in vec3 nor, float h )
{
	float occ = 0.0;
    float sca = 1.0;
    pos.y+=h;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = SDF( aopos )[0];
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

vec3 calcNormal(vec3 pos)
{
	vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3(( mod(float((i+3)/2),2.0) ),(mod(float(i/2),2.0)),(mod(float(i),2.0)))-1.0);
        n += e*SDF(pos+0.0005*e)[0];
    }
    return normalize(n);
}

vec4 castRay(vec3 rayOrigin, vec3 rayDir)
{
    float t=0.0;
    float trasp=0.0;
    bool refrakkted=false;
    vec3 rd=rayDir;
    for (int i=0;i<iterationAmount;i++)
    {
        vec3 res = SDF(rayOrigin + rd * t);
        if (res[0] < (0.0001*t))
        {
            if (res[2]==0.0)
            {
            	return vec4(t,res[1],trasp,0.0);
            }
            else
            {
                if (!refrakkted)
                {
                    vec3 hitNorm=calcNormal(rayOrigin+rd*t);
                    vec3 newdir;
                    if (res[1]==7.0) newdir=rd; // don't refract the thecas
                    else newdir=refract(rd,hitNorm,0.90);
                    rd=newdir;
                    trasp=t;
                    refrakkted=true;
                }
            }
        }

        if (!refrakkted) t+=res[0];
        else t += 0.02;
    }

    return vec4(-1.0,-1.0,-1.0,-1.0);
}

vec3 fog(vec3 c, float dist, vec3 fxcol)
{
    const float FOG_DENSITY = 0.06;
    vec3 FOG_COLOR = fxcol.xyz;

    float fogAmount = 1.0 - exp(-dist * FOG_DENSITY);

    return mix(c, FOG_COLOR, fogAmount);
}

vec3 srgb(float r, float g, float b) {
    return vec3(r*r,g*g,b*b);
}
vec3 srgb(float r) {
    return vec3(r*r);
}

vec4 cubePlasma (vec2 coord, vec3 p, int ptype) 
{
    float 
        k = iTime * 3.0,
        as = 4.78207464329,
    	fd = 12.18910931609,
    	as1 = 2.839499561581 / 24.0 + sin(k) * 0.02,
    	fd2 = 2.188989119604,
    	ps = -5.624243766069,
    	ps2 = 9.393007904291,
    	p1 = sin(k / ps) * 4.0,
    	p2 = sin(k / fd) * 3.0,
    	t3 = sin(k / fd2) + cos(as1),
        t4 = sin(k / ps);    

    float coordDivider=32.0;
    coord.x = floor(coord.x * coordDivider) / coordDivider;
    coord.y = floor(coord.y * coordDivider) / coordDivider;

    float x = sin(p1 + coord.x) * sin(p2 + coord.y) + sin(t3 + coord.x) * sin(t4 + coord.y) * -p.x;

    float r,g,b;

    if (ptype==0)
    {
        r = (0.01 );
        g = (0.12 + sin(x / as1)) / 2.0; 
        b = (0.97 + sin(x / as1 )) + sin(coord.y / as1) / 4.0;
    }
    else if (ptype==1)
    {
        r = (0.81 + sin(x / as1)) / 2.0;
        g = (0.51 + sin(x / as1)) / 2.0; 
        b = (0.37 + sin(x / as1 )) + sin(coord.y / as1) / 14.0;
    	//r=pow(r,2.9);
        r+=0.5;
        g+=0.5;
    	g=pow(g,2.0);
    	b=pow(b,2.0);
    }
    else if (ptype==2)
    {
        r = (0.31 + sin(x / as1)) / 2.0;
        g = (0.97 + sin(x / as1)) / 2.0; 
        b = (0.17 + sin(x / as1 )) + sin(coord.y / as1) / 14.0;
    	r=pow(r,2.9);
    	g=pow(g,2.0);
    	//b=pow(b,2.0);
    }

    r=clamp(r,0.0,1.0);
    g=clamp(g,0.0,1.0);
    b=clamp(b,0.0,1.0);
    vec4 color = vec4(vec3(r,g,b), 1.0);
    return color;
}

vec4 boxmap( in vec3 p, in float k, int ptype ) {
    vec3 m = pow( abs(p), vec3(k) );
	vec4 x = cubePlasma(p.yz, p,ptype);
	vec4 y = cubePlasma(p.zx, p,ptype);
	vec4 z = cubePlasma(p.xy, p,ptype);
	return (x * m.x + y * m.y + z * m.z) / (m.x + m.y + m.z);
}

vec4 render(vec3 rayOrigin, vec3 rayDir, vec2 uv, vec2 fragCoord)
{
    vec3 col=vec3(0.);
    vec3 L=normalize(vec3(1.33,1.0,-1.0));

    vec4 rayHit = castRay(rayOrigin, rayDir);

    float mat=rayHit[1];
    vec3 pHit=rayOrigin+rayDir*rayHit[0];
    vec3 N=calcNormal(pHit);
    float dotprod=abs(dot(N,L));

    if (mat==1.0)
    {
        float occlHeight=1.6;
        if (pHit.z<0.0) occlHeight=0.52; // amigahballs
        if ((pHit.z>13.5)&&(pHit.z<15.0)) occlHeight=0.5; // bin
        if (pHit.z>16.0) occlHeight=0.2; // cybomantorus
        float occ = calcAO( pHit, vec3(0.0,1.0,0.0),occlHeight );

        vec2 a=vec2(1.0);
        if ((N.z>0.01)||(N.z<-0.01))
        {
            a=vec2(
                vec2(1.)*smoothstep(-0.05, 0.05, mod(pHit.x, 1.))*
                smoothstep(-0.05, 0.05, mod(pHit.y, 1.)));
        }
        else if ((N.x>0.01)||(N.x<-0.01))
        {
            a=vec2(
                vec2(1.)*smoothstep(-0.05, 0.05, mod(pHit.y, 1.))*
                smoothstep(-0.05, 0.05, mod(pHit.z, 1.)));
        }
        else if ((N.y>0.01)||(N.y<-0.01))
        {
            a=vec2(
                vec2(1.)*smoothstep(-0.05, 0.05, mod(pHit.x, 1.))*
                smoothstep(-0.05, 0.05, mod(pHit.z, 1.)));
        }

        float cenDist=distance(vec3(pHit.x,pHit.y,mod(pHit.z,6.0)),vec3(0.0,0.0,3.0));

        if (pHit.z<0.0)
        {
            //if (iTime>4.80) col = vec3(.82-a.y,.73-a.x,0.62-a.y);
            //else col = vec3(.4-a.x,.5-a.y,.6-a.x);
            col = vec3(.82-a.y,.73-a.x,0.62-a.y);
        }
        else if ((pHit.z>=0.0)&&(pHit.z<8.0))
        {
            if (iTime>5.80) col = vec3(.7-a.x,.8-a.y,0.9-a.x);
            else col = vec3(.4-a.x,.5-a.y,.6-a.x);
        }
        else if ((pHit.z>=8.0)&&(pHit.z<16.0))
        {
            if (iTime>6.80) col = vec3(.82-a.y,.73-a.x,0.62-a.y);
            else col = vec3(.4-a.x,.5-a.y,.6-a.x);
        }
        else if ((pHit.z>=16.0)&&(pHit.z<25.0))
        {
        	col = vec3(.4-a.x,.5-a.y,.6-a.x);
        }
        else
        {
            if (iTime>8.80) col = vec3(.82-a.y,.73-a.x,0.62-a.y);
        	else col =vec3(.4-a.x,.5-a.y,.6-a.x);
        }

        col+=vec3(1.0)/cenDist*1.25;
        if (N.y>0.01) col=mix(col,vec3(occ),0.06);
        //col=fog(col,distance(rayOrigin,pHit)/4.0,vec3(1.,1.,1.));
    }
    else if (mat==2.0) // metaballs
    {
        dotprod+=pow(dotprod,16.0);
        col=vec3(.85,.5,.7)*dotprod;

        vec3 oldcolor = col + (col * texture(iChannel1, (mod(fragCoord, 8.0) / 8.0)).rrr);
        col = floor(oldcolor);    
    }
    else if (mat==3.0) // twisting block
    {
        vec3 r = reflect(rayDir,N);
        float marble = smoothstep(-0.5,0.4,triplanar(pHit*2., N));
        float tex = max(triplanar(pHit*20., N),0.)*marble;
        float spexex = mix(5., 6., tex);
        float diff1 = pow(length(sin(r*2.)*0.5+0.5)/sqrt(3.),2.);
        float diff2 = pow(length(sin(r*2.+3.5)*0.5+0.5)/sqrt(3.),2.);
        float diff3 = pow(length(sin(r*2.5+1.)*0.5+0.5)/sqrt(3.),2.);
        vec3 col1 = mix(srgb(0.,.05,.1), srgb(0.4,.3,1.), diff1) + pow(diff1, spexex)*1.5;
        vec3 col2 = mix(srgb(0.1,.0,0.3), srgb(1.,.2,0.5), diff2) + pow(diff2, spexex)*1.5;
        vec3 col3 = mix(srgb(0.33), srgb(0.2,0.9,1.), diff3) + pow(diff3, spexex)*2.5;
        col = mat3(col2,col1,col3)*transpose(mat3(col2,col1,col3))*vec3(mix(0.3,0.1,marble));  

        if (iTime<5.80) col*=vec3(0.25);
    }
    else if (mat==4.0) // rolling torus
    {
        vec3 r = reflect(rayDir,N);
        float marble = smoothstep(-0.5,0.4,triplanar(pHit*2., N));
        float tex = max(triplanar(pHit*20., N),0.)*marble;
        float spexex = mix(5., 6., tex);
        float diff1 = pow(length(sin(r*2.)*0.5+0.5)/sqrt(3.),2.);
        float diff2 = pow(length(sin(r*2.+3.5)*0.5+0.5)/sqrt(3.),2.);
        float diff3 = pow(length(sin(r*2.5+1.)*0.5+0.5)/sqrt(3.),2.);
        vec3 col1 = mix(srgb(0.2,.05,.1), srgb(0.24,.3,1.), diff1) + pow(diff1, spexex)*2.5;
        vec3 col2 = mix(srgb(0.41,.0,0.3), srgb(0.5,.2,0.5), diff2) + pow(diff2, spexex)*2.5;
        vec3 col3 = mix(srgb(0.33), srgb(0.02,0.3,1.), diff3) + pow(diff3, spexex)*3.5;
        col = mat3(col2,col1,col3)*transpose(mat3(col2,col1,col3))*vec3(mix(0.3,0.1,marble));        
    }
    else if (mat==5.0) // ball's base
    {
        float occ = calcAO( pHit, vec3(0.0,1.0,0.0),0.3);
        col=vec3(.05,.05,.05)*dotprod;
		if (N.y>0.01) col=mix(col,vec3(occ),0.06);
    }
    else if (mat==6.0) // amigahballs
    {
    	vec3 amigaLight=normalize(vec3(1.03,1.0,-1.0));;
        if (pHit.x>0.0) amigaLight=normalize(vec3(-1.03,1.0,-1.0));;

        vec3 N2=roty(N,iTime);
        vec2 matuv = vec2( atan(N2.x,N2.z), acos(N2.y ) );
        vec2 qp = floor(matuv*2.51);
        float intensity=mod(qp.x+qp.y,2.0);
        if (intensity==1.0) col=vec3(1.0,0.0,0.0);
        else col=vec3(1.0);
        float NoL = max(dot(N, amigaLight), 0.1);
        col*=NoL;
        col+=pow(NoL,32.0);

        vec3 oldcolor = col + (col * texture(iChannel1, (mod(fragCoord, 8.0) / 8.0)).rrr);
        col = floor(oldcolor);    
    }
    else if ((mat==8.0)||(mat==8.1)||(mat==8.2)) // plasmacube
    {
        // 2nd reality cube faces are 
        // red-yellow-black
        // blue-white-black
        // green-brown-black

        float cubeZ=28.0;
        if (mat==8.0) col=boxmap(vec3(pHit.x,pHit.y,pHit.z-cubeZ),34.0,0).xyz;
        else if (mat==8.1) col=boxmap(vec3(pHit.x,pHit.y,pHit.z-cubeZ),34.0,1).xyz;
        else col=boxmap(vec3(pHit.x,pHit.y,pHit.z-cubeZ),34.0,2).xyz;

        //col=vec3(1.0,0.0,0.0);
        col*=dotprod;
    }
    else if (mat==9.0) // trashbin
    {
        vec3 r = reflect(rayDir,N);
        float marble = smoothstep(-0.5,0.4,triplanar(pHit*2., N));
        float tex = max(triplanar(pHit*20., N),0.)*marble;
        float spexex = mix(5., 6., tex);
        float diff1 = pow(length(sin(r*2.)*0.5+0.5)/sqrt(3.),2.);
        float diff2 = pow(length(sin(r*2.+3.5)*0.5+0.5)/sqrt(3.),2.);
        float diff3 = pow(length(sin(r*2.5+1.)*0.5+0.5)/sqrt(3.),2.);
        vec3 col1 = mix(srgb(0.05,.05,.05), srgb(0.3,.3,.3), diff1) + pow(diff1, spexex)*1.5;
        vec3 col2 = mix(srgb(0.1,.1,0.1), srgb(.2,.2,0.2), diff2) + pow(diff2, spexex)*1.5;
        vec3 col3 = mix(srgb(0.43,0.43,0.43), srgb(0.82,0.82,0.82), diff3) + pow(diff3, spexex)*2.5;
        col = mat3(col2,col1,col3)*transpose(mat3(col2,col1,col3))*vec3(mix(0.3,0.1,marble));
        col*=0.8;
    }
    else if (mat==10.0) // random demoscene logo
    {
        col=vec3(1.0,85.0/255.0,0.0)*dotprod;
        col*=0.8;
    }
    else
    {
        col=vec3(0.0,1.0,0.0);
    }

    if (rayHit[2]!=0.0)
    {
        vec3 tN=calcNormal(rayOrigin+rayDir*rayHit[2]);
        float dotprod=max(dot(tN,L),0.0);
        dotprod+=pow(dotprod,2.0);
        col=mix(col,vec3(0.8,0.8,0.8)*dotprod,0.15);
    }

    col=pow(col,vec3(0.58));
    return vec4(col,1.0);
}

vec3 getCameraRayDir(vec2 uv, vec3 camPos, vec3 camTarget)
{
    vec3 camForward = normalize(camTarget - camPos);
    vec3 upz=vec3(0.,1.,0.);
    vec3 camRight = normalize(cross(upz, camForward));
    vec3 camUp = normalize(cross(camForward, camRight));

    float fPersp = 2.0;
    vec3 vDir = normalize(uv.x * camRight + uv.y * camUp + camForward * fPersp);

    return vDir;
}

vec2 normalizeScreenCoords(vec2 screenCoord)
{
    vec2 result = 2.0 * (screenCoord/iResolution.xy - 0.5);
    result.x *= iResolution.x/iResolution.y;
    return result;
}

float onelinerRandom(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = normalizeScreenCoords(fragCoord);

    vec3 camPos,camTarget;

    //float fft = texture( iChannel0, vec2(0.0,0.0) ).x;

    // opening fade in
    if (iTime<10.4)
    {
        camPos=vec3(0.0,-1.8,-11.0+(iTime/11.0));
        camTarget=vec3(0.0,0.0,-4.0+(iTime/11.0));
    }
    // panning right-left
    else if ((iTime>=10.4)&&(iTime<20.0))
    {
        camPos=vec3(10.4-iTime/1.5,-1.6,-8.0);
        camTarget=vec3(10.4-iTime/1.5,-1.6,-7.0);
    }
    // walkthrough
    else if ((iTime>=20.0)&&(iTime<49.0))
    {    
        camPos=vec3(0.0,-0.1,-26.0+iTime);
        camTarget=vec3(0.0,0.0,0.0+iTime);
    }
    else
    {
        camPos=vec3(0.0,-0.1,(-18.0+42.0)-(abs(.7*sin(iTime-49.0))));
        camTarget=vec3(0.0,0.0,-18.0+42.0+2.0);
    }

    //camPos=vec3(sin(iTime)*0.2,0.6,7.5);
    //camTarget=vec3(0.0,0.0,9.0);
    vec3 rayDir = getCameraRayDir(uv, camPos, camTarget);   
    vec4 finalCol=vec4(render(camPos, rayDir,uv,fragCoord).xyz,1.0);

    // fadein
    if (iTime<2.001)
    {
        finalCol=vec4(mix(finalCol.xyz,vec3(0.0),(2.0-iTime)/2.0),1.0);
    }

    // fadeout
    if (iTime>55.0)
    {
        finalCol=vec4(mix(vec3(0.0),finalCol.xyz,clamp((59.0-iTime)/4.0,0.0,1.0)),1.0);
    }

    fragColor=vec4(finalCol.rgb, 1.0);
}

float Hash11(float a){
 a=sin(a*564.331)*cos(a*126.542);
    return fract(a);
}
float disortion(vec2 uv, float h, float thickness,float sinus){
    float res=0.;
	if(uv.y>h && uv.y <h+thickness)
    res= sin(sinus*(uv.y-h));
    if(res>.99)res=Hash11(iTime*346.221);
    return res;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);
    float line1=fract(iTime*.38)*12.-4.;
    float line2=fract(iTime*.21)*12.-4.;  
    float line3=fract(iTime*.11)*12.-4.;      

    float scale=8.;
   	vec2 uvv=uv*smoothstep(-1.5,2.,length(uv))*scale;

    uvv.x-=disortion(uvv,line1,.3,14.);
    uvv.x-=disortion(uvv,line2,.4,8.);
    uvv.x-=disortion(uvv,line3,.2,18.);    
	vec2 fv=fract(uvv);
    vec2 gv=floor(uvv);

    float color=Hash11(gv.x);
    color+=uv.x*uv.y;
 	if(mod(uvv.y*iResolution.y/16.,2.)<=0.3)color-=.05; 
    else color+=sin(iTime)*.08+.1;

    col+=vec3(color*.3,color*.9,color*.4);
    fragColor = vec4(col,1.0);
}

