vec3 color_cycled(int i) {
    i = (i + int(iTime)) % 10;
	// Here we assume colors has the first one repeated rather than doing the usual mod 10;
    float t = fract(iTime);
    t = mix(t, smoothstep(0., 1., t), .2);
    return mix(colors[i], colors[i+1], t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // put things into a square
    uv.x -= .5;
    uv.x /= iResolution.y / iResolution.x;
    uv.x += .5;

    if (uv.x < .0 || uv.x > 1.) {
        fragColor = background;
        return;
    }

    // 10x10 grid.
    vec2 grid = uv * 10.;
    uv = fract(grid); 

    // center-based coordinates
    uv -= .5;
    uv = abs(uv*2.);

    vec3 col = background.rgb;


    if (max(uv.x, uv.y) < 0.50) col.rgb = color_cycled(outer[int(grid.x) + 10*int(grid.y)]);
    if (max(uv.x, uv.y) < 0.25) col.rgb = color_cycled(inner[int(grid.x) + 10*int(grid.y)]);


    // Output to screen
    fragColor = vec4(col,1.0);
}

const int ITERATIONS = 64;
const float SPEED = 0.25;
const float PAN = 2.8;
const float MASK_VIS = 0.0; // Set between 0-1 to visualize masks

float vortex(vec2 uv, float dist, float seed, float bias, float offset)
{
    float ang = atan(uv.y, uv.x)+sin(dist+0.1*seed)*(1.2-offset)*2.0;
    ang += 3.14159*(0.01*seed);
    return clamp((sin((ang)*(3.0+offset*float(ITERATIONS)))+bias)/(1.0+bias), 0.0, 1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(iResolution.xy*0.5))/iResolution.y;
    float texel = 1.0/iResolution.y;
    float dist = length(uv);
    vec3 col = vec3(0.0);
    for (int i=0; i<ITERATIONS; i++) 
    {
        float offset = float(i)/float(ITERATIONS);
        float seed = 1000.0*fract(3.1379136*floor(iTime*SPEED+offset));
        float time = fract(iTime*SPEED+offset);
        vec2 pan = vec2(0.0, time*PAN);
        float maskA = vortex(uv, dist, seed+100.0*float(i), -0.998+texel, offset);
        float dist2 = length(uv+pan);
        float maskB = vortex(uv+pan, dist2, seed+42.0*float(i), -0.99+texel, offset);
        float radius = pow(((maskA*maskA)+(maskB*maskB)), 2.0);
        float fade = time*(1.0-time);
        float mask = maskA*maskB*fade*radius;
        col = mix(col, vec3(0.42+1.0*dist-0.2*offset-0.1*radius, 0.75-0.5*offset-0.5*dist, 1.0-0.8*dist), mask);
        col = max(vec3(maskA*MASK_VIS, maskB*MASK_VIS+0.1, max(maskA*MASK_VIS*2.0, maskB*MASK_VIS)+0.12)*fade, col);
    }
    fragColor = vec4(col*4.0,1.0);
}

ivec2 dataSet = ivec2(0x01234567, 0x89ABCDEF);

float selectFromMat(ivec2 coord, mat4x4 matrix){
	coord &= 3;
	float value;
	switch(coord.x){
		case 0: value = matrix[coord.y].x; break;
		case 1: value = matrix[coord.y].y; break;
		case 2: value = matrix[coord.y].z; break;
		case 3: value = matrix[coord.y].w; break;
		default: value = 0.0f;} 
	return value;
}

float duff(int step, mat4x4 block){
	float value = 0.;
	mat4x4 diffblock = block - mat4x4(8.);

	switch(step & 15){
		case 15: value += diffblock[3].w;
		case 14: value += diffblock[3].z;
		case 13: value += diffblock[3].y;
		case 12: value += diffblock[3].x;
		case 11: value += diffblock[2].w;
		case 10: value += diffblock[2].z;
		case  9: value += diffblock[2].y;
		case  8: value += diffblock[2].x;
		case  7: value += diffblock[1].w;
		case  6: value += diffblock[1].z;
		case  5: value += diffblock[1].y;
		case  4: value += diffblock[1].x;
		case  3: value += diffblock[0].w;
		case  2: value += diffblock[0].z;
		case  1: value += diffblock[0].y;
		case  0: value += diffblock[0].x;
	}
	return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 normUV = fragCoord.xy /iResolution.xy;
	ivec4 shifted0 = (ivec4(dataSet.x) >> ivec4(28, 24, 20, 16)) & ivec4(15);
	ivec4 shifted1 = (ivec4(dataSet.x) >> ivec4(12,  8,  4,  0)) & ivec4(15);
	ivec4 shifted2 = (ivec4(dataSet.y) >> ivec4(28, 24, 20, 16)) & ivec4(15);
	ivec4 shifted3 = (ivec4(dataSet.y) >> ivec4(12,  8,  4,  0)) & ivec4(15);

	mat4x4 matthew = mat4x4(shifted0, shifted1, shifted2,  shifted3);

	vec2 timeOffset = vec2(cos(iTime), sin(iTime))/4.;

	ivec2 bigGrid = ivec2((4.* (normUV - timeOffset)) );
	ivec2 smallGrid = ivec2((16.* (normUV + timeOffset) ));
	float value = selectFromMat(bigGrid, matthew);
	float value2 = selectFromMat(smallGrid, matthew);
	float value3 = duff(int(normUV.y * 16.) , matthew);
	float arbitraryCombination = ((value3+12.)/64.) * (value2 + value)/31.;
    float unGamma = pow(arbitraryCombination, 0.45);

	fragColor = vec4( unGamma );
}

vec4 HmdWarpParam = vec4( 1, 0.22, 0.24, 0 );
vec2 Scale = vec2(0.1469278, 0.2350845);
vec2 ScaleIn = vec2(4, 2.5);

vec2 HmdWarp(vec2 texIn, vec2 LensCenter)
{
   vec2 theta = (texIn - LensCenter) * ScaleIn;
   float rSq = theta.x * theta.x + theta.y * theta.y;
   vec2 theta1 = theta * (HmdWarpParam.x + HmdWarpParam.y * rSq + HmdWarpParam.z * rSq * rSq + HmdWarpParam.w * rSq * rSq * rSq);
   return LensCenter + Scale * theta1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uvo = fragCoord.xy / iResolution.xy;
	vec2 uv;
	vec2 ScreenCenter = vec2( 0.25, 0.5 );
	vec2 LensCenter = vec2( 0.2863248, 0.5 );	

	uv = HmdWarp(uvo,LensCenter); //gl_TexCoord[0].xy);
   if (any(notEqual(clamp(uv, ScreenCenter-vec2(0.25,0.5), ScreenCenter+vec2(0.25, 0.5)) - uv, vec2(0.0, 0.0))))
   {
	   ScreenCenter = vec2( 0.75, 0.5 );
	   LensCenter = vec2( 0.7136753, 0.5 );

	   uv = HmdWarp(uvo,LensCenter);

	    if (any(notEqual(clamp(uv, ScreenCenter-vec2(0.25,0.5), ScreenCenter+vec2(0.25, 0.5)) - uv, vec2(0.0, 0.0))))
		   fragColor = vec4(0.0, 0.0, 0.0, 1.0);			
		else
			fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
   }
   else	
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
