#define R                       (iResolution.xy)
#define MR                      min(R.x, R.y)
#define VERTICAL_CHECKERS       22.
#define CHECKERS_DIFF           6.5
// Suggestion from FabriceNeyret2:
#define CHECKER_BORDER	        (1.7 * VERTICAL_CHECKERS / MR)
#define S(B, v)                 smoothstep(B, 0., min(v.x, v.y))
#define BORDER_COLOR            vec3(0.1, 0.1, 0.1)

#define ANGULAR_SPEED 0.2
#define ANGULAR_ADD   0.5

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0,4.0,2.0),
                             6.0) -3.0 ) -1.0,
                     0.0,
                     1.0 );
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

mat2 rot(in float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec4 toCheckers(in vec2 p) {
    // Distort
    p += 0.05 * sin(p *12.);
    // Tiles
    p *= VERTICAL_CHECKERS + CHECKERS_DIFF * sin(iTime);
    p *= rot(iTime * ANGULAR_SPEED + sin(iTime * ANGULAR_ADD));

    return vec4(
        fract(p.x),
        fract(p.y),
        floor(p.x),
        floor(p.y));
}

void mainImage(out vec4 O, in vec2 u) {
    // Normalize to -0.5 ... 0.5 on the shorter axis.
    u = (u - 0.5 * R) / MR;
    vec4 c = toCheckers(u);

    // Simplification of:
    //   float lb = smoothstep(CHECKER_BORDER, 0.0, min(c.x, c.y));
    //   float ur = smoothstep(1.0 - CHECKER_BORDER, 1.0, max(c.x, c.y));
    //   float borderFactor = max(lb, ur);
    // Suggestion from FabriceNeyret2:
    float borderFactor = S(CHECKER_BORDER, min(c.xy, 1. - c.xy));
    // light or dark?
    float isLight = mod(c.z + c.w, 2.);
    // Inner color
    vec3 innerColor = hsb2rgb(vec3(
        mod((c.z + c.w + iTime * 4.)/22., 3.),
        smoothstep(-1.6, 1., sin((c.z + c.w) / 12.1 + iTime * 1.3)),
        .7 + 0.3 * sin(c.z - c.w + iTime * 3.7)));

    O = vec4(
        mix(innerColor, BORDER_COLOR, borderFactor),
        1.0);
}

// volume noise test

vec3 erot(vec3 p, vec3 ax, float ro)
{
    return mix(dot(p,ax)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

float WaveletNoise(vec3 p, float z, float k) {
    // https://www.shadertoy.com/view/wsBfzK
    float d=0.,s=1.,m=0., a;
    for(float i=0.; i<3.; i++) {
        vec3 q = p*s, g=fract(floor(q)*vec3(123.34,233.53,314.15));
    	g += dot(g, g+23.234);
		a = fract(g.x*g.y)*1e3 +z*(mod(g.x+g.y, 2.)-1.); // add vorticity
        q = (fract(q)-.5);
        q = erot(q, normalize(tan(g+.1)), a);
        d += sin(q.x*10.+z)*smoothstep(.25, .0, dot(q,q))/s;
        p = erot(p,normalize(vec3(-1,1,0)),atan(sqrt(2.)))+i; //rotate along the magic angle
        m += 1./s;
        s *= k; 
    }
    return d/m;
}

#define brightness -7.7
#define saturation 0.9

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=fragCoord.xy/iResolution.xy-.5;
	uv.y*=iResolution.y/iResolution.x;
	vec3 dir=vec3(uv*0.8,2.);
	float time=iTime*0.04;

	//rotation
    vec2  mm = iMouse.xy/iResolution.xy;
	float a1=0.5+(mm.x*0.5);
	float a2=0.8+(mm.y*0.5);

	mat2 rot1=mat2(cos(a1),sin(a1),-sin(a1),cos(a1));
	mat2 rot2=mat2(cos(a2),sin(a2),-sin(a2),cos(a2));
	dir.xz*=rot1;
	dir.xy*=rot2;
	vec3 from=vec3(1.,.5,.75);
	from+=vec3(time*1.8,time,-5.);
	from.xz*=rot1;
	from.xy*=rot2;

	// volumetric
	float s=0.1;
    float fade=1.;
	vec3 v=vec3(0.);
	for (int r=0; r<50; r++)
    {
		vec3 p=from+s*dir*.5;
		vec3 p2 = p*4.0;
		float a = WaveletNoise(p2, 0.0, 1.9)*2.0 - 1.0;
		a *= a * a;
		v += vec3(s, s*s, s*s*s*s)* a * brightness * fade;
		fade *= 0.925;
		s += 0.032;
	}
	v=mix(vec3(length(v)),v,saturation);
	fragColor = vec4(v.gbr*.01,1.);	

}

// A Green Leafy thing :)

float wave(vec2 p)
{
  float v = sin(p.x + sin(p.y) + sin(p.y * .43));
  return v*v;
}

float get(vec2 p,float t)
{
  mat2 rot = mat2(0.5, 0.86, -0.86, 0.5);
  float v = wave(p);
  p.y += t;
  p *= rot;
  v += wave(p.yx);
  p.y += t * .17;
  p *= rot;
  v += wave(p.xy);
  v = abs(1.5 - v);
  v+=pow(abs(sin(p.x+v)),18.0);
  return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = (iResolution.xy - 2.0*fragCoord.xy)/iResolution.y;
    float t = iTime;
	float scale =14.0;
	float speed = .3;
	uv.y += sin(fract(t*0.1+uv.x)*6.28)*0.05;	// wibble
	uv.xy += t*0.08;					// scroll
	vec2 p = uv*scale;
	//p.y+= 1.0/p.y*p.y;
  	float v = get(p,t*speed);
  	v = smoothstep(-3.5,3.5,v);
	vec3 col = vec3(.29, 0.86, 0.4);
    fragColor = vec4(col*v*v, 1.0);

}

