float intersect_sphere( vec3 O, vec3 D, vec3 C, float r )
{
	float b = dot( O-=C, D ),
	      h = b*b - dot( O, O ) + r*r;
	return h < 0. ? -1.             // no intersection
	              : -b - sqrt(h);
}

