void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Init registers.
    float reg[32];

    // Load these into registers. Ideally iTime, iResolution would be constants.
    reg[16] = iTime;
    reg[17] = fragCoord.x;
    reg[18] = fragCoord.y;
    reg[19] = iResolution.x;
    reg[20] = iResolution.y;

    // Execute instructions.
    for (int i = 0; i < instructions.length(); i += 4) {
        int dst = instructions[i+0];
        int inst = instructions[i+1];
        int src0 = instructions[i+2];
        int src1 = instructions[i+3];

        float a = (src0 < 32) ? reg[src0] : constants[src0-32];
        float b = (src1 < 32) ? reg[src1] : constants[src1-32];

        if (inst == MOV) 		reg[dst] = a;
        else if (inst == ADD)   reg[dst] = a + b;
        else if (inst == SUB)   reg[dst] = a - b;
        else if (inst == MUL)   reg[dst] = a * b;
        else if (inst == DIV)   reg[dst] = a / b;
        else if (inst == SIN)   reg[dst] = sin(a);
        else if (inst == COS)   reg[dst] = cos(a);
        else if (inst == SQR)   reg[dst] = sqrt(a);
        else if (inst == RSQ)   reg[dst] = 1.0/sqrt(a);
        else if (inst == PCK)   reg[dst] = a * 0.5 + 0.5;
    }

    // Output to screen.
    fragColor = vec4(reg[0], reg[1], reg[2], reg[3]);
}

