// This is the starting point for a YouTube tutorial:
// https://youtu.be/Vmb7VGBVZJA


#define MAX_STEPS 50
#define MAX_DIST 12.
#define SURF_DIST .25

// Untouched from tutoial
mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}


// Used to be math for a cube
// Now its just a mess of distance based warping
float sdBox(vec3 p, vec3 s) {
    p = min( (p)-s, (p)+s);
	return length(p)+(p.x+p.y);
}


// Slice'n'Dice!
// Duel plane cutting a botched up box with warped planes and distance in space
float GetDist(vec3 p, vec3 ro) {
	float time = iTime*.3;
    vec3 pd = p;
    pd.x = abs(p.x) + tan(p.z*.2) * cos(-p.y*.3);
    pd.y *= pd.x<0.0 ? -2.0 : 1.1;
    pd.z = mix( pd.z, pd.x, sin(p.z)*.15-.1 );

    float scalar = sin( dot(normalize(ro-pd),pd)*.1) * (pd.x+pd.z)*.4;
    pd.z = mix( -pd.z, pd.y+pd.x, abs(scalar) );


    vec2 tUv = vec2( sin(pd.x*.01+scalar*.1), cos(pd.z*.1+scalar*.01)) *.1+.5;
    vec3 tex = texture(iChannel0, tUv ).rgb;

    vec3 slicer = normalize( vec3(sin(time+pd.z-pd.x+1.2)*.50+.5, -0.71, cos(time+pd.z+pd.y)*.10+.2) );
	slicer -= texture(iChannel1, slicer.rg*.01).rgb;
    float plane = dot(pd, slicer); // Dot Plane 1


    slicer = vec3( sin(-time*1.5+pd.z+tex.g+5.1)*2.0, .90, cos(time+pd.y+tex.g)*2.0 );

    float pa = dot( pd*vec3(1.27,.07,.95), slicer ); // Dot Plane 2
    plane = plane*abs(pa); // Combine both cutting planes

    float box = sdBox(pd+vec3(.0,.15,.0), vec3(.7)); // Warped Box Math 1

    slicer = vec3( sin(-time*1.5+pd.x+tex.g), .0, cos(time+pd.y+tex.g)*2.0 );
    float boxf = sdBox( pd*vec3(-1,1,-1)-vec3(0.0,.2,1.0), vec3(-slicer*0.5+.5) ); // Warped Box Math 2
    box = box+abs(boxf)-0.10; // Combine both boxes

    float d = max( plane, box) * min(1.0,length(pd));

    return d;
}

// Minor tweeks
vec4 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float dS=0.;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        dS = GetDist(p, ro);
        dO += dO>MAX_DIST || dS<SURF_DIST ? 0.00 : dS;
    }

    return vec4( ro + rd*dO,dO); // ##
}

vec3 GetNormal(vec3 p, vec3 ro, float dO) {
	float d = GetDist(p, ro);
    vec2 e = vec2(.005, 0); // ##

    vec3 n = d - vec3(
        GetDist(p-e.xyy, ro),
        GetDist(p-e.yxy, ro),
        GetDist(p-e.yyx, ro));

    return normalize(n);
}


// Minor tweeks
vec4 GetLight(vec3 p, vec3 n) {
    vec3 lightPos = vec3(3.0, 5, 4);
    vec3 l = normalize(lightPos-p);

    float dif = dot(n, l)*.5+.5;

    return vec4(n,dif); // #
}

// Untouched from tutoial
vec3 rayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 0, 3.4);

    float ds = length(uv)*.1+1.0;
    vec3 rd = rayDir(uv, ro, vec3(0,-.37,0), ds);

    vec4 d = RayMarch(ro, rd); // vec3(rayMarchEnd), float lastStepSize
	float bgMask = step(d.w,MAX_DIST);

    vec3 p = d.xyz;

    vec3 n = GetNormal(p, ro, d.w);
	float dotEdge = dot( p.xyz ,n.xyz)* bgMask ; // Edge fall off
    vec4 dif = GetLight(p, n); // Lighting dir vector

	// Get some warping from surface bend direction to apply as an offset to the xyz texture reads
	vec3 texWarp=cross( normalize(ro-d.xyz), n.xyz )*dotEdge*.5+.5;


	// Triplanar, it ainy pretty, but it works ... sorta
    vec3 tx = texture(iChannel0, p.yz*.1+texWarp.yz).rgb;
    vec3 ty = texture(iChannel0, p.xz*.1+texWarp.xz).rgb;
    vec3 tz = texture(iChannel0, p.xy*.1+texWarp.xy).rgb;
    vec3 td = abs( vec3( dot(vec3(1,0,0),n), dot(vec3(0,1,0),n), dot(vec3(0,0,1),n) ) );
	vec3 tdInv = vec3( 1.0-td.x, 1.0-td.y, 1.0-td.z );
	// Scale by other axes prominance
    vec3 tm = vec3(
			td.x * (tdInv.y * tdInv.z),
			td.y * (tdInv.x * tdInv.z),
			td.z * (tdInv.x * tdInv.y));
    vec3 tex = tx*tm.x + ty*tm.y + tz*tm.z; // Combine the triplanar colors

    // Boost the color of the center mass
	vec3 tf = texture(iChannel2, uv*.1+d.zz*.1+texWarp.xy*.1).rgb;

    float dscale = max(0.0,(d.w-3.0)*.02); // Boost green/blue bg colored bits
	tex += cross(vec3(.1,1,0), p.xyz)*(1.0+dotEdge*dscale);
	float bBrown=max(0.0, 1.0-max(0.0, d.w-2.5)); // Boost Brown;
    tex += tf*(1.0+bBrown);

    col = vec3(tex*dif.w* bgMask * (1.0-dotEdge*dscale) );

    fragColor = vec4(col,1.0);
}

float line(float top, float bottom, float sharpen, vec2 uv) {
    sharpen = 0.05 * smoothstep(0.6, 1., uv.x) + 0.05 * smoothstep(0.3, 0., uv.x);
    return smoothstep(top, top + sharpen, uv.y) - smoothstep(bottom, bottom + sharpen, uv.y);
}

float wave(float time, vec2 uv, float phase) {
    float wave = sin(time + uv.x * phase);
    float blur = 0.1 * smoothstep(0., .5, uv.x) * 0.1 * smoothstep(1., 0.5, uv.x);
    uv.y += phase * blur * wave;
    return line(0.495, 0.505, 0.01, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float audio = 1.; texture(iChannel0, vec2(uv * 0.1)).x;

    float line = wave(iTime * 3., uv, 10. + sin(iTime) * 1.5);
    float line2 = wave(iTime * 4., uv, 13. + sin(iTime + 2.) * 3.);
    float line3 = wave(iTime *  5., uv, 20. + sin(iTime + 3.) * 3.);

    vec3 col1 = vec3(1.0, 0.1, 0.2) * line;
    vec3 col2 = vec3(0.8, 0.4, 0.2) * line2;
    vec3 col3 = vec3(0.3, 0.2, 0.9) * line3;
    vec3 col = 4. * (col1 * audio + col2 * audio + col3 * audio);

    fragColor = vec4(col,1.0);
}

// code primarily from https://www.shadertoy.com/view/XsyXWV
// Author: Chris Camargo


// First Raymarch - Cubes
// Chris Camargo, June 2016

// Distance function for box.
float box(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;

    return min(max(d.x,max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

// from https://www.shadertoy.com/view/Xds3zN
// THE Inigo Quilez
float sdBoundingBox( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;

  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// from https://www.shadertoy.com/view/Xds3zN
// THE Inigo Quilez
float sdEllipsoid( vec3 p, vec3 r ) // approximated
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

// Repeater distance function for box.
float rep(vec3 p, vec3 c)
{
    vec3 q = mod(p, c) - 0.5 * c;
    return box(q, vec3(0.5, 0.5, 0.5));
}

// Raymarch, computing the distance at each step and incrementing the
// ray parameter t.
float trace(vec3 o, vec3 r)
{
    float t = 0.0;
    for (int i = 0; i < 32; ++i) {
        vec3 p = o + r * t;

        // Distance function.
        float d = rep(p, vec3(2.0, 3.0, 2.0));

        t += d;
    }
    return t;
}

// Image computation.
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float x = fragCoord.x;            // Current pixel x-coordinate. Range: [0.5, iResolution.x - 0.5].
    float y = fragCoord.y;            // Current pixel y-coordinate. Range: [0.5, iResolution.y - 0.5].
    float minX = 0.5;                 // Min x-coordinate value.
    float minY = 0.5;                 // Min y-coordinate value.
    float maxX = iResolution.x - 0.5; // Viewport width.
    float maxY = iResolution.y - 0.5; // Viewport height.

    // Feature Scaling. Bring coords to value between [0, 1].
    // https://en.wikipedia.org/wiki/Feature_scaling#Rescaling
    vec2 uv = vec2((x - minX) / (maxX - minX), (y - minY) / (maxY - minY));

    // Coords between [0, 1] so 'eye' is centered at (0.5, 0.5).
    // We want 'eye' centered at (0, 0). Thus subtract 0.5 from
    // each coord.
    uv -= 0.5;

    // Move coords away from 'eye' by a factor of 2.0.
    // You can play with this number.
    uv *= 2.0;

    // Scale x based on aspect ratio.
    uv.x *= iResolution.x / iResolution.y;

    // translate in x
    float xtranslate = cos(iTime) * 2.0;
    const float positionK = 0.5;
    const float parallaxK = 0.5;
    // origin is a-function-of-translation
    // Ray origin.
    vec3 o = vec3(xtranslate * positionK, 0.0, 0.0);

    // view direction scales linearly with the opposite of translation
    // Ray direction.
    vec3 r = normalize(vec3(uv.x - xtranslate * parallaxK, uv.y, 1.0));

    r = normalize(r);

    // Rotate direction r around y-axis by a fraction of time.
    // float theta = iTime * 0.25;
    // r.xz *= mat2(cos(theta), -sin(theta), sin(theta), cos(theta));

    // Trace ray from origin o in direction r.
    float t = trace(o, r);
    float t2 = trace(o + vec3(0.01, 0.0, 0.0), r - vec3(0.01, 0.0, 0.0));

    // compute marginal derivative. $t is distance
    float dtdx = (t - t2) / 0.001;

    // dampen the force field glows
    float g = 0.05 * dtdx;

    // Set the color via 1 / (1. + t): Light attenuation.
    // lerp in an occasional dark tint to gb values
    float forceFieldMode = cos(iTime * 2.0) * 0.1 + 0.9;
    vec3 color = vec3(0.5 * (1.0 - forceFieldMode), +0.01 * g + forceFieldMode / (1. + t), -0.01 * g + forceFieldMode / (1. + t));

    // Set pixel color.
    fragColor =  vec4(color, 1.0);
}

#define PI acos(-1.)
#define TAU PI*2.

vec3 lookat(vec3 p, vec3 eye, vec3 target, vec3 up)
{
	vec3 w=normalize(target-eye), u=normalize(cross(w,up));
	return vec3(dot(p,u), dot(p,cross(u,w)), dot(p,w));
}

vec2 rotate(vec2 p, float a)
{
    return p*cos(a)+vec2(-p.y, p.x)*sin(a);
}

vec3 func(vec3 p, vec3 nor, float r, float t)
{
    t*=TAU;
    vec2 uv=vec2(cos(t),sin(t))*r*(1.+sin(t*6.)*.15);
    vec3 u=normalize(cross(nor,vec3(0,1,0)));
    vec3 v=normalize(cross(nor,u));
    return uv.x*u+uv.y*v+p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord*2.-iResolution.xy)/iResolution.y;
    vec3 ro=vec3(0,0,10);
    ro.xz=rotate(ro.xz, iTime);
    vec3 ta=vec3(.1,.2,.1);
    vec3 rd=lookat(normalize(vec3(uv,2)),ro,ta,vec3(0,1,0));

    float R=.5;

    vec3 nor;
 	float ITR=150.,pitch=1.,t=.5,de=R+1.,z=1e10;   
    for(int j=0;j<2;j++)
    {
        float t0=t-pitch*.5;
        pitch/=ITR;
        for(float i=0.;i<ITR;i++)
        {
            t0+=pitch;
            vec3 p=func(vec3(.2,.1,.1),normalize(vec3(3,2,-2)),3.,t0);
            float de0=length(cross(p-ro,rd));
            if(de0<=R){
                de=de0;
                float z0=dot(p-ro,rd)-sqrt(R*R-de*de);
                if(z>z0){
                    z=z0;
                    nor=normalize(ro+rd*z-p);
                    t=t0;
                }

            }
        }
    }
    if(de<=R)
    {
		fragColor+=max(dot(nor,normalize(vec3(1))),.2);
    }
}

// https://www.youtube.com/watch?v=l-07BXzNdPw&t=129s

vec2 N22(vec2 p){
    vec3 a = fract(p.xyx * vec3(123.34,234.34,345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    uv.x *= iResolution.x/iResolution.y;

    float m = 0.;
    float minDist = 100.;
    float cellIndex = 0.;

    for(float i=0.;i<200.;i++){
        vec2 nv = N22(vec2(i));
        vec2 p = sin(nv * iTime);
        float d = length(uv -p);
        m+= smoothstep(0.12, 0.119, d);
        if(d < minDist){
            minDist = d;
            cellIndex = i;
        }
    }

    vec3 col = vec3(cellIndex/100., cellIndex/120., cellIndex/170.);
    // Output to screen
    fragColor = vec4(col ,1.0);
}

mat2 rot(float a){return mat2(cos(a),-sin(a),sin(a),cos(a));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
        t = iTime+float(c)/10.;
        for(int i=0;i<1;i++){
            uv=abs(uv);
            uv-=.5;
            uv=uv*rot(t/float(i+1));
        }
	    col[c]=fract(uv.x*2.),fract(uv.x*2.),fract(uv.x*2.);
    }

