#define r(A)     mat2( C=cos(A), S=sin(A), -S, C )           //
//#define r(a)   mat2( cos( a + vec4(0,11,33,0) ) )          // bug: https://www.shadertoy.com/view/ttGyzh
#define w(c,a) ( R.xy = r(a) * c.xz + iTime,  abs( cos( R.y + cos( R.x ) ) ))  //
#define W(c)   ( w(c,9.) + w(c,7.) + w(c,h) )                //
#define D(c)     W(h*c) + W(c)*.1 + 9. + c.y

void mainImage( out vec4 o, vec2 u ) {
    float h = .3, f = h, C,S; 
    vec3 m = u.xyy/iResolution.x - .5, c=m-m, R;
    m.z -= .4; m.xz *= r(iTime*h);

    for( m /= length(m) ; 
         f++ < 30. && (C=D((c+h))) > .1 ; 
         c += m * C );

    o = min((D(c)+h)*9., 0.) * min(m.x+.6, 0.) 
      + .1* vec4( C= c.y+12. , 2.+C , 4 , 1 )
      + f/50. - length(c)/7e2 ;
}

vec3 hsv2rgb(float h, float s, float v) {
	vec3 a = fract(h + vec3(0.0, 2.0, 1.0)/3.0) * 6.0 - 3.0;
	a = clamp(abs(a) - 1.0, 0.0, 1.0) - 1.0;
	a = a*s + 1.0;
	return a*v;
}

mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c,s,-s,c);
}

vec3 onRep(vec3 p, float interval) {
	return mod(p, interval) - interval * 0.5;
}

vec3 rotate(vec3 p, float angle, vec3 axis) {
	vec3 a = normalize(axis);
	float s = sin(angle);
	float c = cos(angle);
	float r = 1.0 - c;
	mat3 m = mat3(
		a.x * a.x * r + c,
		a.y * a.x * r + a.z * s,
		a.z * a.x * r - a.y * s,
		a.x * a.y * r - a.z * s,
		a.y * a.y * r + c,
		a.z * a.y * r + a.x * s,
		a.x * a.z * r + a.y * s,
		a.y * a.z * r - a.x * s,
		a.z * a.z * r + c
	);
	return m * p;
}

float box(vec3 p, float s) {
    p = abs(p) - s;
    return max(max(p.x, p.y), p.z);
}

float plane(vec3 p) {
    float d = p.y;
    return d;
}

float sdFloor(vec3 p) {
    return dot(p, vec3(0., 1., 0.)) + 0.28;
}

float boxZ(vec3 p, float speed, float size, float repx, float repz) {
    p.z -= (iTime - 1.) * speed;
    p.z = onRep(p, repz).z;
    p.x = onRep(p, repx).x;

    p.y += 0.2 - size;

    p = rotate(p, iTime * speed, vec3(-0.5,0.0,0.0));
    return box(p, size);
}

float boxX(vec3 p, float speed, float size, float repx, float repz) {
    p.x -= iTime * speed;
    p.x = onRep(p, repx).x;
    p.z = onRep(p, repz).z;
    p.y += 0.2 - size;

    p = rotate(p, iTime * speed, vec3(0.0,0.0,0.5)); 
    return box(p, size);
}

float myBoxZ1(vec3 p) {
    return boxZ(vec3(p.x + .5, p.y, p.z), 5., 0.1, 5., 2.);
}
float myBoxZ2(vec3 p) {
    return boxZ(vec3(p.x+1.5, p.y, p.z), 4., 0.2, 5., 2.);
}
float myBoxZ3(vec3 p) {
    return boxZ(vec3(p.x+2.5, p.y, p.z), 3., 0.25, 5., 2.);
}
float myBoxZ4(vec3 p) {
    return boxZ(vec3(p.x+3.5, p.y, p.z), 2., 0.3, 5., 2.);
}
float myBoxZ5(vec3 p) {
    return boxZ(vec3(p.x+4.5, p.y, p.z), 1.8, 0.4, 5., 2.);
}
float myBox4(vec3 p) {
    return boxX(vec3(p.x, p.y, p.z), 5., 0.1, 2., 8.);
}
float myBox5(vec3 p) {
    return boxX(vec3(p.x, p.y, p.z-2.), 4., 0.2, 2., 8.);
}
float myBox6(vec3 p) {
    return boxX(vec3(p.x, p.y, p.z-4.), 3., 0.25, 2., 8.);
}
float myBox7(vec3 p) {
    return boxX(vec3(p.x, p.y, p.z-6.), 2., 0.3, 2., 8.);
}

float sceneDist(vec3 p) {
    float ret = sdFloor(p);
    // z
    ret = min(ret, myBoxZ1(p));
    ret = min(ret, myBoxZ2(p));
    ret = min(ret, myBoxZ3(p));
    ret = min(ret, myBoxZ4(p));
    ret = min(ret, myBoxZ5(p));

    return ret;
}

const float EPS = 0.0001;
vec3 getNormal(vec3 p) {
	return normalize(vec3(
		sceneDist(p + vec3(EPS,0.,0.)) - sceneDist(p + vec3(-EPS,0.,0.)),
		sceneDist(p + vec3(0.,EPS,0.)) - sceneDist(p + vec3(0.,-EPS,0.)),
		sceneDist(p + vec3(0.,0.,EPS)) - sceneDist(p + vec3(0.,0.,-EPS))
	));
}

const vec3 lightDir = vec3(-0.577, 0.577, 0.577);

mat3 camera(vec3 ro, vec3 ta) {
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(0.0, 1.0, 0.0);
    vec3 cu = cross(cw, cp);
    vec3 cv = cross(cu, cw);
    return mat3(cu, cv, cw);
}

float genShadow(vec3 ro, vec3 rd) {
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadowCoef = 0.5;
    for (int i = 0; i<20; i++) {
        h = sceneDist(ro + rd * c);
        if(h < 0.001) {
            return shadowCoef;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }

    return 1.0 - shadowCoef + r * shadowCoef;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float screenZ = 1.5;
    vec3 rd = normalize(vec3(uv, screenZ));

    // Camera
    vec3 ta = vec3(-1.5, -0.5, -1.);
    vec3 ro = vec3(1., 2.5, 2.);
    if (mod(iTime, 50.) < 10.) {
        ro *= mod(iTime, 50.);
    } else if (mod(iTime, 50.) < 20.) {
        ro = vec3(-1., 0.5, 1.5);
        ro.x *= mod(iTime, 50.) - 9.;
        ro.y *= mod(iTime, 50.) - 10.;
    } else if (mod(iTime, 50.) < 30.) {
        ro = vec3(0.1, 5., 0.5);
        ro.x += 2.;
        ro.zy *= mod(iTime, 50.) - 18.;
        ro.xy /= 2.;
    } else if (mod(iTime, 50.) < 40.) {
        ro = vec3(2., 0.5, 2.5);
        ro.xy *= mod(iTime, 50.) - 30.;
    } else {
        ro = vec3(-5., 0.5, 2.5);
        ro.z *= mod(iTime, 50.) - 40.;
    }
    rd = camera(ro, ta) * normalize(vec3(uv, screenZ));

    float d = 0.0;
    vec3 col = vec3(0.);

    vec3 light = normalize(lightDir + vec3(0., 2., 1.));
    float shadow = 1.0;

    for(int i=0; i<99; i++) {
        vec3 rp = ro + rd * d;
        float dist = sceneDist(rp);

        if (abs(dist) < 0.0001) {
            vec3 normal = getNormal(rp);

            // light
            vec3 halfLE = normalize(light - rd);
            float diff = clamp(dot(light, normal), 0.1, 1.0);
            float spec = pow(clamp(dot(halfLE, normal), 0.0, 1.0), 50.0);

            shadow = genShadow(rp + normal * 0.001, light);

            float u = 1.0 - floor(mod(rp.x, 2.0));
            float v = 1.0 - floor(mod(rp.z, 2.0));

            if((u == 1.0 && v < 1.0) || (u < 1.0 && v == 1.0)) {
                diff *= 0.7;
            }

            col = vec3(0.89);
            col = col * diff + vec3(spec);

            if(myBoxZ1(rp) < 0.001) {
                col.rgb -= mod(iTime, 1.) * 0.5;
            }
            else if(myBoxZ2(rp) < 0.001) {
                col.r += 0.5;
            }
            else if(myBoxZ3(rp) < 0.001) {
                col.g += 0.5;
            }
            else if(myBoxZ4(rp) < 0.001) {
                col.b += 0.5;
            }
            else if(myBoxZ5(rp) < 0.001) {
                col.rgb += 0.5;
            }

            break;
        }

        d += dist;
    }

    fragColor = vec4(col * max(0.5, shadow), 1.);
}

vec2 brickTile(vec2 pos, float zoom){
    pos *= zoom;

    float time = fract(iTime * .5);
    float col = step(1., mod(pos.x + (1.0 - step(.75, time)) * .5, 2.0));
    float row = step(1., mod(pos.y - step(.5, time) * .5, 2.0));

    pos.x += (clamp(time, .0, .25) + clamp(time - .5, .0, .25)) * (row - .5) * 4.;
    pos.y += (clamp((time - .25), .0, .25) + clamp(time - .75, .0, .25)) * (col - .5) * 4.;

    return fract(pos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){    
    vec2 pos = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    pos = brickTile(pos, 6.);

    pos = pos - vec2(.5);
    float angle = atan(pos.x, pos.y);
    angle += iTime * 1.;
    float r = length(pos);

    vec3 color = vec3(smoothstep(cos(angle*3.),cos(angle*3.)+.1,r*3. ));
    fragColor = vec4(color,1.0);
}

