	float y_proj = floor((-v.x + v.y) / 4.0 + 0.5*(float(dir) + 1.0));


    vec2 coord_back = x_proj * vec2(1.0, 1.0)*2.0 + y_proj * vec2(-1.0, 1.0)*2.0;
    vec2 half_point = coord_back;

    float index = mod((x_proj + y_proj + 4096.0 + float(dir)), 2.0);
    float direction = 1.0 - 2.0 * index;
    vec2 offset = v - half_point;
    vec2 rotated = half_point + vec2(offset.x + offset.y * direction, offset.y - offset.x * direction);

    vec2 new_coord = vec2(floor(rotated.x + rotated.y)/2.0, floor(rotated.y - rotated.x) / 2.0);


    return new_coord;
}


