vec4 galaxy(vec2 dir){
	vec2 pos = vec2(dir.x,dir.y-0.5);
	float t =  iTime*0.1;
	float si = sin(t);
	float co = cos(t);
	mat2 ma = mat2(co, si, -si, co);


    float v1, v2, v3;
	v1 = v2 = v3 = 0.0;

	float s = 1.0;
	for (int i = 0; i < 90; i++)
	{
		vec3 p = s * vec3(pos, 1.0);
		p.xy *= ma;
		p += vec3(.22, .3, s - 1.5 - sin( .13) * .1);
		for (int i = 0; i < 8; i++)	p = abs(p) / dot(p,p) -0.665;
		v1 += dot(p,p) * .0015 * (1.8 + sin(length(pos.xy ) + .5  ));
		v2 += dot(p,p) * .0013 * (1.5 + sin(length(pos.xy) + 1.2 ));
		v3 += length(p.xy*10.) * .0003;
		s  += 0.020;
	}
    float len = length(pos);
	v1 *= smoothstep(.7, .0, len);
	v2 *= smoothstep(.5, .0, len);
	v3 *= smoothstep(.9, .0, len);

	vec3 col = vec3( v3 * (1.5 + sin(iTime * .2) * .4),
					(v1 + v3) * .3,
					 v2) + smoothstep(0.2, .0, len) * .85 + smoothstep(.0, .6, v3) * .3;

	return vec4(min(pow(abs(col), vec3(1.2)), 1.0), 1.0) ;


}

