vec3 intersect_plane(vec3 origin, vec3 ray){

    if(ray.y == 0.0){
        return vec3(0.0,0.0,1.0);
    }
    float a = -origin.y/ray.y;
    if(a <= 0.0){
        return vec3(0.0,1.0,1.0);
    }
    ivec2 grid_coord = ivec2(floor((origin + a*ray).xz));
    int grid = abs(grid_coord.x % 2) ^ abs(grid_coord.y % 2);
    return (grid != 0) ? vec3(0.0, min(10.0/a, 1.0), 0.0) : vec3(0.0, 0.0, 0.0);
}

vec3 intersect_sphere(vec3 origin, vec3 ray){

    ray = normalize(ray);
    //vec3 center = vec3(cos(iTime), cos(0.5*iTime) + 2.0, sin(iTime) + 3.0);
    vec3 center = vec3(4.0*(iMouse.xy / iResolution.xy - vec2(0.5)) + vec2(0.0, 1.0), 2.0*iTime + 2.5);
    //float radius = 0.5*cos(iTime) + 0.6;
    float radius = 1.0;
    center.y = max(center.y, radius);

    float a = 1.0;
    float b = 2.0 * dot(ray, origin - center);
    float c = dot(origin - center, origin - center) - radius*radius;
    float disc = b*b - 4.0*a*c;
    if(disc <= 0.0){
        return intersect_plane(origin, ray);
    }
    float s = (-b - sqrt(disc))/(2.0*a);
    if(s <= 0.0){
        s = (-b + sqrt(disc))/(2.0*a);
        if(s <= 0.0){
            return intersect_plane(origin, ray);
        }
    }
    vec3 intersection = origin + s*ray;
    vec3 normal = normalize(intersection - center);
    vec3 ray2 = 2.0*dot(normal, -ray)*normal + ray;
    return 0.6*intersect_plane(intersection, ray2);
}

