	vec3 origin = vec3(0.0, 1.5, 2.0*iTime);

    vec2 pixeldim = vec2(1.0/iResolution.x, 1.0/iResolution.y);
    vec3 ray_curr = ray;
    vec3 col = vec3(0.0);
    for(int i = 0; i < NUM_SAMPLES; i++){
        for(int j = 0; j < NUM_SAMPLES; j++){

        	col += intersect_sphere(origin, ray_curr);
            ray_curr += vec3(pixeldim.x/float(NUM_SAMPLES), 0.0, 0.0);
