// This is a simple shader to address a simple message to my American friends.
// Make yourself and the world a favor, go vote, fight authoritarianism, claim your country back.
// https://www.usa.gov/how-to-vote


#define S(a, b, t) smoothstep(a, b, t)
#define sat(a) clamp(a, 0.0, 1.0)

float star(vec2 uv)
{
    float aa = iResolution.x * 0.03;

    uv.x = -abs(uv.x);

    vec2 n  = normalize(vec2(1.0, -0.35));
    vec2 n2 = normalize(vec2(-0.7, 1.0));

    float dot1 = sat(dot(uv - vec2(0.0, 1.0), n) * aa);

    float dot2 = sat(dot(uv + vec2(0.0, 0.351), n2) * aa);


    float star1 = dot1 * dot2 ;

    float dot3 = sat((0.32 - uv.y) * aa);

    uv.x = -uv.x;
	float dot4 = sat(dot(uv  + vec2(0.0, 0.351), n2) * aa);


    float star2 = dot3 * dot4 ;

    return sat(star1 + star2);
}


vec3 flag(vec2 uv)
{
    float stripe;

    float aa = 10.0 / iResolution.x;

    stripe = S(0.25 -aa, 0.25 + aa, abs(fract(uv.y * 6.5 + 0.25) - 0.5));

    vec2 boxUV = vec2(uv.x * 2.2, (1.0 - uv.y) * (13.0 / 7.0));

    float aa2 = aa * 0.2;
    float square = S(1.0 + aa2, 1.0 - aa2, boxUV.x);
    square *= S(1.0 + aa2, 1.0 - aa2, boxUV.y);

    vec2 starsTile =  boxUV * vec2(6.0, 5.0);

    vec2 starsUv = (fract(starsTile) - vec2(0.5)) * vec2(4.0, 3.0);

    starsUv.y = -starsUv.y;

    float stars = star(starsUv);


    starsTile -= vec2(0.5);
    starsTile.x = clamp(starsTile.x, 0.0, 5.0);
    starsTile.y = clamp(starsTile.y, 0.0, 4.0);

    vec2 starsUv2 = (fract(starsTile) - vec2(0.5)) * vec2(4.0, 3.0);
    starsUv2.y = -starsUv2.y;

    stars += star(starsUv2);

    vec3 starsCol = mix(vec3(0.2, 0.1, 0.6), vec3(1.0), stars);

    vec3 col = mix(vec3(0.8, 0.1, 0.2), vec3(1.0), stripe);
    col = mix(col, starsCol, square);

    return vec3(col);
}



float sdCapsule( vec2 p, vec2 a, vec2 b, float r )
{
  vec2 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}


float voteDst(vec2 uv)
{


    float r = 0.5;
    float dst = sdCapsule(uv, vec2(0.0,3.0), vec2(1.5,0.0), r);
    dst = min(dst, sdCapsule(uv, vec2(3.0,3.0), vec2(1.5,0.0), r));


    dst = min(dst, sdCapsule(uv, vec2(8.0,3.0), vec2(11.0,3.0), r));
    dst = min(dst, sdCapsule(uv, vec2(9.5,3.0), vec2(9.5,0.0), r));

    dst = min(dst, sdCapsule(uv, vec2(12.5,0.0), vec2(12.5,3.0), r));

    dst = min(dst, sdCapsule(uv, vec2(12.5,3.0), vec2(14.5,3.0), r));
    dst = min(dst, sdCapsule(uv, vec2(12.5,0.0), vec2(14.5,0.0), r));
    dst = min(dst, sdCapsule(uv, vec2(12.5,1.5), vec2(13.5,1.5), r));

    dst = min(dst, abs(length(uv - vec2(5.5, 1.5)) - 1.5) - 0.5);

    return dst;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;


    float yUv = uv.y + 0.5;


    vec2 flagsUv = uv * vec2(0.3, 0.5) * 1.3 + vec2(0.5);

    float t = iTime * 0.3;
    vec2 wave = vec2(sin(uv.y * 1.3 + t * 0.5) * 0.5, sin(uv.x * 4.0 + t * 4.0));

    wave += vec2(sin(uv.y * 4.3 + t * 3.6) * 0.25, sin(uv.x * 3.2 + t * 6.2)* 0.5);

    //wave = vec2(sin(uv.y + iTime), sin(uv.x + iTime)) * 0.1;


    float fakeShading = mix(0.6, 1.3, S(1.0, -1.0, dot(wave, vec2(-0.7, 0.5))));


    flagsUv += wave * 0.02;

    vec2 flagMaskXY = (vec2(0.5) - abs(flagsUv - vec2(0.5))) * 200.0;

    float flagMask = sat(flagMaskXY.x) * sat(flagMaskXY.y);

    vec3 flagCol = flag(flagsUv) * fakeShading * vec3(0.9, 0.9, 1.1);

    vec3 bg = mix(vec3(0.3, 0.4, 1.0),  vec3(0.0, 0.0, 1.0),  yUv);

    bg.rgb += (uv.y * uv.y + uv.x * 0.1) * 0.6;

    bg.rgb *= S(2.5, 1.0, length(uv * vec2(1.0, 1.5))) * 0.6 + 0.4;



    // Time varying pixel color
    //vec3 col = vec3(star(uv.xy));

    //vec3 col = vec3(flag(uv.xy));

    vec3 col = mix(bg, flagCol, flagMask);

    float vote = voteDst(uv * 5.0 + vec2(7.25, 1.5));


    col = mix(vec3(0.0), col, S(-1.2, 1.0, vote));


    float aa = 15.0 / iResolution.x;

    float outline = 0.2;
    col = mix(vec3(0.0), col, S(outline -aa, outline +aa, vote));
    col = mix(vec3(1.0), col, S(-aa, aa, vote));


    col.rgb = pow(col.rgb, vec3(0.7));

    // Output to screen
    fragColor = vec4(col,1.0);

    //fragColor.rgb = vec3(vote(uv));
}

