//Thomas M.
const int testIters = 512;
const vec2 zoomVec = vec2(0.348,0.5);

//Taken from: https://www.chilliant.com/rgb2hsv.html
vec3 hueToRgb(float H)
{
    float R = abs(H * 6. - 3.) - 1.;
    float G = 2. - abs(H * 6. - 2.);
    float B = 2. - abs(H * 6. - 4.);
    return clamp(vec3(R,G,B), 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;
    uv = uv*2.-1.;
    uv.x -= 1.5;

    //Zoom in
    uv /= exp(iTime);
    uv += zoomVec;

    //Z is a complex number and must be treated as such and not as a standard vector
    vec2 z = vec2(0.);
    float outp = 0.;
    for(int i = 0; i < testIters; i++)
    {
        //z = z^2 + uv
        float zxPrev = z.x;
        z.x = z.x*z.x - z.y*z.y;
        z.x += uv.x;
        z.y = 2.*z.y*zxPrev;
        z.y += uv.y;

        if(length(z) <= 2.0)
            outp += 1./float(testIters);
    }

    vec3 col = hueToRgb(fract(outp));

    /*if(length(z) > 2.0)
        col = vec3(0.);
    else
        col = vec3(1.);*/

    // Output to screen
    fragColor = vec4(col,1.0);
}

