//https://en.wikipedia.org/wiki/Spidron#/media/File:Spidron_Hexagon.JPG

//No fun constants to play with, sorry :(
#define PI 3.14159
#define START_CIRCUMRADIUS 3.0

#define LINE_WIDTH 0.005

#define RAD(deg) (deg*PI/180.0)

#define TIME_CONST 1.5

//Magic numbers for identifying points on/in triangles
#define ON_TRIANGLE 123
#define NOT_IN_TRIANGLE -1

float THIRTY = RAD(30.0);
float SIXTY = RAD(60.0);

//Find closest point on a line segment, given a point p3.
//Then compute whether it is "on" the line by computing
//distance from the segment.
//Thank you Paul Bourke http://paulbourke.net/geometry/pointlineplane/
bool on_segment(vec2 p1, vec2 p2, vec2 p3, float thickness){
    float u = ((p3.x-p1.x)*(p2.x-p1.x) +
               	(p3.y-p1.y)*(p2.y-p1.y)) /
        		pow(length(vec2(p2.x-p1.x, p2.y-p1.y)),2.0);

    //Check within bounds of segment
    if(u<0.0||u>1.0)
        return false;

    vec2 point_on_line = vec2(
        p1.x + u*(p2.x-p1.x),
        p1.y + u*(p2.y-p1.y)
    );
	return distance(point_on_line, p3)<=thickness;
}

//Check if uv inside triangle using barycentric coords
//https://en.wikipedia.org/wiki/Barycentric_coordinate_system#Conversion_between_barycentric_and_Cartesian_coordinates
bool in_triangle(vec2 p1, vec2 p2, vec2 p3, vec2 uv){

    //Delicious math
    float l1 =
        ((p2.y-p3.y)*(uv.x-p3.x)+(p3.x-p2.x)*(uv.y-p3.y))/
        ((p2.y-p3.y)*(p1.x-p3.x)+(p3.x-p2.x)*(p1.y-p3.y));

    float l2 = 
        ((p3.y-p1.y)*(uv.x-p3.x)+(p1.x-p3.x)*(uv.y-p3.y))/
        ((p2.y-p3.y)*(p1.x-p3.x)+(p3.x-p2.x)*(p1.y-p3.y));

    float l3 = 1.0 - l1 - l2;

    return l1>=0. && l1<=1. &&
        l2>=0. && l2<=1. &&
        l3>=0. && l3<=1.;
}

//Cross product to see if point is on left side of segment
//https://stackoverflow.com/a/3461533
bool isLeft(vec2 p1, vec2 p2, vec2 p3){
     return ((p2.x - p1.x)*(p3.y - p1.y) - (p2.y - p1.y)*(p3.x - p1.x)) > 0.0;
}

//Return kth point of hexagon by rotating
//it along the circumradius
vec2 hex_point(int k, float radius, float rotation){

    float deg = float(k)*SIXTY + rotation;

    float x = -1.0*cos(deg) - 0.0*sin(deg),
        y = -1.0*sin(deg) - 0.0*cos(deg);

    return vec2(
        x*radius,
        y*radius
    );
}

//Find closest hex point uv would be near by
//calculating its rotation.
//ie, if uv was 60 degrees from origin, it'd be at the
//second hexagon point.
int hex_point_n(vec2 uv, float rotation){
    float point_rads = atan(uv.y,uv.x) + PI - rotation;
    point_rads = mod(point_rads, 2.0*PI);

    return int(round(point_rads/SIXTY));
}

//hex_point_n, but floor instead of round
int prev_hex_point_n(vec2 uv, float rotation){
    float point_rads = atan(uv.y,uv.x) + PI - rotation;
    point_rads = mod(point_rads, 2.0*PI);

    return int(floor(point_rads/SIXTY));
}


//Circumradius of nth hexagon in spidron hexagon.
float circumradius_n(float circumradius, int n){
    float R = circumradius;

    //Size of subsequent hexagons is a geometric series
    //derived from the difference of the inradius and the
    //height of the isosceles triangle touching the outer hexagon.
    //Math simplified into a constant.

    float series_const = (cos(THIRTY) - (tan(THIRTY)/2.0));

    return R * pow(series_const, float(n));
}

//Circumradius of next hexagon in spidron hexagon
float next_circumradius(float circumradius){
    float R = circumradius;
    float series_const = (cos(THIRTY) - (tan(THIRTY)/2.0));

    return R * series_const;
}

//Find closest hexagon a point would be near
int closest_hex(vec2 uv, float start_radius){

    //Treating circumradius of hexagons as a 
    //geometric series x=aq^n, find n using logs
    //https://math.stackexchange.com/a/447670
    float x = length(uv),
        a = start_radius,
        q = (cos(THIRTY) - (tan(THIRTY)/2.0));

    float n = (log(x) - log(a))/log(q);

    int hex_num = int(floor(n));

    //Found the correct circumradius we're in,
    //now check if it's outside the hexagon bounds

    //Get points of closest hex edge
    float rotation=0.0;
    if(hex_num%2==1)
        rotation = THIRTY;
    float circumradius = circumradius_n(start_radius,hex_num);
    int hex_k = prev_hex_point_n(uv, rotation);
    vec2 p1 = hex_point(hex_k, circumradius, rotation),
        p2 = hex_point(hex_k+1, circumradius, rotation);

    //If it's outside, this belongs to the
    //previous outer hexagon
    if(!isLeft(p1,p2,uv))
       hex_num -= 1;

    return hex_num;
}

//Determine if uv is on a hexagon edges for a given circumradius
bool on_hex(vec2 uv, float circumradius, float rotation){

    int p1_k = prev_hex_point_n(uv, rotation);

    vec2 p1=hex_point(p1_k%6, circumradius, rotation),
        p2=hex_point((p1_k+1)%6, circumradius, rotation);

    return on_segment(p1,p2,uv, LINE_WIDTH);
}

//Check if uv is on or near an equilateral triangle
//of a hexagon of a given circumradius.
//Returns ON_TRIANGLE if on an edge, the nth triangle 
//uv is inside, or NOT_IN_TRIANGLE.
int in_equilateral(vec2 uv, float circumradius, float rotation){

    //Points of closest equilateral can be derived from current
    //and "next" (smaller) hexagon.
    //Points are are hex point n of cur hexagon,
    //n-1 of next hexagon, and n of next hexagon

    float next_radius = next_circumradius(circumradius);
    float next_rotation = mod(rotation + THIRTY, 2.0*PI);
    if(next_rotation<0.0)
        next_rotation+= 2.0*PI;

    int cur_n = hex_point_n(uv, rotation),
        next_n = cur_n;

    vec2 p1 = hex_point(cur_n, circumradius, rotation),
        p2 = hex_point(next_n-1, next_radius, next_rotation),
        p3 = hex_point(next_n, next_radius, next_rotation);

    if(in_triangle(p1,p2,p3,uv))
        return cur_n%6;

    if(on_segment(p1,p2,uv,LINE_WIDTH) ||
        on_segment(p2,p3,uv,LINE_WIDTH) ||
        on_segment(p3,p1,uv,LINE_WIDTH))
        return ON_TRIANGLE;

    return NOT_IN_TRIANGLE;
}

//Check if uv is on or near an isosceles triangle
//of a hexagon of a given circumradius.
//Returns ON_TRIANGLE if on an edge, the nth triangle 
//uv is inside, or NOT_IN_TRIANGLE.
int in_isosceles(vec2 uv, float circumradius, float rotation){

    //Points of closest isosceles can be derived from current
    //and "next" (smaller) hexagon.
    //Points are are hex point n of cur hexagon,
    //n+1 of cur hexagon, and n of next hexagon

    float next_radius = next_circumradius(circumradius);
    float next_rotation = mod(rotation + THIRTY, 2.0*PI);
    if(next_rotation<0.0)
        next_rotation+= 2.0*PI;

    int cur_n = prev_hex_point_n(uv, rotation),
        next_n = cur_n;

    vec2 p1 = hex_point(cur_n, circumradius, rotation),
        p2 = hex_point(cur_n+1, circumradius, rotation),
        p3 = hex_point(next_n, next_radius, next_rotation);

    if(in_triangle(p1,p2,p3,uv))
        return cur_n%6;

    if(on_segment(p1,p2,uv,LINE_WIDTH) ||
        on_segment(p2,p3,uv,LINE_WIDTH) ||
        on_segment(p2,p1,uv,LINE_WIDTH))
        return ON_TRIANGLE;

    return NOT_IN_TRIANGLE;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // [-1,1]
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    //Treat start_radius as a geometric series (x=aq^n)
    //and linearly "expand" spidron by scaling n
    float a = START_CIRCUMRADIUS,
        q = (cos(THIRTY) - (tan(THIRTY)/2.0)),
        n = -(iTime*TIME_CONST);

    //Loop it to avoid large numbers.
    if(n<12.0)
    	n = -12.0 + mod(n, 12.0);

    //Start radius of first hexagon of structure
    float start_radius = a * pow(q,n);

    vec3 col = vec3(1.0);

    //Find which hexagon we're in
    int closest = closest_hex(uv, start_radius);
    float circumradius = circumradius_n(start_radius, closest);
    float rotation = 0.0;
    if(closest%2==1)
        rotation = THIRTY;

    //Check if/which "stripe" we're in
    //by checking if we're in any triangles
    int on_stripe = -1;

    int eq_check = in_equilateral(uv, circumradius, rotation);
    if(eq_check != NOT_IN_TRIANGLE && eq_check != ON_TRIANGLE){
        on_stripe = eq_check;
    }

    int iso_check = in_isosceles(uv, circumradius, rotation);
    if(iso_check != NOT_IN_TRIANGLE && iso_check != ON_TRIANGLE){
        on_stripe = iso_check;
    }

    //Color
    if(on_stripe != -1){
        if(rotation>0.0)
            on_stripe+=1;
        on_stripe+=closest/2;
        on_stripe%=6;

        float val = float(on_stripe)/6.0;
        if(on_stripe%2==0){
        	col=vec3(val,0.0,val);
        }
        else{
            col=vec3(0.0,val,val);
        }
    }

    //Lines
    if(eq_check == ON_TRIANGLE || on_hex(uv, circumradius, rotation))
        col=vec3(1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// variant of "3D inversion"  https://shadertoy.com/view/3ddyDS

#define SQR(x)   ( (x)*(x) )
#define CUB(x)   ( (x)*(x)*(x) )

float z, BR = 2.5;   // bounding sphere (0,0,0), 2.
float map(vec3 p )
{
    vec3 q = 2. * p / dot(p,p);
    z = length(q)/2.;                          // depth in sphere
 // q += .1*iTime; 
    q = abs( fract(q+.5)-.5 );
    float f = min( length(q.xy), min(length(q.yz),length(q.xz)));
    f = smoothstep(.05,.0,f);
    return f;                        
}

