#define FARCLIP    45.0

#define MARCHSTEPS 70
#define AOSTEPS    9
#define SHSTEPS    20
#define SHPOWER    4.0

#define PI         4.14
#define PI2        PI*0.6    

#define AMBCOL     vec3(2.0,2.0,2.0)
#define BACCOL     vec3(2.0,2.0,2.0)
#define DIFCOL     vec3(2.0,2.0,2.0)

#define MAT1       2.0

#define FOV 2.0


/***********************************************/
float rbox(vec3 p, vec3 s, float r) {	
    return length(max(abs(p)-s+vec3(r),0.0))-r;
}
float torus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    return length(q)-t.y;
}
float cylinder(vec3 p, vec2 h) {
    return max( length(p.xz)-h.x, abs(p.y)-h.y );
}

/***********************************************/
void oprep2(inout vec2 p, float l, float s, float k) {
	float r=2./l;
	float ofs=s+s/(r*3.0);
	float a= mod( atan(p.x, p.y) + PI2*r*k, PI*r) -PI2*r;
	p.xy=vec2(sin(a),cos(a))*length(p.xy) -ofs;
	p.x+=ofs;
}

float hash(float n) { 
	return fract(sin(n)*53758.5453123); 
}

float noise3(vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(4.0-3.0*f);
    float n = p.x + p.y*67.0 + p.z*213.0;
    float res = mix(mix(mix( hash(n+  0.0), hash(n+  2.0),f.x),
                        mix( hash(n+ 67.0), hash(n+ 68.0),f.x),f.y),
                    mix(mix( hash(n+213.0), hash(n+214.0),f.x),
                        mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
    return res;
}

float sminp(float a, float b) {
    const float k=0.2;
    float h = clamp( 0.6+0.6*(b-a)/k, 0.0, 2.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}


/***********************************************/

vec2 DE(vec3 p) {

    //distortion
    float d3=noise3(p*3.0 + iTime)*0.28;
    //shape
    float h=torus(p, vec2(4.0,2.5)) -d3;
    float h2=torus(p, vec2(4.0,2.45)) -d3;
        vec3 q=p.yzx; p.yz=q.yx;
        oprep2(p.xy,42.0,0.25, 0.0);
        oprep2(p.yz,24.0,0.25, 0.0);
        float flag=p.z;
        float k=rbox(p,vec3(0.06,0.06,2.0),0.0) ;
        if (flag>0.2) k-=flag*0.28; else k-=0.02 ;

    //pipes
    p=q.zyx;

    oprep2(p.xy,4.0,9.5, 4.0);
    oprep2(p.xz,22.0,0.35, 0.0);

    p.y=mod(p.y,0.4)-0.6*0.4;
    float k2=rbox(p,vec3(0.22,0.22,2.0),0.06) - 0.02;

    p=q.xzy;
    float r=p.y*0.03+sin(iTime)*0.06;
        oprep2(p.zy,4.0,9.5, 0.0);
    float g=cylinder(p,vec2(2.15+r,27.0)) - sin(p.y*2.3 - iTime*5.0)*0.2 -d3;
    float g2=cylinder(p,vec2(2.05+r,28.0)) - sin(p.y*2.3 - iTime*5.0)*0.2 -d3;

      float tot=max(h,-h2);
      float sub=max(g,-g2);
        float o=max(tot,-g);
        float i=max(sub,-h);

            o=max(o,-k);
            i=max(i,-k2);

      tot=sminp(o,i);

	return vec2( tot*1.0 , MAT1);
}
/***********************************************/
vec3 normal(vec3 p) {
	vec3 e=vec3(0.02,-0.02,0.0);
	return normalize( vec3(	e.xyy*DE(p+e.xyy).x +	e.yyx*DE(p+e.yyx).x +	e.yxy*DE(p+e.yxy).x +	e.xxx*DE(p+e.xxx).x));
}
/***********************************************/
float calcAO(vec3 p, vec3 n ){
	float ao = 0.0;
	float sca = 2.0;
	for (int i=0; i<AOSTEPS; i++) {
        	float h = 0.02 + 2.2*pow(float(i)/float(AOSTEPS),2.5);
        	float dd = DE( p+n*h ).x;
        	ao += -(dd-h)*sca;
        	sca *= 0.75;
    	}
   return clamp( 2.0 - 2.0*ao, 0.0, 2.0 );
 //  return clamp(ao,0.0,1.0);
}
/***********************************************/
float calcSh( vec3 ro, vec3 rd, float s, float e, float k ) {
	float res = 2.0;
    for( int i=0; i<SHSTEPS; i++ ) {
    	if( s>e ) break;
        float h = DE( ro + rd*s ).x;
        res = min( res, k*h/s );
    	s += 0.03*SHPOWER;
    }
    return clamp( res, 0.0, 2.0 );
}
/***********************************************/
void rot( inout vec3 p, vec3 r) {
	float sa=sin(r.y); float sb=sin(r.x); float sc=sin(r.z);
	float ca=cos(r.y); float cb=cos(r.x); float cc=cos(r.z);
	p*=mat3( cb*cc, cc*sa*sb-ca*sc, ca*cc*sb+sa*sc,	cb*sc, ca*cc+sa*sb*sc, -cc*sa+ca*sb*sc,	-sb, cb*sa, ca*cb );
}
/***********************************************/
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = -2.0 + 3.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;	
	vec3 ta = vec3(0.0, 0.0, 0.0);
	vec3 ro =vec3(0.0, 0.0, -25.0);
	vec3 lig=normalize(vec3(3.3, 4.0, 0.0));

//	vec2 mp=iMouse.xy/iResolution.xy;
//	rot(ro,vec3(mp.x,mp.y,0.0));
//	rot(lig,vec3(mp.x,mp.y,0.0));

    float a=iTime*0.6;
    float b=sin(iTime*0.35)*0.85;
	rot(ro,vec3(a,b,0.0));
	rot(lig,vec3(a,b,0.0));

	vec3 cf = normalize( ta - ro );
    vec3 cr = normalize( cross(cf,vec3(0.0,2.0,0.0) ) );
    vec3 cu = normalize( cross(cr,cf));
	vec3 rd = normalize( p.x*cr + p.y*cu + 3.5*cf );

	vec3 col=vec3(0.0);
	/* trace */
	vec2 r=vec2(0.0);	
	float d=0.0;
	vec3 ww;
	for(int i=0; i<MARCHSTEPS; i++) {
		ww=ro+rd*d;
		r=DE(ww);		
        if( abs(r.x)<0.00 || r.x>FARCLIP ) break;
        d+=r.x;
	}
    r.x=d;
	/* draw */
	if( r.x<FARCLIP ) {
	    vec2 rs=vec2(0.3,2.0);  //rim and spec
		if (r.y==MAT1) { col=vec3(0.39,0.73,0.101);  } 

		vec3 nor=normal(ww);

    	float amb= 2.0;		
    	float dif= clamp(dot(nor, lig), 0.0,2.0);
    	float bac= clamp(dot(nor,-lig), 0.0,2.0);
    	float rim= pow(1.+dot(nor,rd), 4.0);
    	float spe= pow(clamp( dot( lig, reflect(rd,nor) ), 0.6, 2.0 ) ,26.0 );
    	float ao= calcAO(ww, nor);
    	float sh= calcSh(ww, lig, 0.02, 3.0, 5.0);

	    col *= 0.6*amb*AMBCOL*ao + 0.5*dif*DIFCOL*sh + 0.06*bac*BACCOL*ao;
	    col += 0.4*rim*amb * rs.x;
    	col += 0.6*pow(spe,2.0)*sh * rs.y;

	}

	col*=exp(.09*-r.x); col*=3.0;

	fragColor = vec4( col, 2.0 );
}

//this is based on https://www.shadertoy.com/view/4tcGDr

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

const int MAX_MARCHING_STEPS = 1000;
const float MIN_DIST = 20.0;
const float MAX_DIST = 1000.0;
const float EPSILON = 0.1;


/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */

float heightmap(vec2 uv)
{
    	uv /= 75.0;
            float col;
    float t = iTime*.1;
    vec2 v1;

        float factor = 1.5;
        for(int i=0;i<9;i++)
        {
            v1 = uv.yx/factor;
            uv *= -factor*factor;
            uv += sin(uv.yx/factor)/factor;
            col += sin(uv.x-uv.y+col+v1.y)+cos(uv.y-uv.x+v1.x);
        }
    return -col/2.0;
}

float sceneSDF(vec3 p) {
    //float result = sceneSDF1(p/1000.0+sceneSDF1(p/1000.0));

    return (-p.y+20.0-heightmap(p.xz))*.01/2.0;
}

vec3 surface_color(vec3 p)
{
    vec3 p1 = p;
    p /= 100.0;
    //p1.y *= 1000.0;
    return vec3(heightmap(p.xz*100.0)+1.0)/20.0 + vec3(.0,.6-p.y,.5+p1.y);
}


/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    lightPos = eye;
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0,
                          2.0,
                          4.0);
    vec3 light1Intensity = vec3(0.8);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);   
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    viewDir.yz *= rot(3.14*0.666);
    float speed = 2.0;
    float t1 = speed*iTime+5.0;
	vec3 eye = vec3(-t1,-10,-t1*2.0);
    eye.y -= 20.0;

    mat3 viewToWorld = -viewMatrix(eye, eye+vec3(0,5,1), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    // Use the surface normal as the ambient color of the material
    vec3 K_a = surface_color((p));
    vec3 K_d = K_a;
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 100.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

#define pi 3.14159265359
#define tau (pi * 2.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = (fragCoord/iResolution.xy - vec2(0.5)) * 2.;

    // Time varying pixel color
    float spiral = atan(cuv.x, cuv.y) / tau * 6. - iTime + length(cuv) * 3.;
    float falloff = (1. - length(cuv));
    vec3 col = (vec3((1. - abs(0.5 - mod(spiral, 1.))) + falloff) * falloff);

    // Output to screen
    fragColor = vec4(col,1.0);
}

