float perlinNoise(float x) {
    float id = floor(x);
    float f = fract(x);
    float u = f;
    return mix(hash1(id), hash1(id + 1.0), u);
}

float perlinNoise(vec2 x) {
    vec2 id = floor(x);
    vec2 f = fract(x);

	float a = hash1(id);
    float b = hash1(id + vec2(1.0, 0.0));
    float c = hash1(id + vec2(0.0, 1.0));
    float d = hash1(id + vec2(1.0, 1.0));
	// Same code, with the clamps in smoothstep and common subexpressions
	// optimized away.
    vec2 u = hermiteInter(f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

float perlinNoise(vec3 x) {
    const vec3 step = vec3(110., 241., 171.);

    vec3 id = floor(x);
    vec3 f = fract(x);

    // For performance, compute the base input to a 1D hash from the integer part of the argument and the 
    // incremental change to the 1D based on the 3D -> 1D wrapping
    float n = dot(id, step);

    vec3 u = hermiteInter(f);
    return mix(mix(mix( hash1(n + dot(step, vec3(0, 0, 0))), hash1(n + dot(step, vec3(1, 0, 0))), u.x),
                   mix( hash1(n + dot(step, vec3(0, 1, 0))), hash1(n + dot(step, vec3(1, 1, 0))), u.x), u.y),
               mix(mix( hash1(n + dot(step, vec3(0, 0, 1))), hash1(n + dot(step, vec3(1, 0, 1))), u.x),
                   mix( hash1(n + dot(step, vec3(0, 1, 1))), hash1(n + dot(step, vec3(1, 1, 1))), u.x), u.y), u.z);
}

float fbm (vec2 x, float H, int octaves) {
	float G = exp2(-H);
	float v = 0.;
	float f = 1.;
	float amp = 1.;
	float aSum = 1.;

    vec2 shift = vec2(100.);
	for ( int i=0; i < FBM_MAX_ITER; ++i) {
		if( i >= octaves) break;
		v += amp * perlinNoise(f*x);
		f *= 2.;
		amp *= G;
		aSum += amp;
		// Rotate and shift to reduce axial bias
		x = rot2(0.5) * x + shift;
	}
	return v / aSum;
}

float fbm (vec3 x, float H, int octaves) {
	float G = exp2(-H);
	float v = 0.;
	float f = 1.;
	float amp = 1.;
	float aSum = 1.;

	for ( int i=0; i < FBM_MAX_ITER; ++i) {
		if( i >= octaves) break;
		v += amp * perlinNoise(f*x);
		f *= 2.;
		amp *= G;
		aSum += amp;
	}
	return v / aSum;
}

float density(vec3 pos, int lod) {
	vec3 q = pos - vec3(0.1,0.1,0.4)*iTime;
	float f = fbm(q, 1., lod);
	float f2 = fbm(q + vec3(100.), 1., lod);
	return f * (smoothstep(1.5, 2., pos.y + f) + 1. - smoothstep(-1., -0.2, pos.y - 1.5*f2));
    // return clamp( -0.5 - pos.y + 2.*f, 0.0, 1.0 );
}

vec4 cloudsMarching(vec3 ro, vec3 rd, vec3 bgCol, vec3 sundir, int steps) {

	float t = 0.;
	vec4 sum = vec4(0.);
#ifdef LOW_QUALITY
	steps = steps/2;
#endif

	for(int i=0; i<steps; i++) {
		vec3 pos = ro + t*rd;
		// clamp computation space
	   	if( abs(pos.y)>3. || sum.a>0.99 ) break;

#ifdef LOW_QUALITY
		int lod = 1;
#else
#ifdef CLOUDS_LOD
	    int lod = int(exp(-0.005*float((i-350))));
#else
	    int lod = 3;
#endif
#endif
		float den = density(pos, lod);
	   	if( den>0.01 ) {

			// mix color using density
			vec4  col = vec4( mix( vec3(1.0,0.95,0.8), vec3(0.25,0.3,0.35), den ), den );

        	// fake lighting using diff of density throught ou sunDir
			float dif = clamp((den - density(pos+0.3*sundir, lod))/0.3, 0.0, 1.0 );
			vec3 lin = vec3(0.91,0.98,1.1) + vec3(1.0,0.6,0.3)*dif;
			vec3 lightColor = mix(vec3(0.91,0.98,1.1), vec3(0.65,0.55,0.52)*2.4,dif*1.3);

			col.xyz *= lightColor;
			// exponential decresing of density (mixing with bgcol)
			col.xyz = mix( col.xyz, bgCol, 1.0-exp(-0.002*t*t) );
			col.w *= 0.5; // reduce density alpha
			col.rgb *= col.a; // apply alpha
			sum += col*(1.0-sum.a);
		}
	// Enforce minimum stepsize. This is probably the most important part of the procedure.
#ifdef LOW_QUALITY
	t += max(0.1,0.05*t);
#else
	t += max(0.05,0.02*t);
#endif
	}
	return sum;
}

vec3 computeCamDir(vec2 uv, vec3 camPos, vec3 camUp, vec3 lookAtPos) {
	vec3 camVec = normalize(lookAtPos - camPos);
	vec3 sideNorm = normalize(cross(camUp, camVec));
	vec3 upNorm = cross(camVec, sideNorm);
	vec3 worldFacing = (camPos + camVec);
	vec3 worldPix = worldFacing + uv.x * sideNorm + uv.y * upNorm;
	return normalize(worldPix - camPos);
}

vec4 render(vec3 O, vec3 D) {

	vec3 sundir = normalize( vec3(-1.0,0.0,-1.0) );

	vec3 col = vec3(0.67,0.8,0.9) - D.y*0.4*vec3(0.5,0.2,0.8); // background sky color
	float sun = clamp( dot(sundir,D), 0.0, 1.0);
	col += 0.3*vec3(1.0,.6,0.1)*pow(sun, 10.0); // add sun

    vec4 res = clamp(cloudsMarching(O, D, col, sundir, 150), 0., 1.);
    col = col*(1.0-res.w) + res.xyz;// clouds

	col += 0.1*vec3(1.0,0.4,0.2)*pow( sun, 3.0 ); // sun glare   

    return vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    uv *= 1.5; // zoom

#ifdef MOUSE
    vec2 m = (iMouse.xy - .5*iResolution.xy)/iResolution.xy;

    // camera pos
    vec3 O = 4.0*normalize(vec3(sin(2.5*m.x+1.), 0.15+m.y*0.15, cos(2.5*m.x+1.)));
#else
	vec3 O = 4.0*vec3(2.5, 0.15, 1.);
#endif
    vec3 D = computeCamDir(uv, O, vec3(0., 1., 0.), vec3(0.));

    fragColor = render(O, D);
}

/*
	Perspex Web Lattice
	-------------------

	I felt that Shadertoy didn't have enough Voronoi examples, so I made another one. :) I'm
	not exactly sure what it's supposed to be... My best guess is that an Alien race with no 
	common sense designed a monitor system with physics defying materials. :)

	Technically speaking, there's not much to it. It's just some raymarched 2nd order Voronoi.
	The dark perspex-looking web lattice is created by manipulating the Voronoi value slightly 
	and giving the effected region an ID value so as to color it differently, but that's about
	it. The details are contained in the "heightMap" function.

	There's also some subtle edge detection in order to give the example a slight comic look. 
	3D geometric edge detection doesn't really differ a great deal in concept from 2D pixel 
	edge detection, but it obviously involves more processing power. However, it's possible to 
	combine the edge detection with the normal calculation and virtually get it for free. Kali 
	uses it to great effect in his "Fractal Land" example. It's also possible to do a
	tetrahedral version... I think Nimitz and some others may have done it already. Anyway, 
	you can see how it's done in the "nr" (normal) function.

	Geometric edge related examples:

	Fractal Land - Kali
	https://www.shadertoy.com/view/XsBXWt

	Rotating Cubes - Shau
	https://www.shadertoy.com/view/4sGSRc

	Voronoi mesh related:

    // I haven't really looked into this, but it's interesting.
	Weaved Voronoi - FabriceNeyret2 
    https://www.shadertoy.com/view/ltsXRM

*/

#define FAR 2.

int id = 0; // Object ID - Red perspex: 0; Black lattice: 1.


// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: https://developer.nvidia.com/gpugems/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    n = max((abs(n) - .3), .002);
    n /= (n.x + n.y + n.z ); // Roughly normalized.

	p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;

    // Loose sRGB to RGB conversion to counter final value gamma correction...
    // in case you're wondering.
    return p*p;
}


// Compact, self-contained version of IQ's 3D value noise function. I have a transparent noise
// example that explains it, if you require it.
float n3D(vec3 p){

	const vec3 s = vec3(8, 257, 213);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(4. - 3.*p); //p *= p*p*(p*(p * 6. - 15.) + 10.);
    h = mix(fract(sin(h)*53758.5453), fract(sin(h + s.x)*53758.5453), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); // Range: [0, 1].
}

// vec2 to vec2 hash.
vec2 hash22(vec2 p) { 

    // Faster, but doesn't disperse things quite as nicely. However, when framerate
    // is an issue, and it often is, this is a good one to use. Basically, it's a tweaked 
    // amalgamation I put together, based on a couple of other random algorithms I've 
    // seen around... so use it with caution, because I make a tonne of mistakes. :)
    float n = sin(dot(p, vec2(31, 289)));
    //return fract(vec2(262144, 32768)*n); 

    // Animated.
    p = fract(vec2(362144, 42768)*n); 
    // Note the ".45," insted of ".5" that you'd expect to see. When edging, it can open 
    // up the cells ever so slightly for a more even spread. In fact, lower numbers work 
    // even better, but then the random movement would become too restricted. Zero would 
    // give you square cells.
    return sin( p*7.2831853 + iTime )*.55 + .6; 

}

// 2D 2nd-order Voronoi: Obviously, this is just a rehash of IQ's original. I've tidied
// up those if-statements. Since there's less writing, it should go faster. That's how 
// it works, right? :)
//
float Voronoi(in vec2 p){

	vec2 g = floor(p), o; p -= g;

	vec3 d = vec3(2); // 1.4, etc. "d.z" holds the distance comparison value.

	for(int y = -2; y <= 2; y++){
		for(int x = -2; x <= 2; x++){

			o = vec2(x, y);
            o += hash22(g + o) - p;

			d.z = dot(o, o); 
            // More distance metrics.
            //o = abs(o);
            //d.z = max(o.x*.8666 + o.y*.5, o.y);// 
            //d.z = max(o.x, o.y);
            //d.z = (o.x*.7 + o.y*.7);

            d.y = max(d.x, min(d.y, d.z));
            d.x = min(d.x, d.z); 

		}
	}

    return max(d.y/0.6 - d.x*0.5, 0.)/2.2;
    //return d.y - d.x; // return 1.-d.x; // etc.

}

// The height map values. In this case, it's just a Voronoi variation. By the way, I could
// optimize this a lot further, but it's not a particularly taxing distance function, so
// I've left it in a more readable state.
float heightMap(vec3 p){

    id =0;
    float c = Voronoi(p.xy*5.); // The fiery bit.

    // For lower values, reverse the surface direction, smooth, then
    // give it an ID value of one. Ie: this is the black web-like
    // portion of the surface.
    if (c<.08) {c = smoothstep(0.8, 2., 2.-c)*.3; id = 2; }

    return c;
}

// Standard back plane height map. Put the plane at vec3(0, 0, 1), then add some height values.
// Obviously, you don't want the values to be too large. The one's here account for about 10%
// of the distance between the plane and the camera.
float m(vec3 p){

    float h = heightMap(p); // texture(iChannel0, p.xy/2.).x; // Texture work too.

    return 1. - p.z - h*.2;

}

