// followed great raymarch tutorial https://www.shadertoy.com/view/XlBGDW
#define CLIP_NEAR 0.1
#define CLIP_FAR 40.0
#define STEPS 100
#define EPSILON 0.0001
#define DISTANCE_BIAS 0.7
#define PI 3.14159
#define SPEED 0.25
#define START 10.0

// distance to sphere function (p is world position of the ray, s is sphere radius)
// from http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdSphere(vec3 p, float s)
{
	return length(p) - s;
}

float scene(vec3 p) {
    vec3 pos_big = vec3(fract(p.x * p.x + 0.5) - 0.5, p.y, p.z); // this + careful camera control is where the effect comes from 
    float d = sdSphere(pos_big, 0.3);
    return d;
}

float raymarch(vec3 p, vec3 direction) {
	float dist = CLIP_NEAR;
    float result;
    for (int i = 0; i < STEPS; ++i) {
        result = scene(p + direction * dist);
        if (result < EPSILON) { break; }
        dist += result * DISTANCE_BIAS;
        if (dist > CLIP_FAR) { break; }
    }
    return dist;
}

vec3 normal(vec3 ray_hit_position, float smoothness)
{	
    // From https://www.shadertoy.com/view/MdSGDW
	vec3 n;
	vec2 dn = vec2(smoothness, 0.0);
	n.x	= scene(ray_hit_position + dn.xyy) - scene(ray_hit_position - dn.xyy);
	n.y	= scene(ray_hit_position + dn.yxy) - scene(ray_hit_position - dn.yxy);
	n.z	= scene(ray_hit_position + dn.yyx) - scene(ray_hit_position - dn.yyx);
	return normalize(n);
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(eye - center);
	vec3 s = normalize(cross(f, up));
	vec3 u = cross(s, f);
	return mat4(
		vec4(s, 0.0),
		vec4(u, 0.0),
		vec4(-f, 0.0),
		vec4(0.0, 0.0, 0.0, 1)
	);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 offset = vec3(SPEED * iTime + START, 0.0,  0.0);
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 direction = normalize(vec3(uv, 2.5));    

    vec3 eye = vec3(
        1.5 - 0.5 * sin(PI * iMouse.x / iResolution.x) + offset.x,
        0.12 - 0.05 * iMouse.y / iResolution.y + offset.y + 0.1 * sin(PI * iMouse.x / iResolution.x),
        0.0 - 0.2 * cos(PI * iMouse.x / iResolution.x) + offset.z);

    mat4 viewToWorld = viewMatrix(eye, offset, vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(direction, 0.0)).xyz;
    direction = worldDir;
    vec3 camera_origin = eye;
    float result = raymarch(camera_origin, direction);

    // arbitrary fog to hide artifacts near the far plane
    // 1.0 / distance results in a nice fog that starts white
    // but if distance is 0 
    float fog = 1.0 - result / CLIP_FAR;

    vec3 materialColor = vec3(1.0, 0.25, 0.1);

    // We can reconstruct the intersection point using the distance and original ray
    vec3 intersection = camera_origin + direction * result;

    // The normals can be retrieved in a fast way
    // by taking samples close to the end-result sample
    // their resulting distances to the world are used to see how the surface curves in 3D
    // This math I always steal from somewhere ;)
    vec3 nrml = normal(intersection, 0.01);

    // Lambert lighting is the dot product of a directional light and the normal
    vec3 light_dir = normalize(vec3(0.0, 1.0, 0.0));
   	float diffuse = dot(light_dir, nrml);
    // Wrap the lighting around
    // https://developer.valvesoftware.com/wiki/Half_Lambert
    //diffuse = diffuse * 0.5 + 0.5;
    // For real diffuse, use this instead (to avoid negative light)
    //diffuse = max(0.0, diffuse);

    // Combine ambient light and diffuse lit directional light
    vec3 light_color = vec3(1.4, 1.2, 0.7);
    vec3 ambient_color = vec3(0.2, 0.45, 0.6);
    vec3 diffuseLit = materialColor * (diffuse * light_color + ambient_color);
	fragColor = vec4(diffuseLit, 1.0) * fog; /* applying the fog last */
}

precision mediump float;


#define ANIMATE 0
#define MAX_STEPS 255
#define MIN_DIST 0.0
#define MAX_DIST 100.0
#define EPSILON 0.0001

float dot2(in vec2 v) { return dot(v,v); }



float noise(vec3 p)
{
	vec3 ip=floor(p);
    p-=ip; 
    vec3 s=vec3(7,157,113);
    vec4 h=vec4(0.,s.yz,s.y+s.z)+dot(ip,s);
    p=p*p*(3.-2.*p); 
    h=mix(fract(sin(h)*43758.5),fract(sin(h+s.x)*43758.5),p.x);
    h.xy=mix(h.xz,h.yw,p.y);
    return mix(h.x,h.y,p.z); 
}

float fbm( in vec3 x, in float H )
{    
    float t = 0.0;
    for( int i=0; i<3; i++ )
    {
        float f = pow( 2.0, float(i) );
        float a = pow( f, -H );
        t += a*noise(f*x);
    }
    return clamp(t, 0.0, 1.0);
}

/* SIGNED DISTANCE FUNCTIONS */
