// intersect ray with the scene
vec2 raycast(vec3 ro, vec3 rd)
{
	vec2 result = vec2(-1.0);
	float t = MIN_DIST;
	for (int i =0; i < MAX_STEPS && t < MAX_DIST; i++) {
		vec2 h = map(ro + t*rd);
		if (abs(h.x) < (EPSILON*t)) {
			result = vec2(t, h.y);
			break;
		}
		t += h.x;
	}

	return result;
}

// get direction of ray with just device coordinates
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}


// calculate the normal via finite differences
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}


float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (0.8-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=0; i<24; i++ )
    {
		float h = map( ro + rd*t ).x;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.2 );
        if( res<0.004 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}




float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

vec3 light = normalize( vec3(-0.5, 0.4, 0.6) );


vec3 render(vec3 ro, vec3 rd, vec3 rdx, vec3 rdy)
{
	// background
	vec3 col = background(rd);

	vec2 tmat = raycast(ro, rd);
	float t = tmat.x;
	float m = tmat.y;
	if (m > -1.) {


		col = 0.2 + 0.2*sin( m*2.0 + vec3(0.0,1.0,2.0) );
        clamp(col, 0.0, 1.0);
        float ks = 1.0;

		vec3 pos = ro + tmat.x*rd;
		vec3  hal = normalize(light - rd );
		vec3 norm = (m < 1.5) ? vec3(0.0,1.0,0.0) : calcNormal( pos );
		vec3 ref = reflect( rd, norm );

		if( m<1.5 )
        {
            // project pixel footprint into the plane
            //vec3 dpdx = ro.y*(rd/rd.y-rdx/rdx.y);
            //vec3 dpdy = ro.y*(rd/rd.y-rdy/rdy.y);

            col = vec3(0.30);
            ks = 0.1;
        }

        if (m > 99.)
        {
            col = vec3(fbm(pos*11., 1.));
        }

		float occ = calcAO(pos, norm); 
		vec3 lin = vec3(0.0);

		// sun
		{
            vec3  hal = normalize( light-rd );
            float dif = clamp( dot( norm, light ), 0.0, 1.0 );
        	      dif *= calcSoftshadow( pos, light, 0.02, 2.5 );
			float spe = pow( clamp( dot( norm, hal ), 0.0, 1.0 ),16.0);
                  spe *= dif;
                  spe *= 0.04+0.16*pow(clamp(1.0-dot(hal,light),0.0,1.0),2.0);
            lin += col*2.20*dif*sunColor;
            lin +=     5.00*spe*sunColor*ks;
        }

        // sky
        {
            float dif = sqrt(clamp( 0.5+1.5*norm.y, 0.0, 1.0 ));
            dif *= occ;
            float spe = smoothstep( -0.2, 0.2, ref.y );
            spe *= dif;
            spe *= 0.04+0.96*pow(clamp(1.0+dot(norm,rd),0.0,1.0), 5.0 );
            spe *= calcSoftshadow( pos, ref, 0.02, 4.2 );
            lin += col*0.70*dif*background(rd);
            lin +=     1.0*spe*background(rd)*ks;
        }
        col = lin;

		// falloff (fakes a depth blur)
        col = mix( col, vec3(0.7,0.7,0.9), 1.0-exp( -0.00002*t*t*t ) );
	}

	return vec3( clamp(col,0.0,1.0) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);

    vec3 eye = vec3(5. * sin(.7*iTime), 7.0, 2. + 5. * cos(.8*iTime));

    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

  	// ray derivatives
    vec2 px = (2.0*(fragCoord+vec2(1.0,0.0))-iResolution.xy)/iResolution.y;
    vec2 py = (2.0*(fragCoord+vec2(0.0,1.0))-iResolution.xy)/iResolution.y;
    vec3 rdx = (viewToWorld * normalize( vec4(px,2.5, 1.0) )).xyz;
    vec3 rdy = (viewToWorld * normalize( vec4(py,2.5, 1.0) )).xyz;

    vec3 col = render(eye, worldDir, rdx, rdy);

	fragColor = vec4(col, 1.0);
}

#define rot2D(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float box1(vec3 p) {
    p.y += 1.6;
    p = abs(p);
    p.x -= .1;
    p.y -= 2.;
    return max(max(p.x,p.y),p.z);
}

float dist(vec3 p, float speed) {
    vec3 q=p, e = vec3(1,0,0);
    p.xy *= rot2D(iTime * speed);
    float a = p.x < 0. ? length(p-e.zxz) - 1. :  length(p+e.zxz) - 1.,
          b = p.y < 0. ? length(p+e.xzz) - 1. :  length(p-e.xzz) - 1. ;
    return min( min(a,b),  box1(q) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;
    vec2 uv = (fragCoord.xy * 2.0 - resolution.xy) / min(resolution.x, resolution.y);

    vec2 uv2 = mod(uv, 0.25) - 0.1;

    vec3 cp = vec3(0.,0.,-5.);
    float screenZ = 0.2; 

    vec3 rd = normalize(vec3(uv2, screenZ));

    float depth = 0.0;
    vec3 col = vec3(1.);

    for (int i=0; i<10; i++) {
        vec3 rp = cp + rd * depth;
        float dist = dist(rp, 2.3);
        if(dist < 0.001) {
            col = vec3(0.);
            break;
        }
        depth += dist;
    }


    fragColor = vec4(col,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 v = vec3(p.x, p.y, sqrt(1.0 - p.x * p.x - p.y * p.y));
    vec3 n = normalize(v);
    //vec3 l = vec3(0.0, 0.0, 1.0);
    vec3 l = vec3(sin(iTime), 0.0, cos(iTime));
    float b = dot(n, l);

    fragColor = vec4(b);
    //fragColor = vec4(n, 1.0);
}

