// iq's noise
float noise(in vec3 x) {
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f * f * (3. - 2. * f);
	vec2 uv = (p.xy + vec2(37., 17.) * p.z) + f.xy;
	vec2 rg = textureLod(iChannel0, (uv + .5) / 256., 0.).yx;
	return -1. + 2.4 * mix(rg.x, rg.y, f.z);
}

// Fbm
float fbm(vec3 p) {
	return noise(p * .06125) * .5 + noise(p * .125) * .25 + noise(p * .25) * .125;
}

// Dodecahedron folding
// checkout: https://www.shadertoy.com/view/wtsGzl
vec3 fold(vec3 p) {
	vec3 nc = vec3(-.5, -.809017, .309017);
	for (int i = 0; i < 5; i++) {
		p.xy = abs(p.xy);
		p -= 2.*min(0., dot(p, nc))*nc;
	}
	return p - vec3(0, 0, 1.275);
}

float sdf_shell(vec3 p, float scale) {
	vec3 fp = fold(p * scale);
	float dist = dot(fp, normalize(sign(fp))) - .0 - sin(fp.y*.2)*2. - sin(fp.y*.5)*1.;
    //g+=(0.1/(.01+dist*dist*999.) * sin(iTime  * .5));

	dist += min(fp.x*1., sin(fp.y*.4)) + fbm(fp )* .03;
	//g+=(0.1/(.01+dist*dist*995.) * sin(iTime  * .5));
	fp = fold(fp) - vec3(.4, 2, -2.3);
	fp = fold(fp) - vec3(.8, 1, .1);
	fp = fold(fp) - vec3(0, .3, 0);
	dist += sin(fp.y*.1)*.1;

	dist *= 1.;
	//g+=(0.1/(.01+dist*dist*9995.));// * sin(iTime  * .5));

	return dist / scale;
}

float sdf_core(vec3 p, float scale) {
	vec3 fp = fold(p * scale);
	float dist = dot(fp, normalize(sign(fp))) - .4 - sin(fp.y*.8)*1.8 - sin(fp.y*.7)*1.;
	dist += min(fp.x*8., sin(fp.y*.1));

	fp = fold(fp) - vec3(-.45, 6.7, -.1);
	fp = fold(fp) - vec3(-.3, 8.7, -0.9);
	fp = fold(fp) - vec3(0, .02, 0);
	dist += sin(fp.y*.2)*8.;
	dist *= .4;

	return dist / scale;
}



float de(vec3 p, vec3 p2) {


	float s = 37.;
	p = mod(p - s * .5, s) - s * .5;

	float dist;
	dist = sdf_shell(p, .1 * (1. /*+ sin(u_time * .5)*/));
	dist = min(dist, sdf_core(p2, .6/* * ( 1.7 - sin(u_time * .5 ) ) */)) * .7;

    #ifdef DECAY
	return dist + fbm(p*30.) * (.6+.45*sin(4.+1.*kick(iTime*.1, .1)));
    #else
    return dist + fbm(p*noise_freq) * noise_amt;
    #endif
}

vec3 camera(vec3 ro, vec3 ta, vec2 uv) {
	vec3 fwd = normalize(ta - ro);
	vec3 left = normalize(cross(vec3(0, 1, 0), fwd));
	vec3 up = normalize(cross(fwd, left));
	return normalize(fwd + uv.x * left + up * uv.y);
}


// mat3 from quaternion
mat3 SetRot( const in vec4 q )
{
	vec4 qSq = q * q;
	float xy2 = q.x * q.y * 2.0;
	float xz2 = q.x * q.z * 2.0;
	float yz2 = q.y * q.z * 2.0;
	float wx2 = q.w * q.x * 2.0;
	float wy2 = q.w * q.y * 2.0;
	float wz2 = q.w * q.z * 2.0;

	return mat3 (	
     qSq.w + qSq.x - qSq.y - qSq.z, xy2 - wz2, xz2 + wy2,
     xy2 + wz2, qSq.w - qSq.x + qSq.y - qSq.z, yz2 - wx2,
     xz2 - wy2, yz2 + wx2, qSq.w - qSq.x - qSq.y + qSq.z );
}



// mat3 from axis / angle
mat3 SetRot( vec3 vAxis, float fAngle )
{	
	return SetRot( vec4(normalize(vAxis) * sin(fAngle), cos(fAngle)) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){


	vec2 vUV = fragCoord.xy / iResolution.xy;
    //vUV = glitch(vUV);
	vec2 vWindow = vUV * 2.0 - 1.0;
	vWindow.x *= iResolution.x / iResolution.y;

	vec2 vMouse = iMouse.xy / iResolution.xy;

	//vec3 ray_ori = vec3(-11.*cos(u_time), sin(u_time)*7., -11. * sin(u_time)); // softer cam

    /*vec3 ray_ori = vec3(-10.*cos(kick(u_time*2., .1)), 16.*cos(kick(u_time*1., .1)), -10.*sin(kick(u_time, .1)));
    vec3 target = vec3(0);
	vec3 ray_dir = camera(ray_ori, target, uv);
	vec3 pos = ray_ori;*/


	float fHeading = iTime * 0.21;
	float fElevation = cos(iTime * 0.1) * 0.5;
	float fCameraDistance = 20.0 + sin(iTime * .5) * 5.0;

	float fSinElevation = sin(fElevation);
	float fCosElevation = cos(fElevation);
	float fSinHeading = sin(fHeading);
	float fCosHeading = cos(fHeading);

	vec3 vCameraOffset;
	vCameraOffset.x = fSinHeading * fCosElevation;
	vCameraOffset.y = fSinElevation;
	vCameraOffset.z = fCosHeading * fCosElevation;

	vec3 vCameraPos = vCameraOffset * fCameraDistance;
	vec3 pos = vCameraPos;
	vec3 vCameraTarget = vec3(0.0, 0.0, 0.0);

	vec3 vForward = normalize(vCameraTarget - vCameraPos);
	vec3 vRight = normalize(cross(vec3(0.0, 1.0, 0.0), vForward));
	vec3 vUp = normalize(cross(vForward, vRight));

	float fFov = 2.0;

	vec3 vDir = normalize(vWindow.x * vRight + vWindow.y * vUp + vForward * fFov);

	vec3 vRotationAxis = vec3(1.0, 4.0, 2.0);

	// Rotate the rotation axis
	mat3 m2 = SetRot( vec3(0.1, 1.0, 0.01), iTime * 0.3 );		

	vRotationAxis = vRotationAxis * m2;

	float fRotationAngle = sin(iTime * 0.5);

	if( iMouse.z > 0.0 )
	{
		vec2 vMouseOffset = vMouse * 2.0 - 1.0;
		vRotationAxis = vec3(vMouseOffset.x, 1.0, vMouseOffset.y);
		//vRotationAxis = vec3(vMouseOffset.y, vMouseOffset.x, 1.0);
		fRotationAngle = length(vRotationAxis);
	}

	m = SetRot(vRotationAxis, fRotationAngle);
	m2 = SetRot(-sin(vRotationAxis), -fRotationAngle*2.);

	// local density/distance
	float ldensity = 0.;

	// accumulation color & density
	vec3 bg = vec3(.16, .12, .22);
	vec4 sum = vec4(bg, 0.);

	float tmax = 45.;
	float tdist = 0., dist = 0.;

	for (float i = 0.; (i < 1.); i += 1. / 64.) {

		if (dist < tdist * .001 || tdist > tmax || sum.a > .95)
			break;

		// evaluate distance function
		vec3 vPos = (pos.xyz) * m;
		vec3 vPos2 = (pos.xyz) * m2;
		//dist = de(pos) * .5;
		dist = de(vPos, vPos2) * .5;

		// check whether we are close enough (step)
		// compute local density and weighting factor 
		const float h = .05;
		ldensity = (h - dist) * step(dist, h);

		vec4 col;
		col.rgb = colormapIQ(ldensity * 3.5 );/*(ldensity * pal_scale + pal_offset,
			vec3(.7, .4, .2 + sin(iTime*1.5)*.1),
			vec3(.9, .8, .4),
			vec3(.4, .5, .6),
			vec3(.8, .5, .3));*/

        col.rgb = clamp(col.rgb, 0., 1.);
		col.a = ldensity;

		// pre-multiply alpha
		// checkout: https://www.shadertoy.com/view/XdfGz8
		// http://developer.download.nvidia.com/assets/gamedev/files/gdc12/GDC2012_Mastering_DirectX11_with_Unity.pdf
		col.rgb *= col.a;

		sum += (1. - sum.a) * col;
		//sum += g * .01;
		// from duke/las
		sum.a += .015;

		// enforce minimum stepsize
		dist = max(dist, .05 + sin(iTime)*.01);

		// step forward
		pos += dist * vDir; // sphere-tracing
		tdist += dist;
	}

	// from duke/las
	// simple scattering approximation
	sum *= 1. / exp(ldensity * .2) * 1.25;

	fragColor = vec4(sum.rgb, 1.);
    fragColor.rgb *= smoothstep( 1.5, 0.4, length(fragCoord.xy-iResolution.xy*.5)/length(iResolution.xy*.5) );
    vec3 grunge = texture( iChannel1, fragCoord/iChannelResolution[1].xy + vec2(.7549,.5698)*float(iFrame) ).rgb;
    fragColor.rgb = mix( fragColor.rgb*mix( vec3(1), grunge, .2 ), grunge*vec3(.3,1,.6), .02 );
    fragColor.rgb *= 1.+.4*sin(6.283185*fragCoord.y*.5*.618+iTime);
    fragColor.rgb *= mix( 1., 1.8, smoothstep(.0,.3,cos(iTime/7.)) );    
    fragColor.rgb = max(vec3(0),fragColor.rgb-.02);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    float t = iTime*.3;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y+vec2(t+2.0);
        //uv *= 10.0;
        int c = 0;
        for(int i=0;i<9;i++)
        {
            c = i%3;
            float factor = 1.5;
            vec2 uv1 = uv;
            uv /= factor;
            uv += uv1;
            uv += (sin(uv.yx))/factor;
            uv *= factor;
            col[c] += sin(uv.x+uv.y);
        }
    fragColor = vec4(col*100.0,1.0);
}

#define pi acos(-1.)

float sdCircle(vec2 p,float r)
{
    return length(p)-r;
}
float sdBox(in vec2 p,in vec2 b)
{
    vec2 d=abs(p)-b;
    return length(max(d,0.))+min(max(d.x,d.y),0.);
}
float sdSegment(in vec2 p,in vec2 a,in vec2 b)
{
    vec2 pa=p-a,ba=b-a;
    float h=clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa-ba*h);
}
float map(vec2 p,float t){
    p*=1.2;
    p.y-=.1;
    float d=sdCircle(p,.2);
    d*=sdCircle(p,.18);

    d=min(d,sdCircle(p,.03));

    vec2 ap=p;
    ap.x=abs(ap.x);
    d=min(d,sdSegment(ap,vec2(0),vec2(.1,-.35))-.01);
    d=min(d,sdSegment(ap,vec2(0,-.35),vec2(.3,-.35))-.02);

    mat2 am=mat2(cos(t),-sin(t),sin(t),cos(t));
    vec2 np=p;
    np*=am;

    float seg=6.;
    float an=pi/float(seg);
    float bn=mod(atan(np.x,np.y),2.*an)-an;
    np=length(np)*vec2(cos(bn),sin(bn));
    np.x-=.1;
    d=min(d,sdBox(np,vec2(.1,.01)));
    float innerHex=dot(np,normalize(vec2(1,0.)));
    innerHex*=dot(np*50.,normalize(vec2(1,0)));
    d=min(d,smoothstep(.003,.1,innerHex));
    vec2 sp=p;
    sp.y+=.04;
    sp*=am;
    bn=mod(atan(sp.x,sp.y),2.*an)-an;
    an=-t;
    sp*=mat2(cos(an),-sin(an),sin(an),cos(an));
    sp=length(sp)*vec2(cos(bn),sin(bn));
    sp.x-=.2;
    an=2.*pi/seg*atan(p.x,p.y)/2./pi*seg+pi/seg;
    sp*=mat2(cos(an),-sin(an),sin(an),cos(an));

    d=min(d,(sdBox(sp,vec2(.0175))-.015)*(sdBox(sp,vec2(.0075,.0125))-.01));

    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord.xy-.5*iResolution.xy)/iResolution.x;
    vec2 m=iMouse.xy/iResolution.xy;
    float segs=5.;
    float t=floor(mod(uv.x,.02)*100.*segs);
    vec3 col=vec3(smoothstep(0.,.00001,map(uv,t*2.)));
    float sx=1.-abs(fract(iTime*.025+.25)*2.-1.);
    if(iMouse.z > 0.) {
    	sx=m.x;
    }
    if(uv.x<sx-.5){
        col*=vec3(ceil(mod(uv.x-sx,.02)-(2.-1./segs)*.01));
    }
    fragColor=vec4(col,1.);
}

