void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*8.;
	uv.x = uv.x*(iResolution.x/iResolution.y);
	vec2 mouse = iMouse.xy*8./iResolution.xy;
    mouse.x = mouse.x*(iResolution.x/iResolution.y);

	vec2 uvInterpolation = fract(uv);
    uvInterpolation = smoothstep(0.,1.,uvInterpolation);
    //uvInterpolation = uvInterpolation*uvInterpolation*(3.0-2.0*uvInterpolation);
    vec2 uvFloor = floor(uv);
    float rand = random(uv);
    float a = random(uvFloor + vec2(0, 1));
    float b = random(uvFloor + vec2(1, 1));
    float c = random(uvFloor + vec2(1, 0));
    float d = random(uvFloor + vec2(0, 0));

    // depending on either mouse click or function based on time a point will be moving across the screen.
    float movement = 2. + length(mouse - uv);
    if (iMouse.w  <= 0.)
    {
     	vec2 coord = vec2(cos(iTime/2.)*iResolution.x/iResolution.y, sin(iTime/4.))*2.;
    	movement = 2.0 + length((coord + vec2(0.5*8.*iResolution.x/iResolution.y, 0.5*8.)) - uv);
    }

    /** Perlin Noise. */
    float result = dot(vec2(1. - uvInterpolation.x, uvInterpolation.x), transpose(mat2(d, a, c, b))*vec2(1.-uvInterpolation.y, uvInterpolation.y));
    //float result = mix(mix(a, b, uvInterpolation.x), mix(d, c, uvInterpolation.x), uvInterpolation.y); 
    fragColor = vec4(max(0.0, cos(result*300./movement + 0.7*pow(movement/2., 2.)*random(vec2(uv*16.)))));

}

