float plot(vec2 uv, float pct)
{
	return smoothstep(pct-10.0, pct, uv.y) - smoothstep(pct, pct+10.0, uv.y);   
}

float func(vec2 ipos)
{
    vec3 center = vec3(floor(0.5 * scale), floor(0.5 * scale), -1.0);
    float dist = distance(vec3(ipos,-1.0), center);
	return sin(dist * 400.0*sin(iTime / 200.0) / 2.0) - 0.2;    
}

float _smooth(in vec2 ipos, in vec2 uv)
{

    float a = func(ipos);
    float b = func(ipos + vec2(1.0, 0.0));
    float c = func(ipos + vec2(0.0, 1.0));
    float d = func(ipos + vec2(1.0, 1.0));


    vec2 u = smoothstep(0.0, 1.0, uv );

    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) +
        	  (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec2 ipos = floor(uv * scale);
    float y = _smooth(ipos, fract(uv * scale));
    float pct = plot(ipos, y);


    fragColor = vec4(vec3(0., y, 0.),1.0);
}

