float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

float sdf(vec3 p) {
    return length(p) - 1.0 + 0.1 * sin(p.y*10.+3.141592653);
}

float opTwist(vec3 p)
{
    const float k = 3.0; // or some other amount
    float c = cos(k*p.y+iTime*0.2);
    float s = sin(k*p.y+3.141592653*iTime*0.2);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return sdf(q);
}


float GetDist(vec3 p) {
    float res = 0.1 * opTwist(p);
    return res;
