	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox2d(vec2 p, vec2 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, p.y), 0.);
}

float GetDist(vec3 p) {
    float r1 = 1.7, r2 = .3;
    vec2 cp = vec2(length(p.xz) - r1, p.y);
    float a = atan(p.x,p.z); // polar angle between -pi and pi
    cp *= Rot(a * .5 + iTime*0.5);
    cp.y = abs(cp.y) - .4;
    float d1 = length(cp) - r2;
   	d1 = sdBox2d(cp, vec2(.01,.4))-0.01; // nice trefoil shape
    float d3 = sdBox2d(cp, vec2( 4.*sin(a*iTime*0.07-.4)*0.0141 - .1,.1+.281*sin(iTime*.3)))-0.2;


    float d4 = sdBox2d(cp,vec2(.2,.3*sin(4.*a)*.8+.5))-.1;
    float d5 = sdBox2d(cp,vec2(.1,-0.4+.3*sin(12.*a)*.5+.5))-.1;
    //d3 *= Rot(iTime);
    float d6 = sdBox2d(cp, vec2(4.*sin(a*3. + iTime*3.1415*3.3-.4)*0.0141 - .1,.1+.281*sin(iTime*.3)))-0.2;

    //float d2 = length(vec2(length(p.xz)-r1,p.y))-.4; // torus
//    float d = smoothstep(d1,d2,sin(iTime*0.7)*.5+0.5); // Doesnt work
    return d1*0.37;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}


vec3 Bg(vec3 rd) {
    float k = rd.y*.5 + .5;

    vec3 col = mix(vec3(.2,.1,.1),vec3(.2,.5,.1),k);
    return col;

}

