    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    col += Bg(rd);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float spec =pow( max(0., r.y), 20.);
    	float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
    	col = mix(Bg(r), vec3(dif),.5) + spec; 
