  return fract(sin(t*457.588)*942.512);
}

float curve(float t, float d) {
  t/=d;
  return mix(rnd(floor(t)), rnd(floor(t)+1.), pow(smoothstep(0.,1.,fract(t)), 10.));
}

vec3 aco=vec3(0);
float map(vec3 p) {

  vec3 bp=p;

  float t3 = time*0.4;
  p.xz *= rot(t3 + sin(p.y*0.3+t3*.7)*.5);
  p.xy *= rot(t3 + sin(p.z*.4+t3*.6)*.5);
  p.yz *= rot(t3 + sin(p.x*.2+t3*.8)*.5);

  float d=length(p)-3.;

  float sd=3./length(p);
  d = min(d, length(p.xz)-sd);
  d = min(d, length(p.xy)-sd);
  d = min(d, length(p.yz)-sd);


  float an = pow(fract(time*0.5),2.);
  float ep = 0.2;
  ep -= an*0.1 + d*0.05;
  float d3 = abs(length(p)-an*20.-3.)-ep;
  d3=max(d3, 0.1);
  aco += vec3(0.8,0.5,1) * 0.004 / (0.05 + abs(d3));

  d = min(d, d3);

  vec3 p6 = bp;
  p6.xz=abs(p6.xz);
  if(p6.z>p6.x) p6.xz = p6.zx;
  p6.x -= 22.;
  p6.z = abs(p6.z)-6.-sin(bp.y*.03 + time)*5.-sin(bp.y*.1 + time)*10.;
  p6.z = abs(p6.z)-3.-sin(bp.y*.07 + time)*5.-sin(bp.y*.2 + time*1.3)*4.;

  float d7 = length(p6.xz) - 1.;
  d7=max(d7,0.2);
  d = min(d, d7);
  aco += vec3(0.8,0.5,1) * 0.008*exp(fract(-time*2.)) / (0.05 + abs(d7));

  d *= 0.7;

  p.x += tick(time, 1.)*4.;

  if(d<0.1) {
  for(float i=1.; i<11.; ++i) {

    p-= 0.8;
    float ss = 9./i;
    vec3 id = repid(p, vec3(ss));
    vec3 p2 = repeat(p, vec3(ss));
    p2 += rnd(id)*0.2;

    float d2 = length(p2)-ss*0.3;



    d=max(d, -d2);

    p.xz *= rot(0.7);
    p.yz *= rot(0.6);
  }

  vec3 p4 = repeat(p + time*4., vec3(8));
  float d4 = max(abs(p4.x)-0.1, d);
  aco += vec3(0.4,0.5,1.9) * 0.026 / (0.15 + abs(d4));
  float d5 = max(abs(p4.y)-0.1, d);
  aco += vec3(0.8,0.5,0.4) * 0.016 / (0.15 + abs(d5));
  d = min(d, d4);
  d = min(d, d5);
}

  return d;
}

float gao(vec3 p, vec3 n, float s) {

  return clamp(map(p+n*s)/s, 0.,1.);
}

float pulse;

void cam(inout vec3 p) {

  float t=time * 0.3 + curve(pulse, 0.6)*27.3;
  p.yz *= rot(t*0.7);
  p.xz *= rot(t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  time = mod(iTime*globalSpeed, 300.);
  pulse = floor(time*0.5);

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  float alpha = 1.0;

  #if USE_POSTFX
      float gd = 10.+curve(time, 0.1)*50.;
      float se = curve(time - length(uv)*0.5, 0.15);
      if(se>0.6) {
        //uv.x += floor(uv.y*gd)*time*0.1;
        vec2 tmp = smoothstep(0.8,0.9,fract(uv*gd));
        uv=floor(uv*gd)/gd;

        alpha = mix(1.,max(tmp.x,tmp.y)*.5+.5,se);
      }
  #endif

  #if USE_SYMMETRY
      uv.y += pow(curve(time, 0.3),2.0)*0.2;

      uv *= 1.0+curve(time - length(uv), 0.3)*.3;

      if(curve(time, 2.0)>0.6) uv=abs(uv);
  #endif


  vec3 s=vec3(0,0,-6. - curve(pulse, 0.5)*10.);
  s.x += (curve(pulse, 0.4)-.5)*8.;
  s.y += (curve(pulse, 0.7)-.5)*8.;
  vec3 r=normalize(vec3(-uv, 1. + sin(time*curve(pulse, 0.3)*2.0)*.5));

  cam(s);
  cam(r);

  vec3 p=s;
  for(int i=0; i<100; ++i) {
    float d=map(p);
    if(d<0.001) break;
    if(d>100.0) break;
    p+=r*d;    
  }

  float fog=1.-clamp(length(p-s)/100., 0.,1.);

  vec3 col=vec3(0);

  col += aco*.9;

  vec2 off=vec2(0.01,0);
  vec3 n=normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));

  float ao = gao(p,n,0.3);
  ao *= gao(p,n,0.6)*.5+.5;
  ao *= gao(p,n,1.2)*.5+.5;

  for(float i=1.; i<30.; ++i) {
    float dd=0.1*i;
    col += map(p+r*dd) * fog * 0.06 * vec3(dd+0.3,0.6,0.9+dd*.3) * ao;
  }

  float fre=pow(1.-abs(dot(n,r)), 3.);
  col += fre * vec3(1,.5,.7) * 1.4 * (0.5-0.5*n.y) * ao * fog;

  col += (1.-fog) * mix(vec3(0), vec3(0.5,0.6,0.7), pow(abs(r.x), 4.)) * 2.;
  col += (1.-fog) * mix(vec3(0), vec3(0.7,0.6,0.3), pow(abs(r.z), 4.)) * 2.;

  #if USE_RAINBOW
    float t5 = time*.3;
    col.xz *= rot(t5);
    col.xy *= rot(t5*.7);
    col=abs(col);
  #endif

  col *= alpha;

  vec3 bcol = col;

  #if USE_LINES
      if(abs(length(uv)-0.3-curve(floor(time*5.)*.4, 0.4))<0.05) col = 1.-col;
      if(abs(length(uv)-0.4-curve(floor(time*5.)*.4, 0.5))<0.05) col = 1.-col;

      uv *= 0.4;
      for(int i=0; i<3; ++i) {

        uv *= rot(0.7+time*0.2);
        uv = abs(uv)-curve(time, 0.3)*0.3;
        uv *= 1.2;

        if(abs(uv.y)<0.003) col = 1.-col;
      }
  #endif

  //col = mix(col, bcol, clamp(curve(time, 0.3)*2,0,1));

  col *= 1.2-length(uv);


  fragColor = vec4(col, 1);
}

//shapes primitives from art of code https://www.youtube.com/watch?v=Ff0jJyyiVyw 
//sofShadows from iquilez https://iquilezles.org/www/articles/rmshadows/rmshadows.htm

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define MIN_DIST 0.005
#define AA 2
#define eAA 0.001
#define wAA 1.0
#define PI 3.14159265359


struct Material{

    vec3 color;
    float shininess;
    float kd;
    float ks;
    float ka;
    float kf;
    float nd;
};

Material mats[2];
vec3 colors[10];

Material i_material(vec3 color,float ka, float kd, float ks, float shininess, float kf, float nd){

    Material m;
    m.color = color;
    m.shininess = shininess;
    m.kd = kd;
    m.ks = ks;
    m.ka = ka;
    m.kf = kf;
    m.nd = nd;
    return m;
}

mat2 Rot2(float angle){

    return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));

}

mat3 RotX(float angle){

    return mat3(1.0, 		0.0,		0.0,
                0.0, cos(angle),-sin(angle),
                0.0, sin(angle), cos(angle));

}

mat3 RotY(float angle){

    return mat3(cos(angle), 0.0,sin(angle),
                0.0,       1.0,0.0       ,
                -sin(angle),0.0,cos(angle));

}

mat3 RotZ(float angle){

    return mat3(cos(angle),-sin(angle),0.0,
                sin(angle), cos(angle),0.0,
                	   0.0,        0.0,1.0);

}

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 min_v3(vec3 a, vec3 b){
 	return(a.x<b.x)?a:b;   
}
vec3 max_v3(vec3 a, vec3 b){
 	return(a.x>b.x)?a:b;   
}

float hash21(vec2 st){

    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);

}

float hash31(vec3 st){
 	return hash21(vec2(hash21(st.xz),hash21(vec2(hash21(st.xy),hash21(st.yz)))));   
}

vec3 hash33(vec3 p){

    return vec3(hash21(p.xy),hash21(p.yz),hash21(p.xz));
}



float sdSphere(vec3 p, vec4 s){
    return length(p-s.xyz)-s.w;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;

    return length(p-c)-r;
}

float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    //t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}

float sdBox(vec3 p, vec3 s) {
	return length(max(abs(p)-s, 0.));
}

float sdTorus(vec3 p, vec2 r) {
	float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

vec3 tripulant(vec3 pos, vec3 translate,int index){
    //pos = RotY(-PI*0.5)*pos;
    pos-=translate;
    vec3 p = pos;
    //p.z*=1.25;
    p.x+=((p.y-1.)*sign(p.x))*0.05;
    vec3 d = vec3(sdCapsule(p,vec3(0,1.5,2),vec3(0,-0.5,2),1.6),0,index);
    d.x = smin(d.x,sdBox(pos-vec3(0,0.4,3.55),vec3(1.9,1.7,0.4))-0.1,0.4);
    vec3 p2 = p;
    p2.x = abs(p2.x);
    d.x = smin(d.x,sdCylinder(p2,vec3(0.8,-1.05,2),vec3(0.85,-2.75,2),0.3)-0.3,0.25);
    d = min_v3(d,vec3(sdBox(p+vec3(0,-1,-0.5-p.x*p.x*0.3),vec3(0.875,0.375,0.125))-0.125,1,0));
    return d;
}

vec3 space_tripulant(vec3 pos, vec3 translate,int index){

    pos-=translate;
    pos.y+=sin(iTime)*0.5;
    pos=RotY(-PI*0.25)*pos;
    pos=RotX(-0.4)*pos;
    vec3 p = pos;
    p.x+=((p.y-1.)*sign(p.x))*0.05;
    vec3 d = vec3(sdCapsule(p,vec3(0,1.5,2),vec3(0,-0.5,2),1.6),0,index);
    d.x = smin(d.x,sdBox(pos-vec3(0,0.4,3.55),vec3(1.9,1.7,0.4))-0.1,0.4);
    vec3 p2 = p;
    p2.x = abs(p2.x);
    d.x = smin(d.x,sdCylinder(p2,vec3(0.8,-1.25,2),vec3(0.85,-1.75,0.0),0.3)-0.3,0.25);
    d = min_v3(d,vec3(sdBox(p+vec3(0,-1,-0.5-p.x*p.x*0.3),vec3(0.875,0.375,0.125))-0.125,1,0));
    return d;
}

vec3 corpse(vec3 pos, vec3 translate){

    pos-=translate;
    pos = RotY(0.7)*pos;
    pos = RotX(-PI*0.1)*pos;
    vec3 p = pos;
    p.x+=((p.y-1.)*sign(p.x))*0.05;
    vec3 d = vec3(sdCapsule(p,vec3(0,1.5,2),vec3(0,-0.5,2),1.6),0,1);
    d = max_v3(d,vec3(p.y,0.,3));
    d = min_v3(d,vec3(sdTorus(p-vec3(0,0,2),vec2(1.4,0.2)),0,1));

    vec3 p2 = p;
    p2.x = abs(p2.x);
    d.x = smin(d.x,sdCylinder(p2,vec3(0.8,-1.25,2),vec3(0.85,-1.75,0.0),0.3)-0.3,0.25);

    vec3 d2 = vec3(sdCapsule(p,vec3(0,0.5,2),vec3(0,0.1,2),0.25),0,0);
    d2.x = smin(d2.x,sdSphere(vec3(abs(p.x),p.yz),vec4(0.3,0.75,2,0.2)),0.5);
    d = min_v3(d,d2); 
    return d;

}

vec3 map(vec3 pos){
 	vec3 d = tripulant(pos,vec3(-2,0,-3),2);
    d = min_v3(d,tripulant(pos,vec3(3,0,0),3));
    d = min_v3(d,tripulant(pos,vec3(8,-0.25,-2),5));
    d = min_v3(d,space_tripulant(pos,vec3(1,5,7),4));
    d = min_v3(d,corpse(pos,vec3(-8.,-1.5,1)));
    d = min_v3(d,vec3(sdSphere(pos,vec4(0.,-103.,0.,100.)),0,25));

    return d;
}



vec3 raymarching(vec3 o, vec3 dir){

    float d = 0.0;
    vec3 t = vec3(0);

    for(int i = 0; i<MAX_STEPS;++i){
     	vec3 pos = o+dir*d;
        t = map(pos);
        d+=t.x;

        if(abs(t.x)<MIN_DIST || d>MAX_DIST)
            break;
    }

    t.x = d;
    return t;

}

vec3 getNormal(vec3 pos,float normal_desv){

    vec2 e = vec2(0.01,0.0);

    return normalize(map(pos).x-vec3(
    						map(pos-e.xyy).x,
        					map(pos-e.yxy).x,
        					map(pos-e.yyx).x
    						)+sin(hash33(pos)*PI)*normal_desv);

}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t).x;
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

float getLigth(vec3 pos,int index){

    Material m = mats[index];

    vec3 ligth  = vec3(0,2,-7);
    vec3 l = normalize(pos-ligth);
    vec3 N = getNormal(pos,m.nd);
    float dif = m.kd*clamp(dot(N,-l),0.0,1.0);
    float spec = m.ks*pow(clamp(dot(-l,reflect(normalize(pos),N)),0.0,1.0),m.shininess);
    float amb = m.ka*clamp(dot(N,normalize(vec3(0.0,0.0,-1.0))),0.0,1.0);
    float fresnel = m.kf*(1.0-clamp(dot(N,normalize(-pos)),0.0,1.0));
    float l_d = dif+spec+amb+fresnel;

    float fog = exp(-0.0125*pos.z*pos.z);
    float shadow = clamp(softshadow(pos+N*0.01,-l,0.,20.,4.)+0.25,0.0,1.0);
	return l_d*fog*shadow;    
}

vec3 getColor(vec3 origin, vec3 dir){

    vec3 col = vec3(0.0);


    vec3 r_pos = raymarching(origin,dir);

    vec3 p = origin+dir*r_pos.x;
	int mat_index = int(r_pos.y);
    int color_index = int(r_pos.z);
    vec3 _color;
    if(color_index<20){
        _color = colors[color_index];
    }else{
        _color = texture(iChannel0,p.xz*0.15).xyz;
    }

    if(r_pos.x>90.){
        float c = hash31(dir);
     	col=vec3(pow(c,200.0));   
    }else{
    	col = _color*getLigth(p,mat_index);
    }


    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    float t = 0.0;//iTime;

    vec2 uv = fragCoord.xy / iResolution.xy; // 0 <> 1
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float distan = 15.0;
    float angle = PI*0.92;//iTime;
    float _x = sin(angle)*distan;
    float _z = cos(angle)*distan;
    vec3 origin = vec3(_x,1.5,_z);
    vec3 lookat = vec3(0);

    float zoom = 1.;

    vec3 f = normalize(lookat-origin);
    vec3 r = cross(vec3(0., 1., 0.), f);
    vec3 u = cross(f, r);

    vec3 c = origin + f*zoom;
    vec3 i = c + uv.x*r + uv.y*u;
    vec3 dir = normalize(i-origin);

    vec3 col = vec3(0.0);

    /*********************
	*       MATERIALS    *
	*********************/

    mats[0] = i_material(vec3(1,1,1),0.2,0.5,0.15,3.0,0.015,0.0015);
    mats[1] = i_material(vec3(1,1,1),0.2,0.45,0.95,4.0,0.25,0.0); 

    /********************
	*      COLORS       *
	********************/
    colors[0] = vec3(1,1,1);
    colors[1] = vec3(0,1,0);
    colors[2] = vec3(0,0,1);
    colors[3] = vec3(1,0,0);
    colors[4] = vec3(0,1,1);
    colors[5] = vec3(1,1,0);


	col = (getColor(origin, dir)*wAA)/(float(AA)+wAA);

    vec3 e = vec3(eAA,-eAA,0.0);
    vec3 e2 = vec3(eAA,-eAA,0.0)*0.7071;
    vec3 sampling[8];
    sampling[0]=e.xzz;
    sampling[1]=e.zyz;
    sampling[2]=e.yzz;
    sampling[3]=e.zxz;
    sampling[4]=e2.xxz;
    sampling[5]=e2.yxz;
    sampling[6]=e2.xyz;
    sampling[7]=e2.yyz;

    for(int i = 0; i<AA;++i){


        vec3 dir = normalize(dir+sampling[i]);

        col += getColor(origin,dir)/(float(AA)+wAA);

    }

    fragColor = vec4(col,1.0);
}

// 'It' Tribute
//
// Inspired by the 'drain' scene from the Stephen King 'It' movie.
// I'd love to come back to this in the future when a) my modelling
// skills are improved, and b) my GPU is faster. :o)
//
// Concrete texture reused from my earlier shader:
// https://www.shadertoy.com/view/WllfzB
