#define MIN_DIST 0.0015
#define MAX_DIST 64.0

//#define AA  // Enable this line if your GPU can take it!

vec3 rayDir;
float faceVisibility, eyeWonk, headLunge;

struct MarchData {
    float d;
    vec3 mat;        // RGB
    float specPower; // 0: None, 30.0: Shiny
    float opacity;   // 1.0: Solid
};

// Thanks Shane - https://www.shadertoy.com/view/lstGRB
float noise(vec3 p) {
	const vec3 s = vec3(7.0, 157.0, 113.0);
	vec3 ip = floor(p);
    vec4 h = vec4(0.0, s.yz, s.y + s.z) + dot(ip, s);
	p -= ip;

    h = mix(fract(sin(h) * 43758.5453), fract(sin(h + s.x) * 43758.5453), p.x);

    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float fbm(vec3 p) {
    return noise(p) * noise(p + 0.2) * noise(p + 0.66);
}

mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

float opRep(float p, float c) {
    float c2 = c * 0.5;
    return mod(p + c2, c) - c2;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdCappedCylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xy), p.z)) - vec2(h, r);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdCapsule(vec3 p, float h, float r) {
	p.x -= clamp(p.x, 0.0, h);
	return length(p) - r;
}

float sdPuddle(vec3 p, float r) {
    r *= 0.9 + 0.1 * cos(7.0 * atan(p.x, p.y));
    return sdCappedCylinder(p, r, 0.05);
}

float sdEye(vec3 p, inout vec3 mat, float openness, float maxEyeWonk) {
    const float r = 0.5;
    float d = length(p) - r;
    vec3 pp = p;
    pp.yz *= rot(0.181);
    float frontAngle = abs(atan(pp.y, pp.z)) / 3.1415;

    const float pupilSize = 0.1;
    const float irisSize = 0.3;

    vec3 eyeRay = rayDir;
    eyeRay.xz *= rot(0.75 * eyeWonk * maxEyeWonk);
    vec3 pupilCenter = eyeRay * -r;
    vec3 pupilToP = p - pupilCenter;
    float distFromFocus = length(pupilToP);

    // Corners.
    mat = mix(mat, vec3(0.3, 0.01, 0.05), smoothstep(0.8, 1.0, abs(p.x / r)));

    // Iris color.
    const vec3 irisCol = vec3(0.068, 0.211, 0.18);
    float radialTint = sin(atan(pupilToP.y, pupilToP.x) * 23.0) * 0.3 + 1.7 - irisSize * distFromFocus;
    mat *= smoothstep(0.0, 0.05, distFromFocus - irisSize); // Gradient
    mat = mix(mat,
              irisCol * radialTint, // Radial lines
              1.0 - min(distFromFocus / irisSize, 1.0)); // Solid

    // Iris outer.
    mat *= mix(0.17, 1.0, smoothstep(0.0, 0.04, abs(distFromFocus - irisSize)));

    // Pupil.
    mat = mix(vec3(0.01), mat, smoothstep(0.0, 0.06, distFromFocus - pupilSize));

    // Open/close eyelids.
    mat *= smoothstep(0.1, 0.0, mix(1.1, 0.91, openness) - frontAngle);
    return d;
}

MarchData sdHead(vec3 p, vec3 eyeMat) {
    p -= vec3(0.0, 1.2, -2.0);

    // Eyes.
    float openness = faceVisibility;
    float blink = clamp(iTime - 32.0, 0.0, 0.4) / 0.4;
    openness *= pow(abs(cos(blink * 3.1415)), 2.0);

    vec3 eyeMat1 = eyeMat;
    MarchData result = MarchData(sdEye(p - vec3(-0.9, 0.0, 0.0), eyeMat1, openness, 1.0), eyeMat1, 30.0, 1.0);

    float eye2 = sdEye(p - vec3(0.9, 0.0, 0.0), eyeMat, openness, 0.0);
    if (eye2 < result.d) {
        result.d = eye2;
        result.mat = eyeMat;
    }

    // Forehead.
    float ox = p.x;
    p.x = abs(p.x);
    float brow = (1.0 - cos(p.x * 2.9)) * 0.1;
    float head = sdCapsule(p - vec3(0.0, 0.37 + brow, 0.0), 1.2, 0.2);

    vec3 pp = p.yxz;
    pp.xz *= rot(0.6);
    head = smin(head, sdCapsule(pp - vec3(-0.36, 0.0, 0.2), 0.5, 0.2), 0.5);
    if (head < result.d) {
        result.d = head;
        float noseDim = mix(1.0, smoothstep(-0.4, 0.5, ox), clamp(0.3 - p.y, 0.0, 1.0));
        result.mat = vec3(0.004 * faceVisibility * noseDim);
        result.specPower = 1e7;
    }

    return result;
}

float getConcreteMaterial(vec3 p, out vec3 mat) {
    // Mix a couple of shades of grey.
    float baseColor = smoothstep(0.0, 0.5, noise(p));
    mat = mix(vec3(0.18, 0.17, 0.17), vec3(0.20, 0.19, 0.19), baseColor);

    // Surface roughness.
    float rough = noise(p * 60.0) * 0.005 // Base
         		  + step(0.2, noise(p * 26.666)) * 0.0033; // Pits/dents.
    mat += rough * 24.0;

    return rough;
}

float getAsphaltMaterial(vec3 p, out vec3 mat) {
    // Surface roughness.
    float rough = abs(fbm(p * 10.0) - 0.5) * 0.1;
    mat = vec3(rough * 0.3);
    return rough;
}

vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    return normalize(forward + right * uv.x + up * uv.y);
}

// Once it has been hit, we exclude the water from the scene.
bool refracted = false;

// Map the scene using SDF functions.
MarchData map(vec3 p) {
    MarchData result;
    result.specPower = 1000.0;
    result.opacity = 1.0;

    // Concrete texture.
    vec3 concreteMat;
    float concreteDisplacement = getConcreteMaterial(p, concreteMat);
    result.mat = concreteMat;

    // Curb.
    vec3 rp = p;
    rp.x = opRep(rp.x, 12.2);
    const vec3 curbSize = vec3(6.0, 2.0, 1.0);
    float curb = max(sdBox(rp, curbSize), 0.25 - (sdBox(p, curbSize) - 0.1));

    // Paving.
    rp.x = opRep(p.x + 2.3, 9.2);
    float slab = sdBox(rp - vec3(0.0, 1.8, 6.0), vec3(4.5, 0.2, 4.5));
    rp.x = opRep(p.x - 2.3, 9.2);
    slab = min(slab, sdBox(rp - vec3(0.0, 1.8, 15.3), vec3(4.5, 0.2, 4.5)));

    if (slab < curb) {
        // Black out underside of paving slabs.
        result.mat *= rayDir.y < 0.0 ? 1.0 : 0.0;
    }

	result.d = min(slab, curb) - 0.1 - concreteDisplacement;

    // Drain.
    p.y += curbSize.y;
    float drainBox = max(
        sdBox(p, curbSize * vec3(1.025, 2.0, 1.0)) - 0.1,
        -sdBox(p, curbSize * vec3(0.9, 1.8, 2.0)));
    if (drainBox < result.d) {
    	result.d = drainBox;
        result.mat *= vec3(.439, .157, .035);
    }

    // Road.
    p.yz += vec2(0.5, 10.7);
    vec3 mm;
    float road = sdBox(p, vec3(100.0, 0.5 + getAsphaltMaterial(p, mm), 10.0));
    if (road < result.d) {
        result.mat = mm;
        result.d = road;
        result.specPower = 1.0;
    }

    // Head.
    vec3 headMat;
    vec3 pp = p - vec3(0.0, 0.6 + sin(headLunge * 6.282) * 0.1, -1.0) * headLunge * 2.0;
    MarchData head = sdHead(pp - vec3(0.0, 0.0, 15.0), concreteMat * 1.3);
    if (head.d < result.d)
        result = head;

    if (!refracted) {
        // Puddles.
        vec3 puddleP = p.xzy - vec3(6.0, 6.0, 0.5);
        float puddles = sdPuddle(puddleP, 1.2);
        puddles = min(puddles, sdPuddle(puddleP - vec3(2.0, 0.0, 0.0), 2.0));
        puddles = min(puddles, sdPuddle(puddleP - vec3(5.0, 0.0, 0.0), 1.5));

        // Stream.
        float waterClip = sdBox(p - vec3(0.0, 0.0, 61.0), vec3(100.0, 100.0, 50.0));
        p.xz += vec2(46.25, -151.08);
        float edgeWave = sin(p.x - iTime * 5.0) * 0.2;
        float radius = 150.0 + edgeWave;
        float prad = length(p.xz);
        float water = max(prad - radius, abs(p.y - 0.6));
        vec3 wp = p;
        wp.xz *= rot(iTime * 0.1) * 0.05;

        float wt = iTime * 8.0;
        float dis = (sin(p.x - wt) + 0.5 * sin((p.x - wt * 0.5) * 1.8)) * 0.25 + 0.5;
        dis += texture(iChannel0, wp.xz).r * 0.8;
        dis *= (radius - prad) * 0.2;
        water -= dis;
        water = max(water, -waterClip);
        water = min(water, puddles);

        if (water < result.d) {
            result.d = water;
            result.mat = vec3(0.25, 0.3, 0.4);
            result.specPower = 3.0;
            result.opacity = 0.2;
        }
    }

    return result;
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.005773;
    return normalize(e.xyy * map(p + e.xyy).d + 
					 e.yyx * map(p + e.yyx).d + 
					 e.yxy * map(p + e.yxy).d + 
					 e.xxx * map(p + e.xxx).d);
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
    return map(p + h * n).d / h;
}

/**********************************************************************************/

vec3 vignette(vec3 col, vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= 0.5 + 0.5 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.4);
    return col;
}

vec3 applyLighting(vec3 p, vec3 rd, MarchData data) {
    vec3 sunCol = vec3(2.0, 1.6, 1.4);
    vec3 sunPos = vec3(6.0, 3.0, -10.0);
    vec3 sunDir = normalize(sunPos - p);
    vec3 n = calcNormal(p);

    // Ambient occlusion (exluding water).
    bool b = refracted;
    refracted = true;
    float amb = dot(vec3(ao(p, n, 0.2), ao(p, n, 0.5), ao(p, n, 2.0)), vec3(0.2, 0.3, 0.5));
    refracted = b;

    // Diffuse color.
    float diff = max(0.0, dot(sunDir, n));

    // Primary light.
    vec3 lig = diff * sunCol * mix(amb, 1.0, iMouse.x/640.0);

    // Specular.
    float spe = pow(max(0.0, dot(rd, reflect(normalize(sunPos - p), n))), data.specPower);
    lig += smoothstep(0.5, 1.0, spe) * 10.0;

    float fre = 1.0 + dot(rd, n);
    float fog = exp(-length(p) * 0.14);
    return mix(data.mat * lig, vec3(0.0), fre) * fog;
}

vec3 getSceneColor(vec3 ro, vec3 rd) {
    // Raymarch.
    vec3 p;

    float d = 0.01;
    float opacity = 1.0;
    MarchData h;
    vec3 col = vec3(0.0);
    for (float steps = 0.0; steps < 90.0; steps++) {
        p = ro + rd * d;
        h = map(p);

        if (abs(h.d) < MIN_DIST) {
            if (h.opacity > 0.9999 || refracted)
            	break; // We've hit a solid surface - Stop.
            opacity = max(0.0, opacity - h.opacity);
            col = applyLighting(p, rd, h) * h.opacity;
            refracted = true;
            rd = refract(rd, calcNormal(p), 0.95);
        }

        if (d > MAX_DIST)
            return vec3(0.0); // Distance limit reached - Stop.

        d += h.d; // No hit, so keep marching.
    }

    // Lighting.
    return applyLighting(p, rd, h) * opacity + col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Camera.
    const float stageCount = 3.0;
    float ft = smoothstep(0.0, 1.0, fract(iTime / 12.0));
    float phase = mod(floor(iTime / 12.0), stageCount);
    float dim = 1.0 - pow(abs(cos(iTime * 3.141 / 12.0)), 100.0);

    vec3 ro, lookAt = vec3(0.0, 0.0, 0.0);
    faceVisibility = 0.0;
    headLunge = 0.0;

    if (phase == 0.0) {
        ro = vec3(mix(-30.0, -9.0, ft), 7.0, -15.0);
        lookAt = ro * vec3(1.1, 0.0, 0.0);
    } else if (phase == 1.0) {
        ro = mix(vec3(20.0, 8.0, -20), vec3(0.0, 5.0, -10.0), ft);
        faceVisibility = min(1.0, (ft - 0.3) * 2.0);
        eyeWonk = 1.0;
    } else if (phase == 2.0) {
        float progress = min(1.0, pow(ft * 4.0, 4.0));
        eyeWonk = 1.0 - smoothstep(0.4, 1.0, (ft - 0.2) * 2.0);
        faceVisibility = 1.0 + min(sin(eyeWonk * 3.141), 0.5);
        headLunge = ft;
        ro = mix(vec3(1.0, 10.0, -20), vec3(-1.0, 0.85, -5.0), progress);
    }

    vec3 col = vec3(0.0);
#ifdef AA
    for (float dx = 0.0; dx <= 1.0; dx++) {
        for (float dy = 0.0; dy <= 1.0; dy++) {
            vec2 coord = fragCoord + vec2(dx, dy) * 0.5;
#else
            vec2 coord = fragCoord;
#endif
            vec2 uv = (coord - 0.5 * iResolution.xy) / iResolution.y;

            rayDir = getRayDir(ro, lookAt, uv);
            col += getSceneColor(ro, rayDir);
#ifdef AA
            refracted = false;
        }
    }
    col /= 4.0;
#endif

    // Output to screen.
    col = vignette(pow(col * dim, vec3(0.4545)), fragCoord);
    fragColor = vec4(col, 1.0);
}

/**
 * Simulation of a colored cube moving on a checkerboard.
 *
 * The cube move one step forward, then one step rightward, then one step forward, ...
 *
 * Although the cube seems to rotate infinitely, it only rotates by 90Â° (either forward or
 * rightward) during [0, PI] seconds then go back to its initial rotation. The cube always stays
 * at position (0, 0, 0) but move a little upward during its rotation.
 *
 * The floor moves one tile backward or leftward during [0, PI] seconds then go back to its
 * initial position.
 *
 * So we have an infinite loop where everything move during [0, PI] seconds then go back to
 * the initial state.
 *
 *
 * The forward movement follows a periodic function looking like:
 *
 *     /         /         /
 *    /         /         /
 *   /         /         /
 *  /         /         /
 * /    _____/    _____/
 * 0    PI   2PI  3PI  4PI
 *
 *
 * The rightward movement follows a periodic function looking like:
 *
 *          /         /         /
 *         /         /         /
 *        /         /         /
 *       /         /         /
 * _____/    _____/    _____/
 * 0    PI   2PI  3PI  4PI
 *
 * So every PI seconds, the cube is moving either forward or rightward.
 *
 * To create the illusion that the cube is moving one tile forward and one tile rightward each
 * step, many equations include an offset of 1.0 if sin(time) > 0.0. You can set ENABLE_FLOOR_OFFSET
 * to 0.0 to disable this offset for the floor and you will see that its position is reset each time.
 */
#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 2   // make this 2 or 3 for antialiasing
#endif
#define SPEED 1.0 // how fast is the simulation
#define ENABLE_FLOOR_OFFSET 1.0
#define PI 3.1415926535897932384626433832795
#define PI2 6.28318530718

//------------------------------------------------------------------

// possible shapes
#define SHAPE_VOID 0.0
#define SHAPE_FLOOR 1.0
#define SHAPE_CUBEFRAME 2.0
// cube faces are arranged in the order they will touch the floor
#define SHAPE_CUBEFACEDOWN 3.0
#define SHAPE_CUBEFACEFRONT 4.0
#define SHAPE_CUBEFACERIGHT 5.0
#define SHAPE_CUBEFACEUP 6.0
#define SHAPE_CUBEFACEBACK 7.0
#define SHAPE_CUBEFACELEFT 8.0

// intersected shape
struct Shape
{        
    float d; // distance
    float t; // type of shape
    vec3 c; // color
};

// colors of each face of the cube
const vec3 CubeFaces[6] = vec3[6](
    vec3(1.0, 0.65, 0.0), // yellow, down
    vec3(0.14, 1.0, 0.0), // green, front
    vec3(1.0, 0.0, 0.0), // red, right
    vec3(1.0, 1.0, 1.0), // white, up
    vec3(0.0, 0.55, 1.0), // blue, back
    vec3(1.0, 0.31, 0.0) // orange, left
);

//------------------------------------------------------------------

// create a quaternion
vec4 quat(in vec3 v, in float a)
{
    return vec4(v * sin(a / 2.0), cos(a / 2.0));
}

vec4 quat_inv(in vec4 q)
{
    return vec4(-q.xyz, q.w);
}

vec4 p2q(in vec3 p)
{
    return vec4(p, 0);
}

vec4 q_mul(in vec4 q1, in vec4 q2)
{
    return vec4(q1.w*q2.x + q1.x*q2.w + q1.y*q2.z - q1.z*q2.y, 
                q1.w*q2.y - q1.x*q2.z + q1.y*q2.w + q1.z*q2.x, 
                q1.w*q2.z + q1.x*q2.y - q1.y*q2.x + q1.z*q2.w, 
                q1.w*q2.w - q1.x*q2.x - q1.y*q2.y - q1.z*q2.z);
}

vec3 rotate(in vec3 p, in vec4 q)
{
    return q_mul(q_mul(q, p2q(p)), quat_inv(q)).xyz;
}

Shape opU( Shape d1, Shape d2 )
{
    if (d1.d<d2.d) {
        return d1;
    }
    return d2;
}

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }


// test intersection with a box https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

// test intersection with the black frame of the cube
float sdCubeFrame(vec3 p, vec3 b)
{    
    return opSubtraction(
        sdBox(p, b * vec3(0.9, 0.9, 1.1)),
        opSubtraction(
            sdBox(p, b * vec3(0.9, 1.1, 0.9)),
            opSubtraction(
                sdBox(p, b * vec3(1.1, 0.9, 0.9)),
                sdBox(p, b)
            )
        )
    );
}

// test intersection with the colored cube
Shape sdColoredCube( in vec3 pos )
{
    Shape res = Shape( 1e10, SHAPE_VOID, vec3(0.0, 0.0, 0.0) );

    {
      res = opU( res, Shape( sdCubeFrame( pos, vec3(0.25, 0.25, 0.25) ), SHAPE_CUBEFRAME, vec3(0.0, 0.0, 0.0 )) );
      res = opU( res, Shape( sdBox( pos+vec3(0.0, 0.24, 0.0), vec3(0.225, 0.01, 0.225)), SHAPE_CUBEFACEDOWN, vec3(0.0, 0.0, 0.0 ) )); // yellow, down
      res = opU( res, Shape( sdBox( pos+vec3(-0.24, 0.0, 0.0), vec3(0.01, 0.225, 0.225)), SHAPE_CUBEFACEFRONT, vec3(0.0, 0.0, 0.0)) ); // green, front
      res = opU( res, Shape( sdBox( pos+vec3(0.0, 0.0, 0.24), vec3(0.225, 0.225, 0.01)), SHAPE_CUBEFACERIGHT, vec3(0.0, 0.0, 0.0 ) )); // red, right
      res = opU( res, Shape( sdBox( pos+vec3(0.0, 0.0, -0.24), vec3(0.225, 0.225, 0.01)), SHAPE_CUBEFACELEFT, vec3(0.0, 0.0, 0.0 ) )); // orange, left
      res = opU( res, Shape( sdBox( pos+vec3(0.24, 0.0, 0.0), vec3(0.01, 0.225, 0.225)), SHAPE_CUBEFACEBACK, vec3(0.0, 0.0, 0.0 ) )); // blue, back
      res = opU( res, Shape( sdBox( pos+vec3(0.0, -0.24, 0.0), vec3(0.225, 0.01, 0.225)), SHAPE_CUBEFACEUP, vec3(0.0, 0.0, 0.0 ) )); // white, up
    }

    return res;
}

//------------------------------------------------------------------

/**
 * calculate the forward movement
 * 
 *     /         /         /
 *    /         /         /
 *   /         /         /
 *  /         /         /
 * /    _____/    _____/
 * 0    PI   2PI  3PI  4PI
 */
float advanceX(in float time)
{
    return mod(min(time - floor(time / PI2) * PI, PI * ceil(time / PI2)) * ceil(sin(time)), PI) / 2.0;
}

/**
 * same as advanceX but without the reset every [PI, 2PI]
 *
 * This is used to compute the forward movement when time == PI without having the
 * reset to 0.
 */
float advanceX2(in float time)
{
    return (time - floor(time / PI2) * PI) / 2.0;
}

/**
 * calculate the rightward movement
 * 
 *          /         /         /
 *         /         /         /
 *        /         /         /
 *       /         /         /
 * _____/    _____/    _____/
 * 0    PI   2PI  3PI  4PI
 */
float advanceZ(in float time)
{
    return mod(min(time - PI - floor((time - PI) / PI2) * PI, PI * ceil((time- PI) / PI2)) * (1.0 - ceil(sin(time))), PI) / 2.0;
}

// test intersection with the scene
Shape map( in float time, in vec3 pos )
{
    Shape res = Shape( 1e10, SHAPE_VOID, vec3(0.0, 0.0, 0.0) );

    {
        pos += vec3(0.0, -0.25, 0.0);
        pos += vec3(0.0, -0.10, 0.0) * abs(sin(time));
    	pos = rotate(pos, q_mul(
            quat(vec3(0.0, 0.0, 1.0), advanceX(time)),
        	quat(vec3(1.0, 0.0, 0.0), advanceZ(time))
       	));
    	res = opU( res, sdColoredCube(pos) );
    }

    return res;
}

//------------------------------------------------------------------

#define ZERO (min(iFrame,0))

// http://iquilezles.org/www/articles/boxfunctions/boxfunctions.htm
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
	return vec2( max( max( t1.x, t1.y ), t1.z ),
	             min( min( t2.x, t2.y ), t2.z ) );
}

Shape raycast( in float time, in vec3 ro, in vec3 rd )
{
    Shape res = Shape(-1.0,SHAPE_VOID,vec3(0.0,0.0, 0.0));

    float tmin = 1.0;
    float tmax = 20.0;

    // raytrace floor plane
    float tp1 = (0.0-ro.y)/rd.y;
    if( tp1>0.0 )
    {
        tmax = min( tmax, tp1 );
        res = Shape( tp1, SHAPE_FLOOR, vec3(1.0, 1.0, 1.0) );
    }
    //else return res;

    // raymarch primitives   
    vec2 tb = iBox( ro-vec3(0.0,0.4,-0.5), rd, vec3(2.5,0.41,3.0) );
    if( tb.x<tb.y && tb.y>0.0 && tb.x<tmax)
    {
        //return vec2(tb.x,2.0);
        tmin = max(tb.x,tmin);
        tmax = min(tb.y,tmax);

        float t = tmin;
        for( int i=0; i<70 && t<tmax; i++ )
        {
            Shape h = map( time, ro+rd*t );
            if( abs(h.d)<(0.0001*t) )
            { 
                res = Shape(t,h.t, h.c); 
                break;
            }
            t += h.d;
        }
    }

    return res;
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in float time, in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (0.8-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<24; i++ )
    {
		float h = map( time, ro + rd*t ).d;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.2 );
        if( res<0.004 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in float time, in vec3 pos )
{
#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(time, pos+0.0005*e).d;
      //if( n.x+n.y+n.z>100.0 ) break;
    }
    return normalize(n);
#endif    
}

float calcAO( in float time, in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map(time, pos + h*nor ).d;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox( in float time, in vec2 p, in vec2 dpdx, in vec2 dpdy )
{
    // filter kernel
    vec2 w = abs(dpdx)+abs(dpdy) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}

vec3 render( in float time, in vec3 ro, in vec3 rd, in vec3 rdx, in vec3 rdy )
{ 
    // background
    vec3 col = vec3(0.7, 0.7, 0.9) - max(rd.y,0.0)*0.3;

    // raycast scene
    Shape res = raycast(time, ro,rd);
    float t = res.d;
    if( res.t != SHAPE_VOID )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = (res.t == SHAPE_FLOOR) ? vec3(0.0,1.0,0.0) : calcNormal(time, pos );
        vec3 ref = reflect( rd, nor );

        // material        
        col = res.c;
        float ks = 1.0;

        /**
         * Every PI time, our cube is moving forward or rightward.
         * 
         * shift is used to shift the first row to the left if == 1
         */
        float shift = ceil(sin(time)) * ENABLE_FLOOR_OFFSET;
        float face = floor(time / PI);
        if (res.t > SHAPE_CUBEFRAME)
        {
            if (res.t == SHAPE_CUBEFACEUP)
            {
            	col = CubeFaces[int(mod(face + 3.0, 6.0))];   
            }
            else if (res.t == SHAPE_CUBEFACEDOWN)
            {
            	col = CubeFaces[int(mod(face, 6.0))];   
            }
            else if (res.t == SHAPE_CUBEFACEFRONT)
            {
            	col = CubeFaces[int(mod(face + 1.0 + mod(face, 2.0), 6.0))];   
            }
            else if (res.t == SHAPE_CUBEFACEBACK)
            {// 1 - 3, 2-3; 3-5, 4-5, 5-1, 6-1
            	col = CubeFaces[int(mod(face + 4.0 + mod(face, 2.0), 6.0))];   
            }
            else if (res.t == SHAPE_CUBEFACELEFT)
            {
            	col = CubeFaces[int(mod(face + 4.0 + mod(face + 1.0, 2.0), 6.0))];   
            }
            else if (res.t == SHAPE_CUBEFACERIGHT)
            {
            	col = CubeFaces[int(mod(face + 1.0 + mod(face + 1.0, 2.0), 6.0))];   
            }
        }
        else if(res.t == SHAPE_FLOOR)
        {
            // project pixel footprint into the plane
            vec3 dpdx = ro.y*(rd/rd.y-rdx/rdx.y);
            vec3 dpdy = ro.y*(rd/rd.y-rdy/rdy.y);

            vec3 pos2 = pos;
       		pos2 += vec3(-0.25, 0.0, -0.25);
            pos2 += vec3(0.5, 0.0, 0.0) * advanceX(time) / advanceX2(PI);
       		pos2 += vec3(0.0, 0.0, -0.5) * advanceZ(time) / advanceX2(PI);
            float tileX = floor((pos2.x + 0.5) / 0.5f);
            float tileZ = floor(-pos2.z / 0.5f) + shift;
       		//pos += vec3(0.0, 0.0, -1.0) * time * max(ceil(-1.0*sin(time)), 0.0) / 5.0;
            float f = checkersGradBox( time, 1.7*pos.xz, 1.7*dpdx.xz, 2.5*dpdy.xz /* blur */);
            col = 0.15 + f*vec3(0.05); // luminance + f * contrast
            ks = 0.4; // godray ?
            float f1 = floor(mod((pos2.x + 0.5 * shift) / 0.5, 2.0));
            float f2 = clamp(floor(mod((pos2.z + 0.5 * f1) / 0.5, 2.0)), 0.15, 0.25);
            col = vec3(f2, f2, f2);
            /**
             * tileX <= 0.0 && tileZ <= 0.0 : only tiles behind the cube
             * abs(tileX - tileZ) <= 1.0 : only the diagonal
             * tileZ >= tileX : one tile up, then one left, then one up, ...
             */
            if (tileX <= 0.0 && tileZ <= 0.0 && abs(tileX - tileZ) <= 1.0 && tileZ >= tileX)
            {
                /**
                 * tileX + tileZ : initial color of the tile
                 * + face : shift by the current cube face that is down
                 * - shift : shift by one for odd rows
                 */
             	col = CubeFaces[int(mod(tileX + tileZ + face - shift, 6.0))];   
            }
        }

        // lighting
        float occ = calcAO( time, pos, nor );

		vec3 lin = vec3(0.0);

        // sun
        {
            vec3  lig = normalize( vec3(-0.5, 0.4, -0.6) );
            vec3  hal = normalize( lig-rd );
            float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
          //if( dif>0.0001 )
        	      dif *= calcSoftshadow(time, pos, lig, 0.02, 2.5 );
			float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0);
                  spe *= dif;
                  spe *= 0.04+0.96*pow(clamp(1.0-dot(hal,lig),0.0,1.0),5.0);
            lin += col*2.20*dif*vec3(1.30,1.00,0.70);
            lin +=     5.00*spe*vec3(1.30,1.00,0.70)*ks;
        }
        // sky
        {
            float dif = sqrt(clamp( 0.5+0.5*nor.y, 0.0, 1.0 ));
                  dif *= occ;
            float spe = smoothstep( -0.2, 0.2, ref.y );
                  spe *= dif;
                  spe *= 0.04+0.96*pow(clamp(1.0+dot(nor,rd),0.0,1.0), 5.0 );
          //if( spe>0.001 )
                  spe *= calcSoftshadow(time, pos, ref, 0.02, 2.5 );
            lin += col*0.60*dif*vec3(0.40,0.60,1.15);
            lin +=     2.00*spe*vec3(0.40,0.60,1.30)*ks;
        }
        // back
        {
        	float dif = clamp( dot( nor, normalize(vec3(0.5,0.0,0.6))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
                  dif *= occ;
        	lin += col*0.55*dif*vec3(0.25,0.25,0.25);
        }
        // sss
        {
            float dif = pow(clamp(1.0+dot(nor,rd),0.0,1.0),2.0);
                  dif *= occ;
        	lin += col*0.25*dif*vec3(1.00,1.00,1.00);
        }

		col = lin;

        col = mix( col, vec3(0.7,0.7,0.9), 1.0-exp( -0.0001*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime*4.0*SPEED;

    // camera	
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    vec3 ro = ta + vec3( 2.5*cos(0.1/**time*/), 1.3, 2.5*sin(0.1/**time */) );
    // camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // ray direction
        vec3 rd = ca * normalize( vec3(p,2.5) );

         // ray differentials
        vec2 px = (2.0*(fragCoord+vec2(1.0,0.0))-iResolution.xy)/iResolution.y;
        vec2 py = (2.0*(fragCoord+vec2(0.0,1.0))-iResolution.xy)/iResolution.y;
        vec3 rdx = ca * normalize( vec3(px,2.5) );
        vec3 rdy = ca * normalize( vec3(py,2.5) );

        // render	
        vec3 col = render( time, ro, rd, rdx, rdy );

        // gain
        // col = col*3.0/(2.5+col);

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    fragColor = vec4( tot, 1.0 );
}

float GetDist(vec3 p)  // simple way calculate the distance using ray marching
{
     vec4 s = vec4(0,1,6,1);   //the last 1 is for radius, the first three are the coordinates of the circle
     float sphereDist = length(p - s.xyz) - s.w;
     float planeDist = p.y;

     float d = min(sphereDist, planeDist);
     return d;

}

#define MAX_STEPS 100    
#define MAX_DIST 100.   
#define SURF_DIST .01

//float and int

float RayMarch(vec3 ro, vec3 rd)
{
 	float d0=0.;
    for(int i=0;i<MAX_STEPS;i++)
    {
       vec3 p = ro + rd*d0;
       float ds = GetDist(p);   
       d0+=ds;
       // to long or to small -> break
       if(d0>MAX_DIST||ds<SURF_DIST) break;

    }

    return d0;
}
vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(.01,0);  //delta e
    vec3 n= d - vec3(GetDist(p-e.xyy),
                   GetDist(p-e.yxy),
                   GetDist(p-e.yyx));

    return normalize(n);
}
float GetLight(vec3 p)
{
    vec3 lightPos = vec3(0,5,6);
    lightPos.xz += vec2(sin(iTime), cos(iTime));  //light moves around
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n,l),0.,1.);

    //utilize the idea of shadowmap to generate the light
    //if the distance can't reach the light then in shadow
    float d = RayMarch(p+n*SURF_DIST*2.,l); //from the plane a little bit
    if(d<length(lightPos - p)) dif*=.1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);
    vec3 ro = vec3(0,1,0);
    vec3 rd= normalize(vec3(uv.x,uv.y,1));  //rd equals every point on the screen

    float d = RayMarch(ro, rd);
    //d /=6.;
    vec3 p = ro+rd*d;
    float dif = GetLight(p);
    col = vec3(dif);

    //col = GetNormal(p);
    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 5.;


    vec2 gv = fract(uv * 1.);

    vec3 col = vec3(0.);

    for(float x = -1.; x <= 1.; x+=1.){
         for(float y = -1.; y <= 1.; y++){

            vec2 offs = vec2(x,y);
            vec2 center = floor(uv)+0.5 + offs;

    		float r = smoothstep(.3, 0.25, length(uv + 0.5 * sin(iTime*2.) - center));    
    		float g = smoothstep(.3, 0.25, length(uv + 1.0 * cos(iTime)    - center));    
    		float b = smoothstep(.3, 0.25, length(uv - center));

    		col += vec3(r, g, b);
         }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec4 MarkField(in vec2 position, in vec2 fragCoord)
{    
    float size = 50.0;

    float startX = position.x;
    float startY = position.y;

    float endX = startX;
    float endY = startY;

    vec2 center = vec2((startX+endX)/2.0, (startY+endY)/2.0);
    float maxDistance = distance(vec2(0.0,0.0), iResolution.xy);
    float currentDistance = distance(center, fragCoord)/2.0;

    float colorValue = currentDistance/maxDistance * cos(iTime) + 0.2;

    if (distance(fragCoord, center) > size){
    	return mix(vec4(colorValue, colorValue, colorValue, 1.0), vec4(sin(iTime), 2.0* sin(iTime), 3.0*sin(iTime), 1.0), 0.2);
    }
    else
    {
        return vec4(0.0, 0.0, 0.0, 1.0);
    }
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if(iMouse.xy == vec2(0,0)){
		fragColor = MarkField(iResolution.xy/2.0, fragCoord);
    }
    else{
        fragColor = MarkField(iMouse.xy, fragCoord);
    }

}

