//Thank you iquilez for some of the primitive distance functions!


const float AA = 2.0;
bool docolor = true;
int depth =8; // Levels of triangle
float pcolorchange = 0.25; // Probability of color change at a given level

const float PI = 3.14159265358979323846264;

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

uint randseed = 1U;
uint xorshift() {
  // Xorshift*32
  // From George Marsaglia: http://www.jstatsoft.org/v08/i14/paper
  randseed ^= randseed << 13;
  randseed ^= randseed >> 17;
  randseed ^= randseed << 5;
  return randseed;
}

float rand() {
  return float(xorshift())/pow(2.0,32.0);
}

vec3 getspherecolor(vec2 z) {

    z += (z.x + z.y) * -(3.0-sqrt(3.0))/6.0;

    uint index = uint(floor(dot(floor(z), vec2(5.,7.))));

    z = fract(z);
    if (z.x + z.y > 1.) z = 1. - z;
  float E = 10.0;
  float t = 0.5;
  uint epoch = uint((t+0.5*E+1.0)/(E+2.0));
  t = mod(t,E+2.0)-1.0;
  t = max(t,0.0);
  if (t > 0.5*E) t = max(0.5*E,t-1.0);
  t = min(t,E-t); // t < 0.5*E
  randseed = ihash(epoch^index+1U); // 0 hashes to 0!
  vec3 col = hsv2rgb(vec3(rand() + iTime * 0.1,0.8, 0.5 + 50.0 * z.x * z.y * (1. - z.x - z.y)));
  for (int i = 0; i < depth; i++) {
    int j;
    if  (z.x > 0.5) { j = 0; z = 2.0*vec2(z.x-0.5,z.y); }
    else if (z.y > 0.5) { j = 1; z = 2.0*vec2(z.x,z.y-0.5); }
    else if (z.x + z.y < 0.5) { j = 2; z = 2.0*z; }
    else { j = 3; z = 0.5-z; }
    index = (index << 2) + uint(j);
    randseed = ihash(epoch^index+1U); // 0 hashes to 0!
    if (rand() < pcolorchange) col = hsv2rgb(vec3(rand() + iTime * 0.1,0.8,0.5 + 50.0 * z.x * z.y * (1. - z.x - z.y)));
  }
  return docolor ? col : vec3(0.8);
}



const int MAX_PRIMARY_RAY_STEPS = 64; //decrease this number if it runs slow on your computer

vec2 rotate2d(vec2 v, float a) { 
	return vec2(v.x * cos(a) - v.y * sin(a), v.y * cos(a) + v.x * sin(a)); 
}

float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float distanceField(vec3 p) {
	return -sdTorus(p, vec2(4.0, 3.0));
}

vec3 castRay(vec3 pos, vec3 dir, float treshold) {
	for (int i = 0; i < MAX_PRIMARY_RAY_STEPS; i++) {
			float dist = distanceField(pos);
			//if (abs(dist) < treshold) break;
			pos += dist * dir;
	}
	return pos;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 mousePos = (iMouse / iResolution.xyxy) * 2.0 - 1.0;
	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
	vec3 cameraPos = vec3(0.0, 0.0, -3.8);

	vec3 cameraDir = vec3(0.0, 0.0, 0.5);
	vec3 planeU = vec3(1.0, 0.0, 0.0) * 0.8;
	vec3 planeV = vec3(0.0, iResolution.y / iResolution.x * 1.0, 0.0);
	vec3 rayDir = normalize(cameraDir + screenPos.x * planeU + screenPos.y * planeV);

	vec3 rayPos = castRay(cameraPos, rayDir, 0.01);

	float majorAngle = atan(rayPos.x, -rayPos.z) * 0.5 + iTime * 0.2;
	float minorAngle = atan(length(rayPos.xz) - 4.0, rayPos.y) + iTime * 0.1;
	vec2 coord = vec2(majorAngle, minorAngle) / PI * 0.5;
	vec3 color = getspherecolor(rotate2d(coord + 37., 37.));
	fragColor = vec4(color, 1.0);
}

/*

    Irreptile Triangle
    ------------------

    Recursively tiling this hexadrafter irreptile from George Sicherman
    https://userpages.monmouth.com/~colonel/drirrep/index.html

*/

// Polygon distance, iq https://www.shadertoy.com/view/wdBXRW

float dot2( in vec2 v ) { return dot(v,v); }
float cross2d( in vec2 v0, in vec2 v1) { return v0.x*v1.y - v0.y*v1.x; }

const int N = 6;

float sdPoly( in vec2[N] v, in vec2 p )
{
    const int num = v.length();
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for( int i=0, j=num-1; i<num; j=i, i++ )
    {
        // distance
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
        d = min( d, dot(b,b) );

        // winding number from http://geomalgorithms.com/a03-_inclusion.html
        bvec3 cond = bvec3( p.y>=v[i].y, p.y<v[j].y, e.x*w.y>e.y*w.x );
        if( all(cond) || all(not(cond)) ) s*=-1.0;  
    }

    return s*sqrt(d);
}


// Spectrum palette, iq https://www.shadertoy.com/view/ll2GD3

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 spectrum(float n) {
    return 1. - pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
}


#define PI 3.14159265359


// MATRIX

mat3 trs(vec2 t) {
	return mat3(1, 0, t.x, 0, 1, t.y, 0, 0, 1);
}

mat3 rot(float a) {
	return mat3(cos(a), -sin(a), 0, sin(a), cos(a), 0, 0, 0, 1);
}

mat3 scl(vec2 s) {
	return mat3(s.x, 0, 0, 0, s.y, 0, 0, 0, 1);
}

float decomposeScale(mat3 m) {
	vec3 a = vec3(0) * m;
	vec3 b = vec3(1,0,0) * m;
    return distance(a, b);
}


// MAIN

const int count = 11;
mat3 tiles[count];

float h = sqrt(3.) / 2.;

float sdTile(vec2 p) {
    vec2[] poly = vec2[](
    	vec2(0, 0),
        vec2(2.5, 0),
        vec2(2.5 - .25, -h / 2.),
        vec2(1.5 - .25, -h / 2.),
        vec2(1., -h),
        vec2(.5, -h)
    );
	return sdPoly(poly, p);
}

//#define LOOP

vec3 shadeTile(float d, float s, int i, int iteration) {
    d = -d;
    d *= iResolution.y * 2. / s;
    d += .001 * iResolution.y;
    //d += (.002 / s + .0008)  * iResolution.y;
    d = clamp(d, 0., 1.) / 4.;
    float t = float(i);
    #ifdef LOOP
        float time = mod(iTime / 3., 1.);
        if (iteration > 0) {
            t /= float(count);
        } else {
            t /= 3.;
        }
    	//t *= 1.666;
        t += time;
    #else
        if (iteration > 0) {
            t /= float(count);
            t = t * mix(1., 10., cos(iTime / 10.) * .5 + .5);
        } else {
            t /= 3.;
        }
        t += iTime / 3.;
    #endif
    vec3 col = d * spectrum(t);
    return col;
}

bool drawTiles(inout vec2 p, inout vec3 col, inout float scale, int iteration) {

	float scaleOut;
    vec2 pOut;
    bool hit = false;

    vec2 p2 = p;
    float scale2 = scale;

    for(int i = 0; i < count; i++ )
    {
        mat3 txm = tiles[i];
        p = (vec3(p2, 1) * txm).xy;
        scale = scale2 * decomposeScale(txm);

        float d = sdTile(p);
        col -= shadeTile(d, scale, i, iteration);

        if (d < 0.) {
            pOut = p;
			scaleOut = scale;
            hit = true;
        }

        if (iteration == 0 && i == 2) {
        	break;
        }
    }

    p = pOut;
    scale = scaleOut;

    return hit;
}

vec3 render(vec2 p) {

    float scale = 1.;
    vec3 col = vec3(1);

    // recurse
    for(int i = 0; i < 4; i++) {
        if ( ! drawTiles(p, col, scale, i)) {
            if (i == 0) {
            	col = vec3(.8);
            }
            break;
        }
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2. * fragCoord - iResolution.xy) / iResolution.y;
	p *= .55;
    p *= vec2(1,-1);
    p -= vec2(-.5,h/2.);


    // prepare matrices

    // tri
    mat3 triOrigin = trs(vec2(-.5, sqrt(3.) / 6.)) * scl(vec2(3));
    tiles[0] = triOrigin * trs(vec2(1., -h));
    tiles[1] = triOrigin * rot(PI / 1.5) * trs(vec2(1., -h));
    tiles[2] = triOrigin * rot(PI / -1.5) * trs(vec2(1., -h));

    // long
    mat3 rect = trs(vec2(-3.5, h)) * scl(vec2(-1));
    mat3 longOrigin = scl(vec2(3. / 1.5)) * trs(vec2(-5, 0)) * scl(vec2(-1, 1));
    tiles[3] = longOrigin;
    tiles[4] = tiles[3] * rect;

    // box
    mat3 boxOrigin = trs(vec2(-.5, h)) * scl(vec2(1.5 / .25));
    tiles[5] = boxOrigin * rot(PI / -3.);
    tiles[6] = tiles[5] * rect;
    tiles[7] = boxOrigin * trs(vec2(-1, 0)) * rot(PI / -3.);
    tiles[8] = tiles[7] * rect;
    tiles[9] = boxOrigin * trs(vec2(-2, 0)) * rot(PI / -3.);
    tiles[10] = tiles[9] * rect;


    // draw

   	vec3 col = render(p);
    col = pow(col, vec3(1./2.2));
    fragColor = vec4(col, 1);
}

