    p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    vec3 color = vec3(0.94);

    vec2 p[MAX_POINTS] = deformedCircle(POINTS);

    drawLine(color, vec3(0.0), p, POINTS);
    drawPoints(color, vec3(0.0), p, POINTS);
    drawSpline(color, vec3(1.0, 0.0, 0.0), p, POINTS);

	fragColor = vec4(color ,1.0);
}

// followed great raymarch tutorial https://www.shadertoy.com/view/XlBGDW
#define CLIP_NEAR 0.1
#define CLIP_FAR 8.0
#define STEPS 100
#define EPSILON 0.0001
#define DISTANCE_BIAS 0.7
#define PI 3.14159

float scene(vec3 p) {
    vec3 pos = vec3(
        sin(p.x * PI + 12.0 * sin(PI * p.x / 5.0)), // phase modulation
        sin(p.y * PI + 12.0 * sin(PI * p.y / 5.0)),
        p.z);
    float d = length(pos);
    return d;
}

float raymarch(vec3 p, vec3 direction) {
	float dist = CLIP_NEAR;
    float result;
    for (int i = 0; i < STEPS; ++i) {
        result = scene(p + direction * dist);
        if (result < EPSILON) { break; }
        dist += result * DISTANCE_BIAS;
        if (dist > CLIP_FAR) { break; }
    }
    return dist;
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
	//vec3 f = normalize(center - eye);
    vec3 f = normalize(eye - center);
	vec3 s = normalize(cross(f, up));
	vec3 u = cross(s, f);
	return mat4(
		vec4(s, 0.0),
		vec4(u, 0.0),
		vec4(-f, 0.0),
		vec4(0.0, 0.0, 0.0, 1)
	);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 offset = vec3(-0.25 * iTime, 0.1 * iTime,  0.0);
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 direction = normalize(vec3(uv, 2.5));
    vec3 eye = vec3(
        1.0 - 2.0 * sin(PI * iMouse.x / iResolution.x) + offset.x,
        1.0 - 2.0 * iMouse.y / iResolution.y + offset.y,
        1.0 - 2.0 * cos(PI * iMouse.x / iResolution.x) + offset.z);
    mat4 viewToWorld = viewMatrix(eye, offset, vec3(0.0, 1.0, 0.0));
    vec3 worldDir = (viewToWorld * vec4(direction, 0.0)).xyz;
    float result = raymarch(eye, worldDir);
    float fog = 0.5 - result / CLIP_FAR;

    vec3 base = vec3(fog, fog, fog) * fog;
    vec3 tint = vec3(1.0, 0.2, 0.2);
    vec3 modulation_tint = vec3(0.1, 0.0, -0.1);
    vec3 modulation_back = vec3(0.1, 0.0, 0.0);
    vec3 back = vec3(0.2, 0.0, 0.2);

    vec3 color = base * (cos(iTime / (3.0 * PI)) * modulation_tint + tint) + back + sin(iTime / PI) * modulation_back;
	fragColor = vec4(color, 1.0);
}

// inspired by : https://www.shadertoy.com/view/4dfSDj
// source: https://www.shadertoy.com/view/ttc3zr
uint murmurHash12(uvec2 src) {
    const uint M = 0x5bd1e995u;
    uint h = 1190494759u;
    src *= M; src ^= src>>24u; src *= M;
    h *= M; h ^= src.x; h *= M; h ^= src.y;
    h ^= h>>13u; h *= M; h ^= h>>15u;
    return h;
}
float hash12(vec2 src) { return uintBitsToFloat(murmurHash12(floatBitsToUint(src)) & 0x007fffffu | 0x3f800000u) - 1.0; }

// optimized smoothstep
float hermiteInter(float t) { return t * t * (3.0 - 2.0 * t); }
vec2 hermiteInter(vec2 t) { return t * t * (3.0 - 2.0 * t); }

float perlinNoise(vec2 xy) {
	vec2 lv = hermiteInter(fract(xy));
	vec2 id = floor(xy);

	float a = hash12(id+vec2(0.,0.));
	float b = hash12(id+vec2(1.,0.));
	float c = hash12(id+vec2(0.,1.));
	float d = hash12(id+vec2(1.,1.));
	float x1 = mix(a, b, lv.x);
	float x2 = mix(c, d, lv.x);
	return mix(x1, x2, lv.y);
}

float fbm (vec2 x, float H, int octaves) {
	float G = exp2(-H);
	float v = 0.;
	float f = 1.;
	float a = 1.;
	float aSum = 1.;

	// Rotate and shift to reduce axial bias
    //mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
    //vec2 shift = vec2(100);
	for ( float i=0.; i < float(octaves); ++i) {
		v += a * perlinNoise(f*x);
		f *= 2.;
		a *= G;
		aSum += a;
		// p = rot * p + shift;
	}
	return v / aSum;
}

float rayPlaneIntersion(vec3 O, vec3 D, vec3 p, vec3 n) {
	float denom = dot(n, D);
	if( denom > 0.001) {
		return  (dot(n, p-O)) / denom;
	}
	return -1.;
}

vec3 rayTrace(vec3 O, vec3 D) {
	vec3 bgCol = vec3(0.16);// background color
	vec3 col = bgCol; 
	vec3 n = vec3(0.,0.,1.); // planes normal

	vec2 pc = vec2(0.9, 0.005); // planeClamp
	float mistDist = 2.5;
	float t = -1.; // t factor to plane ip = O+t*D
    for (float k=0.; k<30.; ++k) {
    	pc.x *= 1.05; // horizontalClamping enlargment
    	float planeDepth = k*0.08125;
		t = rayPlaneIntersion(O, D, vec3(0., 0., planeDepth), n);
        if (t>0.) { // if we hit something
        	vec3 ip = O+t*D; // intersection pt
        	float vx = abs(ip.x);
        	if (vx<pc.x) { // clamp horizontal axis
		        float noise = 0.18*(fbm(3.*ip.xz+0.08*iTime, 1., 3)*2.0-1.0);
		        noise *= exp(-10.*vx*vx); // reduce impact horizontaly
		        ip.y += noise;
	        	if(abs(ip.y) < pc.y) { // clamp verticaly axis  
		        	float d = abs(ip.y/pc.y); // distance to edge
		        	// d = -(d-1.); // distance inversion
		        	col = mix(vec3(1.), bgCol, hermiteInter(d));

		        	float mistCoeff = clamp(pow(planeDepth / mistDist, 1.5), 0., 1.) ;
		        	col = mix(col, bgCol, mistCoeff);
		        	break;
		        }
        	}
        }
    }
    return clamp(col,0.,1.);
}

vec3 computeCamDir(vec2 uv, vec3 camPos, vec3 camUp, vec3 lookAtPos) {
	vec3 camVec = normalize(lookAtPos - camPos);
	vec3 sideNorm = normalize(cross(camUp, camVec));
	vec3 upNorm = cross(camVec, sideNorm);
	vec3 worldFacing = (camPos + camVec);
	vec3 worldPix = worldFacing + uv.x * sideNorm + uv.y * upNorm;
	return normalize(worldPix - camPos);
}

