vec3 intersect( in vec3 ro, in vec3 rd )
{
    float m = -1.0;
	float mint = 10.0;

    // plane	
	float tf = (0.0-ro.y)/rd.y;
	if( tf>0.0 ) { mint = tf; m = 0.0; }

	// mike
	float maxd = min(5.0,mint);
	float precis = 0.001;
    float t = 0.0;
	float d = 0.0;
    for( int i=ZERO; i<128; i++ )
    {
	    vec2 res = map( ro+rd*t );
        float h = res.x;
		d = res.y;
        if( h<precis||t>maxd ) break;
        t += h;
    }

   if( t<maxd && t<mint )
	{
		mint = t;
		m = d;
	}

    return vec3( mint, m, m );
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k )
{
    float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<50; i++ )
    {
        float h = map(ro + rd*t).x;
        res = min( res, smoothstep(0.0,1.0,k*h/t) );
		t += clamp( h, 0.01, 0.25 );
		if( res<0.005 || t>10.0 ) break;
    }
    return clamp(res,0.0,1.0);
}

vec2 rotate( vec2 p, float a )
{
    return vec2(cos(a)*p.x+p.y*sin(a), p.y*cos(a)-p.x*sin(a));
}
float sdEquilateralTriangle( in vec2 p,float ro )
{
    vec2 q=rotate(p,ro);
    const float k = sqrt(3.0);
    q.x = abs(q.x) - 1.0;
    q.y = q.y + 1.0/k;
    if( q.x+k*q.y>0.0 ) q = vec2(q.x-k*q.y,-k*q.x-q.y)/2.0;
    q.x -= clamp( q.x, -2.0, 0.0 );
    return -length(q)*sign(q.y);
}

