float perlinNoise(float x) {
    float id = floor(x);
    float f = fract(x);
    float u = f;
    return mix(hash1(id), hash1(id + 1.0), u);
}

float perlinNoise(vec2 x) {
    vec2 id = floor(x);
    vec2 f = fract(x);

	float a = hash1(id);
    float b = hash1(id + vec2(1.0, 0.0));
    float c = hash1(id + vec2(0.0, 1.0));
    float d = hash1(id + vec2(1.0, 1.0));
	// Same code, with the clamps in smoothstep and common subexpressions
	// optimized away.
    vec2 u = hermiteInter(f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

float perlinNoise(vec3 x) {
    const vec3 step = vec3(110., 241., 171.);

    vec3 id = floor(x);
    vec3 f = fract(x);

    // For performance, compute the base input to a 1D hash from the integer part of the argument and the 
    // incremental change to the 1D based on the 3D -> 1D wrapping
    float n = dot(id, step);

    vec3 u = hermiteInter(f);
    return mix(mix(mix( hash1(n + dot(step, vec3(0, 0, 0))), hash1(n + dot(step, vec3(1, 0, 0))), u.x),
                   mix( hash1(n + dot(step, vec3(0, 1, 0))), hash1(n + dot(step, vec3(1, 1, 0))), u.x), u.y),
               mix(mix( hash1(n + dot(step, vec3(0, 0, 1))), hash1(n + dot(step, vec3(1, 0, 1))), u.x),
                   mix( hash1(n + dot(step, vec3(0, 1, 1))), hash1(n + dot(step, vec3(1, 1, 1))), u.x), u.y), u.z);
}

float fbm (vec2 x, float H, int octaves) {
	float G = exp2(-H);
	float v = 0.;
	float f = 1.;
	float amp = 1.;
	float aSum = 1.;

    vec2 shift = vec2(100.);
	for ( int i=0; i < FBM_MAX_ITER; ++i) {
		if( i >= octaves) break;
		v += amp * perlinNoise(f*x);
		f *= 2.;
		amp *= G;
		aSum += amp;
		// Rotate and shift to reduce axial bias
		x = rot2(0.5) * x + shift;
	}
	return v / aSum;
}

float fbm (vec3 x, float H, int octaves) {
	float G = exp2(-H);
	float v = 0.;
	float f = 1.;
	float amp = 1.;
	float aSum = 1.;

	for ( int i=0; i < FBM_MAX_ITER; ++i) {
		if( i >= octaves) break;
		v += amp * perlinNoise(f*x);
		f *= 2.;
		amp *= G;
		aSum += amp;
	}
	return v / aSum;
}

float distanceField(vec3 p) {
    float final = perlinNoise(p*0.06125) -0.5;
    float other = perlinNoise(p*0.06125 + 1234.567) - 0.5;
    final = 1./(abs(final*final*other));
    return final*0.0001;
}

vec3 computeCamDir(vec2 uv, vec3 camPos, vec3 camUp, vec3 lookAtPos) {
	vec3 camVec = normalize(lookAtPos - camPos);
	vec3 sideNorm = normalize(cross(camUp, camVec));
	vec3 upNorm = cross(camVec, sideNorm);
	vec3 worldFacing = (camPos + camVec);
	vec3 worldPix = worldFacing + uv.x * sideNorm + uv.y * upNorm;
	return normalize(worldPix - camPos);
}

// return dist, marchingCount, maxDist
vec3 rayMarching(vec3 O, vec3 D, int steps, inout float density) { // ray origin and dir
    float t = 0.0;
    float marchingCount = 0.0;
 	float maxD = 1.e-10;
    for(int i = 0; i < steps; i++) {
    	if( i > MAX_STEPS) break;
    	vec3 newPos = O + D * t;
        float d = distanceField(newPos);

        // custom incrementation here for this sketch
        t += 3.; // precision handling
        density += d*0.01;
        ++marchingCount;
        maxD = max(maxD, d);

        // If we are very close to the object, consider it as a hit and exit this loop
        if( t > MAX_DIST || abs(d) < SURF_DIST*0.99) break;
    }
    return vec3(t, marchingCount, maxD);
}

float densityMarching(vec3 O, vec3 D) {
	float density = 0.;
    // ray marching
	int steps = 12;
    float t = 0.0;
    for(int i = 0; i < steps; i++) {
    	if( i > MAX_STEPS) break;
    	vec3 newPos = O + D * t;
        float d = distanceField(newPos + 12.);

        // custom incrementation here for this sketch
        t += 3.; // precision handling
        density += d*0.01;
        // If we are very close to the object, consider it as a hit and exit this loop
        if( t > MAX_DIST || abs(d) < SURF_DIST*0.99) break;
    }
    return density;
}

vec3 render(vec3 O, vec3 D) {
	vec3 cyan = vec3(25., 80., 122.)/255.;
    vec3 blue = vec3(21., 71., 199.)/255.;
    vec3 orange = vec3(255., 184., 113.)/255.;

    float density =  densityMarching(O, D);

	vec3 col = blue*0.1;
	col += pow(density, 0.45)*orange*0.5;
	col += gauss(density*2., 0.1) * cyan * 0.2;
	return saturate(col);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

	vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    uv *= 2.; // zoom
    float delay = 12.; // loop delay in s
	float a = 2.*PI*iTime/delay; // angle
	float r = 3.; // radius
	float y = sin(a + 12.)*0.5;
	vec3 O = vec3(r*cos(a), y, r*sin(a)); // origin

	vec3 finalColor = vec3(0.);
#if AA_SAMPLES > 1
    for (float i = 0.0; i < float(AA_SAMPLES); i++) {
    	for (float j = 0.0; j < float(AA_SAMPLES); j++) {
	        vec2 deltaUV = (vec2(i, j) / float(AA_SAMPLES) *2.0 - 1.0) / iResolution.y;
	        uv += deltaUV;
#endif
			vec3 D = computeCamDir(uv, O, vec3(0.,1.,0.), vec3(0.)); // dir
	    	finalColor += render(O, D);
#if AA_SAMPLES > 1
    	}
    }
    finalColor /= float(AA_SAMPLES * AA_SAMPLES); // Average samples
#endif

    // color grading
    finalColor *= vec3(1.0 ,0.9, 0.98) *1.2;

	fragColor = vec4(finalColor,1.);
}

//my version of the triangle-based backgrounds inspired by
//https://www.shadertoy.com/view/MdfBzl

#define K 5.
#define T (K + iTime * .2)
#define S(a, b) vec2(cos(a),-sin(b))

//barycentric coordinates
//using some methods discovered by FabriceNeyret2
vec3 b(vec2 a, vec2 b, vec2 c, vec2 p) {
    a = inverse(mat2(b - a, c - a)) * (p - a);
    return abs(vec3(1. - a.x - a.y, a));
}

//simple hash
vec2 h(vec2 p){
    return fract(sin(p * 34. + p.yx * 91.) * 4e5) * 2. - 1.;
}

//animation
vec2 a(vec2 p){
    vec2 b = h(p) * T;
    return (p + S(b.x, b.y) * .2 + .5) / K;
}

//colour
vec3 c(vec2 p) {
    return .5 + .5 * cos(6.283 * (vec3(.8, .8, .5) * dot((p + h(p)) / K, S(T, T)) + vec3(0, .2, .5)));    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 u = fragCoord.xy / iResolution.y, id = floor((u *= mat2(1, 0, -.5, .8)) * K);
    vec3 x, y, z, w;
    float m = 1., d, q, r;

    for(int i=0; i<4; i++){       
        //setup vertices coordinates
        //using some methods discovered by FabriceNeyret2
        //using i-i/2*2 instead i%2 for WebGL 1 compatibility 
        vec2 v0 = id - vec2(i-i/2*2,i/2), v1=v0, v2=v0, v3=v0+1.; v2.x++; v1.y++;


        //barycentric coordinates for two tiangles
 		z = b(a(v0), a(v1), a(v2), u), 
        w = b(a(v1), a(v2), a(v3), u);

        //the sum of the coordinates - 1. to understand is the point inside the triangle or not
        q = z.x + z.y + z.z - 1.,
        r = w.x + w.y + w.z - 1.;

        //setup colours for the vertices of the triangles
        //interpolation between colours of the vertices
        z = mat3(c(v2), c(v0), c(v1)) * z, 
        w = mat3(c(v2), c(v1), c(v3)) * w;

        //if the point is out of the triangle - make a mask for the antialiasing, sum triangle colour otherwise
        if(q > 1e-6) m = min(m, q * 1e2); else x += z;
        if(r > 1e-6) m = min(m, r * 1e2); else x += w;

        //mixing colours for antialiasing on edges
        if(q < .01) y += z, d++;
        if(r < .01) y += w, d++;
    }

	fragColor = vec4(mix(y / d, x, m), 1.);
}

