// å®žé™…æ˜¯ Ray-marching
vec2 castRay( in vec3 ro, in vec3 rd )
{
	float tmin = 1.0;
	float tmax = 20.0;

#if 1   // åŠ é€Ÿ Raymarching
	// bounding volume
	float tp1 = (0.0-ro.y)/rd.y;
	if( tp1>0.0 )
		tmax = min( tmax, tp1 );

	float tp2 = (1.6-ro.y)/rd.y;
	if( tp2>0.0 )
	{
		if( ro.y>1.6 )
			tmin = max( tmin, tp2 );
		else
			tmax = min( tmax, tp2 );
	}
#endif

	float t = tmin;
	float m = -1.0;
	for( int i=0; i<64; i++ )
	{
		float precis = 0.0005*t;
		vec2 res = map( ro+rd*t );
		if( res.x<precis || t>tmax )
			break;
		t += res.x;
		m = res.y;	// å”¯ä¸€ç”¨åˆ° res.y çš„åœ°æ–¹ï¼Œå½±å“æè´¨çš„è®¡ç®—
	}

	if( t>tmax )
		m=-1.0;
	return vec2( t, m );
}

// æŸ”åŒ–é˜´å½±
// http://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
	float t = mint;
	for( int i=0; i<16; i++ )
	{
		float h = map( ro + rd*t ).x;
		res = min( res, 8.0*h/t );
		t += clamp( h, 0.02, 0.10 );
		if( h<0.001 || t>tmax )	// åœ¨[ mint, maxt)èŒƒå›´å†…è¿›è¡Œæ’å€¼
			break;
	}
	return clamp( res, 0.0, 1.0 );
}

