#define ITER_GEOM 2
#define ITER_SHADING 8
#define WAVE_HEIGHT 1.2
#define dot2(a) dot(a,a)

#define T0(ip) texelFetch(iChannel0, ivec2(mod(ip,256.)), 0).r
#define rgb(r,g,b) pow(vec3(r,g,b)/255., vec3(2.2))

const vec2 subject = vec2(-1.3,5.);

float noise(vec2 p)
{
    vec2 ip = floor(p), fp = fract(p);
    fp = smoothstep(0.,1.,fp);
    vec2 e = vec2(1,0);
    return mix(
        mix(T0(ip     ), T0(ip+e.xy), fp.x),
        mix(T0(ip+e.yx), T0(ip+e.xx), fp.x),
        fp.y);
}


float seaOctave(vec2 uv, bool choppy)
{
    // Adapted from TDM "Seascape"
    uv += 2.*noise(uv);
    uv.x *= 0.62;
    vec2 wv = 0.5+0.5*cos(2.*uv);
    float w = pow(wv.x * wv.y, 0.6);
   	if(choppy)
        return (1.-w)*(1.-w);
    return 1.-w;
}

float clouds(vec2 uv, float time, float displace)
{
    uv += 0.7*noise(displace*uv + time) + time*vec2(1.,0.3);
    float f = 0.5*noise(uv);
    f += 0.25*noise(2.*uv) + 0.125*noise(4.*uv) + 0.0625*noise(8.*uv);
    return f;
}


float seaHeight(vec2 uv, float time, int iter)
{
    vec2 p = uv;
    uv *= 0.4;
    float phase = time*0.18;
    float h = 0.;
    float amp = 0.25 * mix(0.2, 1.0, smoothstep(15.,5.,length(p-subject)));
    for(int i=0; i<iter; i++)
    {
        bool choppy = (i<3);
        h += amp * (seaOctave(uv+phase, choppy) + seaOctave(-uv+phase, choppy));
        //uv *= mat2(.6,.8,-.8,.6)*2.;
        uv *= mat2(0,2,-2,0);
        // Wave propagation speed is approximately proportional to sqrt(wavenumber)
        // https://en.wikipedia.org/wiki/Dispersion_(water_waves)
        // so we scale phase by the appropriate factor
        phase *= sqrt(2.);
        amp *= 0.4;
    }
    return h;
}


float map(vec3 p, int iter)
{
    float d = p.z + WAVE_HEIGHT*(1. - seaHeight(p.xy, iTime, iter));
     // Forced perspective : the sea in the foreground is lowering toward the viewer
    d += 2./(1.+p.y) - 0.4;
    return d;
}

float raymarch(vec3 ro, vec3 rd)
{
    if(rd.z > 0.) return 100.;
    float d, t=-ro.z/rd.z; // Water surface is below z=0
    for(int i=0; i<100; i++)
    {
        d = map(ro+t*rd, ITER_GEOM);
        if(d < 0.005 || t > 100.) break;
        t += d;
    }
    return t;
}

vec3 normal(vec3 p, int iter)
{
    float eps = 0.005;
    #if 0
    vec2 e = eps * vec2(1,-1);
    return normalize(
          e.xxx*map(p+e.xxx, iter)
        + e.xyy*map(p+e.xyy, iter)
        + e.yxy*map(p+e.yxy, iter)
        + e.yyx*map(p+e.yyx, iter)
        );
    #else
    vec2 e = eps*vec2(1,0);
    return normalize(
        vec3(
            map(p+e.xyy, iter) - map(p-e.xyy, iter),
            map(p+e.yxy, iter) - map(p-e.yxy, iter),
            2.*e.x
            ));
    #endif
}

const vec3 moonColor = rgb(182,168,107);
const vec3 seaColor = rgb(35,59,59)*3.;
const vec3 horizonTint = rgb(140,120,61)/moonColor;

vec3 moon = normalize(vec3(-0.2,1.0,0.35));

vec3 getSkyColor(vec3 rd)
{
    float LdotV = dot(rd, moon);
    float surface = 0.01*0.01;
    float moonDisk = smoothstep(2.*surface, surface, 1.-LdotV);

    float cltime = iTime*0.1; // Clouds
    vec2 cluv = rd.xz / (1.+rd.y);
    vec2 moonuv = moon.xz / (1.+moon.y);

    vec3 moonHalo = vec3(clamp((1.+LdotV)/2., 0., 1.));

    moonHalo = pow(moonHalo, vec3(20,18,18)*1.5) * moonColor;

    moonHalo *= smoothstep(0.5,0.0,abs(cluv.x-moonuv.x));

    vec3 col = moonDisk * moonColor * 3. + moonHalo;


    {
        // Back clouds
        float cl = clouds(vec2(50,100) * cluv, cltime, 3.);
        float cl2 = clouds(vec2(50,100) * mix(cluv, moonuv, 0.05), cltime, 3.);
        float lig = max((cl-cl2), 0.) / max(dot2(moonuv-cluv),1e-3)*0.03;
        //cl += smoothstep(0.2, 0.6, length(vec2(rd.z, rd.x+0.15*rd.y)));
        //cl = smoothstep(0.5, 1.0, cl);
        cl *= smoothstep(0.,0.2,length(cluv-moonuv));
        vec3 clcol = lig * mix(moonColor, vec3(0.5), 0.5);
        col = mix(col, clcol, cl);
    }

    // Front clouds
    float cl = clouds(vec2(30,50) * cluv, cltime, 1.);
    cl += smoothstep(0.2, 0.6, length(vec2(rd.z, rd.x+0.15*rd.y)));
    cl = smoothstep(0.5, 1.0, cl);
    float cl2 = clouds(vec2(30,50) * mix(cluv, moonuv, 0.01), cltime, 1.);
    cl2 += smoothstep(0.2, 0.6, length(vec2(rd.z, rd.x+0.15*rd.y)));
    cl2 = smoothstep(0.5, 1., cl2);
    float lig = max((cl-cl2), 0.);

    lig *= 1. / max(dot2(moonuv-cluv),1e-3)*0.05;

    vec3 clcol = lig*moonColor*vec3(1.,0.8,0.6);
    col = mix(col, clcol, cl);




    float fogDepth = 1./max(rd.z,0.001);
    fogDepth = mix(fogDepth, 0., cl*smoothstep(0.,0.3,rd.z));
    col = mix(col, moonHalo*horizonTint, 1.-exp(-0.08*fogDepth));

    return col;
}

vec3 getBigMoonColor(vec3 rd)
{
    // Fake reflections using a big moon
    //vec3 dist = vec3(0.05,1.,1.);
    //vec3 rd2 = normalize(dist*rd), moon2=normalize(-dist*moon);
    vec3 rd2 = rd, moon2 = normalize(vec3(0.,10.,5.));
    float LdotV = dot(rd, moon);
    float surface = 3e-4;
    float moonDisk = smoothstep(surface, 0., 1.-LdotV);
    return moonColor*moonDisk*0.002/surface;
}

float shadow(vec3 ro, vec3 rd)
{
    float d, t=0.;
    for(int i=0; i<20; i++)
    {
        d = map(ro+t*rd, ITER_GEOM);
        if(d < 0.02 || t > 100.) break;
        t += d;
    }
    return t>10. ? 1. : 0.;
}

vec3 calculateColor(vec3 ro, vec3 rd)
{

    float t = raymarch(ro, rd);
    if(t < 100.)
    {
    	vec3 p = ro+t*rd;
        vec3 n = normal(p, ITER_SHADING);
        vec3 rrd = reflect(rd, n);
        //vec3 col = vec3(seaHeight(p.xy, iTime, ITER_SHADING));
        vec3 col = 0.5+0.5*n;

        float fre = 1.0 - clamp(dot(-rd, n), 0.0, 1.0);
        fre = pow(fre, 5.);

        // Here come the non-physical lighting tricks!

        // Fake subsurface scattering inspired by Tekf
        // "Wax (fake subsurface)"
        // https://www.shadertoy.com/view/lslXRj
        float subsurfDist = 0.15;
        float subsurfCoef = (map(p + moon*subsurfDist, 1) - map(p, 1))/subsurfDist;
        subsurfCoef = smoothstep(0.25, 0.5, subsurfCoef);
        // Modulate this component to highlight only the backfaces of waves
        float backSubsurfCoef = clamp(dot(n, vec3(-1,-1,0)*moon), 0., 1.0);
        float subsurfDist2 = 0.5;
        float subsurfCoef2 = (map(p + moon*subsurfDist2, ITER_GEOM) - map(p, ITER_GEOM))/subsurfDist2;
        subsurfCoef2 = clamp(subsurfCoef2, 0., 1.);
        vec3 diffuse = 2.*seaColor * subsurfCoef2;

        float subjectDist = length(p.xy - subject);
        // Add the strange lighting in the background
        float backDist = length(p.xy - vec2(-10.,50.)*0.8);
        vec3 backLight = pow(smoothstep(30.,0.,backDist),3.)*vec3(3.8,4.,5.)*2.;
        diffuse *= smoothstep(5., 0.0, subjectDist) + backLight;
        diffuse += 5.*moonColor*backSubsurfCoef*subsurfCoef * smoothstep(3.,0.,subjectDist);

        float LdotN = dot(n, moon);
        //diffuse *= 1.+LdotN;

        vec3 specular = getSkyColor(rrd) * backLight;

        specular += getBigMoonColor(rrd)
     				* smoothstep(5.0, 0.0, subjectDist);
					//* shadow(p+0.01*n, rrd);

        {
            // Add a light at the position of the "subject"
            vec3 lpos = vec3(subject, 0.15-map(vec3(subject,0),ITER_GEOM));
            vec3 ldir = lpos - p;
            vec3 lcol = vec3(1.,0.3, 0.03);
            float ldist = length(ldir);
            ldir /= ldist;
            float intensity = 1./(ldist*ldist);
           	float LdotV = clamp(dot(ldir, rrd), 0., 1.);
            specular += lcol * pow(LdotV, 1000.) * 10.*intensity;
            diffuse += lcol * LdotV * intensity*0.01;
        }


        col = mix(diffuse, specular, fre);

        //col += moonColor *3. * pow(smoothstep(30.,0.,backDist), 3.)*fre;

        // Mix with fog
        float fog = 1.-exp(-t*0.02);
        col = mix(col, getSkyColor(vec3(rd.xy, 0.)), fog);

        return col;
    } else
    {
        return getSkyColor(rd);
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0,0,0.1);
    vec3 rd = normalize(vec3(uv.x,2.,uv.y+0.2));

    vec3 col = calculateColor(ro, rd);

    col = mix(col, 1.-(4./27.)/(col*col), step(2./3.,col));
    col = pow(col, vec3(1./2.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// BlinnPhong shading.
